INSERT INTO `educationandsocialwork` VALUES ('Education Curriculum Secondary Diploma', 'EDCURSEC 625', 'Social Studies Education 2', 'Develops the knowledge and skills associated with planning and teaching Social Studies and includes methodologies for addressing national assessment for Years 11-13. Addresses questions such as: How do teachers plan and implement learning experiences, units and programmes that meet national requirements? How can students be challenged to debate and consider social issues?', 'Prerequisite: EDCURSEC 624', 'null', 'null');
