INSERT INTO `educationandsocialwork` VALUES ('Education Curriculum Secondary Diploma', 'EDCURSEC 639', 'The Learning Area of Technology', 'Develops the knowledge, understanding and issues associated with Technology education in the New Zealand Curriculum. Explores current and seminal theory to address questions such as: What is technology? Why is this Learning Area important? What are the important principles and concepts underpinning Technology in the New Zealand Curriculum?\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Curriculum Secondary Diploma', 'EDCURSEC 640', 'Developing Technological Literacy', 'Develops the knowledge, skills and understanding that constitute technological literacy and an understanding of pedagogical approaches to learning in technology. Addresses questions such as: What is technological knowledge? How do the components of practice relate to project development? What is the relationship between knowledge and capability? What do teachers need to know to teach this subject effectively?', 'Prerequisite: EDCURSEC 639', 'null', 'null');
