INSERT INTO `educationandsocialwork` VALUES ('Education Curriculum Secondary Diploma', 'EDCURSEC 641B', 'Teaching Specialist Technological Practice', 'Develops pedagogical content knowledge; skills and attitudes associated with specialist domains of practice in technology. Addresses questions such as: What is technological practice? How does industry practice relate to classroom practice? What strategies are effective for teaching technology to diverse learners? How does specialist knowledge contribute to classroom practice? What teaching methodologies and resources maximise student success?', 'Corequisite: EDCURSEC 639 or 687', 'null', 'null');
