INSERT INTO `educationandsocialwork` VALUES ('Education Curriculum Secondary Diploma', 'EDCURSEC 690B', 'Multi-disciplinary Approaches', 'Develops pedagogical knowledge, skills and attitudes associated with teaching in multi-disciplinary contexts incorporating information and communication technologies. Addresses questions such as: What are the important principles, concepts and skills associated with multi-disciplinary teaching? How do teachers plan for cross-curricular projects? How can multi-disciplinary teams operate effectively? How can the use of ICT contribute to effective learning?', 'Restriction: EDCURR 625', 'null', 'null');
