INSERT INTO `educationandsocialwork` VALUES ('Education Curriculum Secondary Diploma', 'EDCURSEC 691B', 'Teaching Subject Specialism', 'Develops the knowledge and skills required to teach a specific secondary school subject. This includes adapting content knowledge for teaching and developing subject specific pedagogical skills. The course addresses questions such as: What do teachers need to know to teach this subject effectively? What resources and strategies maximise the motivation and learning of diverse learners in this subject?', 'Restriction: EDCURSEC 601-690', 'null', 'null');
