INSERT INTO `educationandsocialwork` VALUES ('Education Curriculum Studies', 'EDCURRIC 728B', 'Special Topic', '', 'To complete this course students must enrol in EDCURRIC 728 A and B, or EDCURRIC 728', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Curriculum Studies', 'EDCURRIC 729', '', '</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Curriculum Studies', 'EDCURRIC 729A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Curriculum Studies', 'EDCURRIC 729B', 'Special Study', '', 'To complete this course students must enrol in EDCURRIC 729 A and B, or EDCURRIC 729', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Curriculum Studies', 'EDCURRIC 740', '', '</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Curriculum Studies', 'EDCURRIC 740A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Curriculum Studies', 'EDCURRIC 740B', 'Accelerate Learning in a Digital World - Level 9', 'A critical examination and application of current theory, research and practice involving the acceleration of students’ learning using digital technologies. Students will undertake an independent critical evaluation of the development, implementation and impact of a digital tool they have designed to accelerate students’ learning.', 'To complete this course students must enrol in EDCURRIC 740 A and B, or EDCURRIC 740', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Curriculum Studies', 'EDCURRIC 750', 'Arts Research: Innovative Practices', 'Students will critically explore emerging visions and theoretical concepts that broaden research practices in the Arts. Emphasis will be on how innovative Arts practices move attention from not just what is researched, but to how the research can be conducted and reported in creative ways. Topics include approaches such as narrative, autobiography, performative ethnography, reader\'s theatre, poetic inquiry, and a/r/tography.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Curriculum Studies', 'EDCURRIC 760', 'Making a Difference – Mathematics and Statistics', 'A practice-focused examination of teaching and learning in mathematics and statistics primary and intermediate level, using evidence-based evaluative inquiry to explore the teaching and learning of key mathematical and statistical concepts. Includes critical examination of pedagogies in mathematics and statistics and research into the learning of key concepts in mathematics and statistics.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Curriculum Studies', 'EDCURRIC 763', 'Special Topic: Sexuality and Health Education', 'How can we ensure schools are healthy places? How might schools address gender and sexuality? This course explores teaching and leading health and sexuality education within curriculum programmes and school-wide. Includes engagement with contemporary issues, international research, and enhancing practice. This course is appropriate for primary and secondary teachers, school leaders and community health workers engaging with schools.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Curriculum Studies', 'EDCURRIC 780', 'Psychology of Writing', 'An advanced study of contemporary theories, research and ideas that relate to the psychology of writing. This includes particular consideration of the development of expertise in writing and the role of instruction. Implications for practice will be at the forefront in the examination of theory and research.', 'Restriction: EDPROFST 754', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Curriculum Studies', 'EDCURRIC 791', 'Enterprise and Innovation in Education', 'Develops a critical understanding of relationships between business and education, the role of business, enterprise and innovation in the community and various theoretical underpinning frameworks. Develops an in-depth understanding of business practice and critiques opportunities for interaction between enterprise and educational institutions. This would include commercial opportunities for educational institutions as well as contribution to curriculum delivery.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Curriculum Studies', 'EDCURRIC 796A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Curriculum Studies', 'EDCURRIC 796B', 'MEd Thesis - Level 9', '', 'To complete this course students must enrol in EDCURRIC 796 A and B', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Curriculum Studies', 'EDCURRIC 797', '', '</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Curriculum Studies', 'EDCURRIC 797A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Curriculum Studies', 'EDCURRIC 797B', 'Dissertation', '', 'To complete this course students must enrol in EDCURRIC 797 A and B, or EDCURRIC 797', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Foundation', 'EDFOUND 10F', 'Academic Literacy I', 'An introduction to relevant academic literacy and study skills, providing students with skills to access a wide range of information literacy strategies.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Foundation', 'EDFOUND 11F', 'Academic Literacy II', 'Extends students\' academic literacy into more complex uses of information, academic writing and reading skills, and strategies for management of time and resources.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Foundation', 'EDFOUND 12F', 'Introduction to Computing', 'Introduces students with little previous computing experience to basic word processing, database development and spreadsheets. Course participants choose work related tasks to develop word processing, database and spreadsheet skills. These skills will be embedded in the context of tertiary study.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Foundation', 'EDFOUND 13F', 'Child Development and Learning', 'An overview of language and learning development, introducing strategies for helping children to develop as learners and readers.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Foundation', 'EDFOUND 14F', 'An Introduction to the New Zealand Education System', 'Introduction to the education system of New Zealand. Includes a focus on the diversity of education at all levels. Introduces students to the historical development of the New Zealand education system and the governance of this system, to the curriculum in New Zealand with a focus on the concept of \'curriculum\', the range of current curriculum documents and the professional nature of teaching.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Foundation', 'EDFOUND 15F', 'Mathematics Preliminary', 'Basic mathematics including an understanding of arithmetic ideas (knowledge and strategies), fractions, decimals and percentages; ratio and proportion, algebraic thinking, measurement using the metric system, and estimation and its uses in context.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Foundation', 'EDFOUND 16F', 'Science and Mathematics for Teaching', 'Mathematics and science for teacher education students in preparation for degree level study.', 'Prerequisite: EDFOUND 15F', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Foundation', 'EDFOUND 17F', 'Pasifika Academic Literacy', 'Particular consideration will be given to an understanding of the \'whole\' person within Pasifika contexts, teaching and learning processes for success, self-management strategies, and development of communication skills.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Foundation Mori', 'EDFOUNDM 10F', 'Te P', 'Introduction to functional and instructional Mori including everyday vocabulary, basic sentence structures, pronouns, possessives and positional language. Aspects of tikanga will include meeting and greeting people with waiata, karakia and hmene, and values such as whnau, whakawhanaungatanga and aroha. Referring to their own hap/iwi students will introduce and locate themselves in relation to their whakapapa and carry out a short mihi.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Foundation Mori', 'EDFOUNDM 11F', 'Te More', 'Builds on the introductory course Te P. Sentence structures will be extended into tenses, negatives, passives and more complex possessives and phrases. The pwhiri will be the context for tikanga studies along with values and practices such as manaaki, awhi and koha. Hap/iwi studies will include the study of student\'s own marae history and pepeha.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Foundation Mori', 'EDFOUNDM 12F', 'Te Weu', 'Students\' Mori language skills will be extended to include particles, adverbs, prefixes, suffixes and transitive verbs and prepositions. The marae will be the context for tikanga studies along with the values of tautoko, tapu/noa and mana. Hap/iwi studies will include whaikrero, pepeha, waiata, tupuna, kaumatua/kuia.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Foundation Mori', 'EDFOUNDM 13F', 'Te Aka', 'Study of complex Mori clauses and sentences, interrogatives and conditional constructions. Tikanga studies further develop the context of the marae to include the tangi, kawe mate, hura khatu and rhui. Hap/iwi studies involve study of taonga and art from student\'s own rohe.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Foundation Mori', 'EDFOUNDM 14F', 'Te Rea', 'Study of Te Reo Mori including complex negatives, particles, verbs and agents. Tikanga studies explore Mori cosmology including te kore, te po, te ao, and ng atua. Hap/iwi studies introduce students to Te Tiriti o Waitangi, Tino Rangatiratanga and related Iwi initiatives. Students carry out an independent study on a topic of their choice pertaining to their own hap/iwi.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Foundation Mori', 'EDFOUNDM 15F', 'Te Whakahaere Thuratanga', 'Intended for students who may already have tertiary study experience but limited Mori language skills. Students carry out a structured investigation whereby they can demonstrate and apply study skills already gained from other contexts.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Foundation Mori', 'EDFOUNDM 16F', 'Te Rangahau Kaupapa Mori', 'Intended for students who may already have tertiary study experience but limited Mori language skills. Students carry out an investigation on a topic of their choice related to their own hap/iwi.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Foundation Mori', 'EDFOUNDM 17F', 'Te Anga Marautanga o Aotearoa', 'An examination of Mori language and content in the Te Anga Marautanga o Aotearoa.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Foundation Mori', 'EDFOUNDM 18F', 'Te Reo Ttai', 'Development of numeracy skills within a Mori language context to the level of current NCEA requirements for entry to university.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Foundation Mori', 'EDFOUNDM 19F', 'Te Reo Ako o te Whare Wnanga', 'Academic literacy in Mori language contexts.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Mori', 'EDUCM 106', 'He Tirohanga ki te Mtauranga i Aotearoa', 'Examines historical and contemporary topics and themes in Mori schooling and education, with particular reference to the revitalisation of Te Reo Mori. Considers Mori educational aspirations and questions in the context of Treaty, social justice and equity debates in Aotearoa New Zealand. Examines te reo Mori discourse relevant to educational contexts and themes investigated in this course.', 'Restriction: EDUCM 118', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Mori', 'EDUCM 118', 'He Tirohanga ki te Mtauranga i Aotearoa', 'Examines the wider context of New Zealand education through a historical and contemporary overview. Draws on a critical sociological analysis of selected issues in society. Some emphasis is given to learners and their communities, including Mori, Pasifika, new migrants, and people with disabilities.', 'Restriction: EDUC 111, 112, 140, EDUCM 140', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Mori', 'EDUCM 119', 'Te Whanaketanga me te Ako', 'Presents an introduction to developmental and psychological theory and research and its application to teaching and learning within a variety of educational settings. Understandings for creating effective learning environments which foster high levels of motivation for all learners will be identified through an exploration of typical and atypical development; and behavioural, cognitive, constructivist and social approaches to teaching and learning.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Mori', 'EDUCM 199', '', '</p>', 'null', 'null', 'null');
