INSERT INTO `educationandsocialwork` VALUES ('Education Mori', 'EDUCM 199B', 'Te Reo Mori', 'To complete this course students must attain a level of competency in Te Reo Mori as determined by the Faculty of Education and Social Work.', 'To complete this course students must enrol in EDUCM 199 A and B, or EDUCM 199', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Mori', 'EDUCM 203', 'Te Atawhai i te Rrenga Ktanga', 'Analyses how experiences and outcomes for learners in contemporary education contexts are shaped by social constructions informed by class, ethnicity, culture, gender, sexuality, and (dis)ability. Examines the role of education policies and socio-historical context on teacher responsiveness to diversity and difference. Explores a range of transformative approaches. Particular attention is given to Pasifika learners.', 'Prerequisite: EDUCM 106', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Mori', 'EDUCM 300', 'Special Study', '</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Mori', 'EDUCM 321', 'Te Ao Trangap me te Mtauranga', 'Investigates the relationship between local, national and global politics, and education in Aotearoa New Zealand. Explores philosophical perspectives on teaching and the relationship between educational theory and practice.', 'Prerequisite: EDUC 118 or EDUCM 118 or EDUC 140 or EDUCM 140', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Mori', 'EDUCM 324', 'Whiria Te Kahatuatinitini', 'Critically appraises philosophical perspectives on education to enable students to articulate a developing philosophy and practice of teaching including the relationship between local, national and global politics and inclusive education in Aotearoa New Zealand. Highlights concepts of social justice, equity and diversity and relates these concepts to competing discourses of ability, (dis)ability and inclusion. Examines te reo Mori discourse appropriate to course content.', 'Prerequisite: EDUCM 203', 'null', 'null');
