INSERT INTO `educationandsocialwork` VALUES ('Education Practice', 'EDPRAC 100', 'Practicum 1', 'Builds relationships and establishes professional communication with konga and colleagues. Engages in collaborative teaching and learning. Explores the role of the professional teacher through an inquiry-based approach to teaching and learning.', 'Corequisite: EDPROFST 102', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Practice', 'EDPRAC 101', 'The Professional Teacher: Primary 1', 'Develops knowledge, skills and attitudes associated with effective pedagogical practice through integrating research, theory and practical experience. Addresses questions such as: What does it mean to be a teacher? What does it mean to be a professional? How are teachers learners? How do teachers establish professional relationships in complex environments? Requires demonstration of emerging pedagogical practice.', 'Prerequisite: Any 45 points from courses in the BEd(Tchg) Schedule', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Practice', 'EDPRAC 102', 'The Professional Teacher: Early Childhood 1', 'Develops knowledge, skills and attitudes associated with effective pedagogical practice through integrating research, theory and practical experience. Addresses questions such as: What does it mean to be a teacher? What does it mean to be a professional? How are teachers learners? How do teachers establish professional relationships in complex environments? Requires demonstration of effective emerging pedagogical practice.', 'Prerequisite: Any 45 points from courses in the BEd(Tchg) Schedule', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Practice', 'EDPRAC 103', 'The Professional Teacher: Health and Physical Education', 'Develops the knowledge, skills and attitudes associated with effective pedagogical practice through integrating research, theory and practical experience. Addresses such questions as: What does it mean to be a teacher, to be a professional, and to establish professional relationships in complex environments? Requires demonstration of developing pedagogical practice.', 'Restriction: EDPRAC 101, 102, EDPRACM 101', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Practice', 'EDPRAC 105', 'Practicum 1', 'Builds relationships and establishes professional communication with konga and colleagues. Engages in collaborative teaching and learning. Explores the role of the professional teacher through an inquiry-based approach to teaching and learning.', 'Corequisite: EDPROFST 103', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Practice', 'EDPRAC 201', 'Practicum Primary 2', 'Further develops the knowledge, skills and attitudes associated with effective pedagogical practice through integrating research, theory and practical experience. Addresses questions such as: How do I teach responsively and purposefully? How do I establish and maintain professional relationships in complex environments and manage the environment effectively and professionally to enable success for learners? Requires demonstration of effective developing pedagogical practice.', 'Prerequisite: EDPRAC 101 and any 75 points from courses in the BEd(Tchg) Schedule', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Practice', 'EDPRAC 202', 'Practicum Early Childhood 2', 'Further develops the knowledge, skills and attitudes associated with effective pedagogical practice through integrating research, theory and practical experience. Addresses questions such as: How do I teach responsively and purposefully; establish and maintain professional relationships in complex environments and manage the learning environment effectively and professionally to enable success for learners? Requires demonstration of effective developing pedagogical practice.', 'Prerequisite: EDPRAC 102 or EDPRACPK 102 and any 75 points from courses in the BEd(Tchg) Schedule', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Practice', 'EDPRAC 203', 'Health and Physical Education Practicum 1', 'Further develops the knowledge, skills and attitudes associated with effective pedagogical practice through integrating research, theory and practical experience. Requires demonstration of developing pedagogical practice and addresses such questions as: Do I as a teacher practise responsibility and teach purposefully to establish and maintain professional relationships in complex environments?', 'Prerequisite: EDPRAC 103', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Practice', 'EDPRAC 204', 'Practicum 2', 'Develops professional knowledge, skills and dispositions required for effective teaching and learning through an inquiry-based approach. Engages in noticing, recognising and responding to diverse learners. Practises and reflects on skills necessary to manage complex learning environments.', 'Prerequisite: EDPRAC 100, EDPROFST 102', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Practice', 'EDPRAC 205', 'Practicum 2', 'Develops professional knowledge, skills and dispositions required for effective teaching and learning through an inquiry-based approach. Engages in noticing, recognising and responding to diverse learners informed by or guided by curricula. Practises and reflects on skills necessary to manage complex learning environments.', 'Prerequisite: EDPRAC 105, EDPROFST 103', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Practice', 'EDPRAC 303', 'Health and Physical Education Practicum 2', 'Develops the knowledge, skills and attitudes associated with effective pedagogy in diverse health and physical education contexts. Requires demonstration of informed and ethical practice and addresses such questions as: Do I have the subject matter knowledge? Can I teach it effectively? Can I access the required knowledge? How do I assess student learning?', 'Prerequisite: EDPRAC 203', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Practice', 'EDPRAC 304', 'Practicum 3', 'Establishes and sustains culturally responsive, ethical, learner-focused relationships with konga, colleagues and whanau in complex environments. Utilises an inquiry-based approach to demonstrate competency in professional knowledge, and the skills and dispositions required for effective teaching in Aotearoa New Zealand.', 'Prerequisite: EDPROFST 208, EDPRAC 204', 'null', 'null');
