INSERT INTO `educationandsocialwork` VALUES ('Education Practice', 'EDPRAC 305B', 'Practicum: Enabling Achievement Primary', 'Refines an emerging philosophy and effective pedagogy through integrating research, theory and practical experience. Addresses questions such as: What are my moral, ethical and legal obligations as a teacher? How do I manage complexities of teaching professionally in order to create and sustain purposeful learning environments and enable achievement for all learners? Requires demonstration of effective, informed and ethical pedagogical practice.', 'Prerequisite: EDPRAC 201 and any 180 points from the BEd(Tchg) Schedule', 'null', 'null');
