INSERT INTO `educationandsocialwork` VALUES ('Education Practice', 'EDPRAC 307', 'Practicum 3', 'Establishes and sustains culturally responsive, ethical, learner-focused relationships with konga, colleagues and whanau in complex environments. Utilises an inquiry-based approach to demonstrate competency in professional knowledge, and the skills and dispositions required for effective teaching in Aotearoa New Zealand.', 'Prerequisite: EDPROFST 212, EDPRAC 205', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Practice', 'EDPRAC 403', 'Advanced Health and Physical Education Practicum', 'Critically evaluates personal pedagogy to consolidate understanding and management of the learning and teaching processes. Requires demonstration of informed and ethical practice and addresses such questions as: How do I manage the complexity of teaching?', 'Prerequisite: EDPRAC 303', 'null', 'null');
