INSERT INTO `educationandsocialwork` VALUES ('Education Practice', 'EDPRAC 600B', 'Professional Practice', 'Uses an evidence-based approach to support students to develop the professional knowledge, skills, and dispositions required for effective teaching in Aotearoa New Zealand, while examining what it means to demonstrate commitment to Te Tiriti o Waitangi. Builds professional relationships and enact practices that sustain culturally responsive, ethical, learner-focused relationships with diverse konga, colleagues, and whanau in complex environments.', 'Restriction: EDPRAC 607, 608, 621, 622', 'null', 'null');
