INSERT INTO `educationandsocialwork` VALUES ('Education Practice', 'EDPRAC 607B', 'Professional Practice in Context', 'Uses an evidence-based approach to develop professional knowledge, skills and dispositions for effective teaching in primary and middle school contexts. Addresses what it means to establish effective professional relationships and to teach inclusively and purposefully in complex environments. Requires demonstration of informed and ethical pedagogy.', 'To complete this course students must enrol in EDPRAC 607 A and B, or EDPRAC 607', 'null', 'null');
