INSERT INTO `educationandsocialwork` VALUES ('Education Practice', 'EDPRAC 608B', 'Professional Learning in Practice', 'Uses an evidence-based approach to develop professional knowledge, skills and dispositions for effective teaching in secondary school contexts. Questions include: what does it mean to establish positive professional relationships and to teach inclusively and purposefully in complex environments? Requires demonstration of informed and ethical pedagogy.', 'Restriction: EDPRAC 604', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Practice', 'EDPRAC 621', 'Conceptualising Practice', 'Develops knowledge, skills and attitudes associated with effective pedagogical practice through integrating research, theory and practical experience in early childhood settings. Addresses questions such as: What does it mean to establish effective professional relationships, practice effectively and teach purposefully in complex environments? Requires demonstration of developing pedagogical practice.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Practice', 'EDPRAC 622', 'Pedagogy in Practice', 'Refines knowledge, skills and attitudes associated with effective pedagogical practice through integrating research, theory and practical experience. Explores such questions as: What does it mean to be a teacher and manage complexities in order to create and sustain purposeful learning environments? What are the moral, ethical and legal obligations of a teacher? Requires demonstration of effective and ethical pedagogical practice.', 'Prerequisite: EDPRAC 621', 'null', 'null');
