INSERT INTO `educationandsocialwork` VALUES ('Education Practice', 'EDPRAC 751', 'Practitioner Inquiry', 'Students will explore what it means to take an \'inquiry stance\' as a framework for posing, investigating and addressing practice issues. They will investigate a range of practitioner research approaches, develop an understanding of practitioner research methods and design a well justified research question and proposal for an ethical investigation of a professional practice setting.\n</p>', 'null', 'null', 'null');
