INSERT INTO `educationandsocialwork` VALUES ('Education Practice', 'EDPRAC 753B', 'Portfolio of Professional Practice', 'Within the context of concurrent field-based teaching, advances professional, analytical and reflective skills in completing a sustained portfolio of teaching practice evidence. Students complete and evaluate a practitioner-inquiry investigation project in a specialist learning area, consistent with the valued learner outcomes as defined by the New Zealand Curriculum.', 'To complete this course students must enrol in EDPRAC 753 A and B', 'null', 'null');
