INSERT INTO `educationandsocialwork` VALUES ('Education Practice Mori', 'EDPRACM 101', 'Pakirehua Ngio: Te Ao Pouako', 'Develops knowledge, skills and attitudes associated with effective pedagogical practice through integrating research, theory and practical experience. Addresses questions such as: What does it mean to be a teacher? What does it mean to be a professional? How are teachers learners? How do teachers establish professional relationships in complex environments? Requires demonstration of effective emerging pedagogical practice.', 'Prerequisite: Any 45 points from courses in the BEd(Tchg) Schedule', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Practice Mori', 'EDPRACM 201', 'Noho  kura: Te Taiao Ako', 'Further develops the knowledge, skills and attitudes associated with effective pedagogical practice through integrating research, theory and practical experience. Addresses questions such as: How do I teach responsively and purposefully; establish and maintain professional relationships in complex environments and manage the learning environment effectively and professionally to enable success for learners? Requires demonstration of effective developing pedagogical practice.', 'Prerequisite: EDPRACM 101 and any 45 points from courses in the BEd(Tchg) Schedule', 'null', 'null');
