INSERT INTO `educationandsocialwork` VALUES ('Education Professional', 'EDPROF 791B', 'Thesis in Educational Leadership - Level 9', 'The thesis must be an original piece of work addressing a significant problem in relation to educational leadership. Students are required to demonstrate an ability to formulate research questions and design and carry out an investigation that answers these questions precisely and with clarity.', 'Prerequisite: EDPROFST 738 and 30 points from EDUC 735, 787, EDPRAC 751, EDPROFST 757', 'null', 'null');
