INSERT INTO `educationandsocialwork` VALUES ('Education Professional Studies', 'EDPROFST 700B', 'Literacies Education: Research and Practice', 'Understandings of research tools adequate for empirical study and an application of theory to literacies practices, critical analysis of how research questions are constructed and ability to situate and view educational issues and questions within major theoretical frameworks in literacies education will be developed.', 'Restriction: EDCURRIC 315, 364', 'null', 'null');
