INSERT INTO `educationandsocialwork` VALUES ('Education Professional Studies', 'EDPROFST 757B', 'Undertaking Research for School Improvement', 'Students will learn how to design research that contributes to the understanding and improvement of educational practice. They will develop an understanding of practitioner based research methods and produce a detailed research proposal that includes a clearly defined, and justified, research design and methodology aimed at studying a specific educational problem.', 'Restriction: EDPROF 772', 'null', 'null');
