INSERT INTO `educationandsocialwork` VALUES ('Education Professional Studies', 'EDPROFST 759B', 'Research Portfolio BEd(Tchg)(Hons) - Level 9', 'A supervised programme of coherent research activity related to a selected aspect of professional practice/education. This will lead to the compilation of a research portfolio that reflects the research and content knowledge, understanding and skills developed during the course of the programme.', 'Restriction: EDPROFST 789', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Professional Studies', 'EDPROFST 760', 'Christian Religious Education in Integrated Schools', 'A critical analysis of pedagogical methodology in Christian Religious Education through an examination of contemporary research, scholarship and theory, in Christian Scripture, Christian thought and History and Christian Religious Education in Integrated Schools.', 'Restriction: EDCURSEC 676', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Professional Studies', 'EDPROFST 762', 'Mentoring Professionals - Level 9', 'An advanced examination of approaches to mentoring and coaching. This course will provide a critical analysis of issues and practices associated with developing professional capacity. Emphasis will be placed upon the role of the educational leader in mentoring other staff into educational and leadership roles across an organisation.', 'Restriction: EDPROF 731', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Professional Studies', 'EDPROFST 764', 'Directions in Disability Policy and Practice', 'Explores contemporary issues and influences in the education of learners with disabilities. Critically examines a range of contemporary social, political and educational responses to disability.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Professional Studies', 'EDPROFST 765', 'Development in Early Years', 'An advanced examination of a range of current theories and research issues related to development in early years of childhood. Topics will include: life experiences of children within family/whnau; cognitive, communicative, social, emotional and physical development in a range of contexts.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Professional Studies', 'EDPROFST 769', 'Developing Professional Expertise', 'Whole school / organisational approaches to the kinds of professional learning and development that have proven positive impacts on a range of outcomes for students are the focus. The underpinning models of professionalism include the development of adaptive expertise that builds knowledge through evidence-informed inquiry. Emphasis is placed on catering for the diversity of learning needs throughout professional careers.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Professional Studies', 'EDPROFST 774', 'Education and Empowerment', 'A critical examination of contemporary issues faced in New Zealand\'s decile 1-3 urban schools. International and New Zealand based literature will familiarise students with current theory and research regarding the history, politics, teaching and learning, and best practice relevant to New Zealand\'s decile 1-3 urban school students, teachers and communities. Emphasis will be on empowerment theories, underpinned by Freirean perspectives.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Professional Studies', 'EDPROFST 777', 'Curriculum: Theory, Issues, Practice - Level 9', 'A critical examination of curriculum using a range of leading educational theories with an emphasis on sociological theory. Importance will be placed on the independent critique of contemporary curriculum issues, and the links between theory, policy and practice. A research informed critical understanding will be applied to the NZ Curriculum Framework, or Te Whriki, or to a sector in the NZ education system.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Professional Studies', 'EDPROFST 779', 'The Culture and Politics of Teachers\' Work', 'An analysis and informed appreciation of the socially constructed and political nature of educators\' work. The concept of the professional and professional work will be critically interpreted through selected social theory frameworks.\n</p>', 'null', 'null', 'null');
