INSERT INTO `educationandsocialwork` VALUES ('Education Professional Studies', 'EDPROFST 782', 'Educational Change - Level 9', 'Examines the purposes and processes of educational change, including an analysis of practices that promote successful outcomes for change initiatives. Processes of educational change in both New Zealand and international contexts will be studied and critiqued from individual, organisational and systemic perspectives. Focuses on leadership practices that have the potential to promote change for improvement.\n</p>', 'null', 'null', 'null');
