INSERT INTO `educationandsocialwork` VALUES ('Education Professional Studies Mori', 'EDPROFM 600A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Professional Studies Mori', 'EDPROFM 600B', 'Te Ao Mori He tirohanga whakamua kia haere whakamuri', 'Introduces socio-political and historical contexts, including Te Tiriti o Waitangi in order to inform understandings of contemporary challenges and aspirations of Mori in educational contexts. Own social locations and cultural beliefs are examined for their impact on teacher practice and outcomes for Mori learners. Learning opportunities that critically integrate Mori language, knowledge and culture are identified and designed.', 'Restriction: EDPROFST 601', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Professional Studies Mori', 'EDPROFM 700', 'Being Mori, Thinking Theory', 'An exploration of theory through a Kaupapa Mori framework. Draws on te reo, tikanga and mtauranga Mori as the foundation for articulating Kaupapa Mori theory as a contemporary theoretical framework of analysis in education.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Professional Studies Mori', 'EDPROFM 701', 'Special Topic: Te Reo in English Medium', 'Critically analyse te reo Mori revitalisation strategies and theories of language planning including developing personal te reo development plans. Developing critical knowledge, skills of mtauranga, tikanga and te reo Mori, knowledge of relevant material and curricula. Critique second language acquisition theory and develop teaching approaches that support the learning and teaching of te reo Mori and tikanga in English-medium settings.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Professional Studies Mori', 'EDPROFM 702', 'Te whakaako i te reo Mori: Teaching te reo Mori', 'Through an exploration of second language acquisition and pedagogical processes, including an analysis of the latest learning technologies, this course will enable critical reflection on current Te Reo Mori teaching practices, enhanced language development, and an opportunity to evaluate and strengthen the effectiveness of one\'s own Te Reo Mori teaching practice. This course will be taught in Te Reo Mori.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Professional Studies Mori', 'EDPROFM 796A', '', '</p>', 'null', 'null', 'null');
