INSERT INTO `educationandsocialwork` VALUES ('Education Professional Studies Mori', 'EDPROFM 797A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Professional Studies Mori', 'EDPROFM 797B', 'Dissertation', '', 'To complete this course students must enrol in EDPROFM 797 A and B, or EDPROFM 797', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Professional Studies Pasifika', 'EDPROFPK 102', 'Pe mafai vefea e ki tatou oi tamaiti aoga Pasifika', 'Explores skills and techniques in learning-to-learn in Pasifika medium, bilingual education settings. Students are introduced to characteristics of the development of a first language platform for further successful learning in English. Interpersonal communication and cognition skills and academic language proficiency in students\' first languages will be scaffolded during this course.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Special', 'EDSPEC 700', 'Special Topic', '</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education and Social Work', 'EDUCSW 101', 'Te Ao Mori', 'Introduces students to Te Ao Mori. Develops basic knowledge and skills in Te Reo, tikanga and kaupapa Mori alongside a critical exploration of the social and cultural contexts of Mori communities.', 'Restriction: SOCWORK 114', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education and Social Work', 'EDUCSW 102', 'Human Development', 'Introduces major theories, themes and recent findings in the area of human development. The emphasis is on age-based developmental changes across the life span. A range of current research findings from diverse disciplinary fields are considered, with a specific focus on societal and cultural issues relevant to the Aotearoa New Zealand context.', 'Restriction: EDUC 115, 119, HUMSERV 102', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education and Social Work', 'EDUCSW 199', '', '</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education and Social Work', 'EDUCSW 199A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education and Social Work', 'EDUCSW 199B', 'English Language Competency', 'To complete this course students must attain a level of competency in the English language as determined by the Faculty of Education and Social Work.', 'To complete this course students must enrol in EDUCSW 199 A and B, or EDUCSW 199', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education and Social Work', 'EDUCSW 201', 'Diversity in Aotearoa/New Zealand', 'Explores diversity in Aotearoa New Zealand, focusing on its bicultural history and contemporary public policy. Questions power relations relating to the Treaty of Waitangi and biculturalism, extending to the nation’s increasing ethnic, cultural and linguistic diversities. Critiques intersectionalities of culture, race, ethnicity, gender, socio-economic location, sexuality, disability, age and examines policy implications. Tamaki Makaurau (Auckland) is a key illustrative setting.', 'Prerequisite: EDUCSW 101', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education and Social Work', 'EDUCSW 202', 'New Cultures of Learning', 'Examines the current \'learning revolution\' that has emerged from widespread economic, social, technological and environmental changes in today\'s globalised world. Questions the what, why and how of learning and recognises that \'formal\' education represents only one aspect of \'learning\'. Provides an overview of theories and practices of new cultures of learning, which students can relate to their own learning experiences.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education and Social Work', 'EDUCSW 302', 'Service Learning', 'A service-learning experience during which students will develop specialist knowledge and skills. With supervision, students will engage in culturally-responsive, reflective practice that is of direct benefit to others. Professional and ethical relationship management, effective communication skills, critical reflection and evidence-based decision making will be emphasised.', 'Prerequisite: Students are required to consent to the disclosure of criminal convictions and safety checks as required by the Children\'s Act 2014', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education and Social Work', 'EDUCSW 303', 'Research and Professional Practice', 'Develops knowledge and understanding of a range of research paradigms and how research informs professional practice. Critically examines the scope and nature of research. Designs a valid, ethical, and appropriate inquiry of a professional practice topic.', 'Prerequisite: 45 points at Stage II', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Health Education', 'HEALTHED 101', 'Food and Education', 'Examines the relationship between food, eating, nutrition, and the body. Examines the social, cultural, political, economic and environmental factors that determine how people eat, what they eat, and why. Explores contemporary issues in nutrition and food education, and key challenges to improving the nutrition of communities.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Health Education', 'HEALTHED 201', 'Child and Youth Health Education', 'Examines child and youth health issues in Aotearoa New Zealand, including how western concepts of health affect Mori and Pacific communities. Explores holistic models of health, and the sociocultural and political contexts of health issues, including the determinants of health. Reflects on the history of public health in education and how particular health concerns impact contemporary practices and policy.', 'Restriction: EDCURRIC 233', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Health Education', 'HEALTHED 202', 'Sexuality, Education and Society', 'Examines the role of sexuality and sexuality education in society. Investigates cultural, historical and contemporary perspectives on gender and sexuality in diverse settings, including education and human services. Explores values, beliefs and issues of equity and sexual social justice. Develops sexuality pedagogies and advocacy for education settings and within communities.', 'Restriction: EDCURRIC 333, EDUC 122', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Health Education', 'HEALTHED 301', 'Whaioranga', 'Critically examines a range of indigenous-Mori and critical literatures in health, physicality and wellbeing and provides opportunities to consider the relationships between indigenous-Mori wellbeing and other social locations and identities.', 'Prerequisite: 15 points from EDUCSW 201, HEALTHED 201, SPORTHPE 201', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Health Education', 'HEALTHED 302', 'Leading Health Promotion in Schools', 'Critically examines how a range of health promotion models and theories can be applied in education settings. Develops in-depth knowledge of how contemporary health issues such as mental health, alcohol, drugs and obesity affect young people in Aotearoa schools. Develops understanding and skills to apply health promotion principles to educational and community settings in culturally responsive and socially critical ways.', 'Prerequisite: HEALTHED 201', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Higher Education', 'HIGHED 701', 'Learning and Teaching', 'Students will document and critically reflect on their teaching practice in the context of their discipline and institution, and the higher education literature on learning and teaching and academic citizenship, taking into account how they will exhibit both leadership through innovation, scholarship and collegiality, and an awareness of difference (gender, ethnicity, ability) such that their practice is culturally and individually sustaining.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Higher Education', 'HIGHED 702', 'Course Design', 'Students will explore and critically reflect on the theory and practice of learning and course design in higher education, including different models and methods of assessment and evaluation, such that they can design, implement and evaluate learning and teaching activities, assessment tasks and courses in ways that speak to practice in their discipline and institution, and the higher education literature.', 'Prerequisite: HIGHED 701', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Higher Education', 'HIGHED 703', 'Topics in Higher Education', 'Students will investigate and critically examine a range of current topics and theories in higher education and higher education research and their impact in a local context, in order to deepen their scholarly understanding of learning and teaching in their discipline and in the local and global higher education context, and equip them to design and undertake a higher education dissertation.', 'Prerequisite: HIGHED 701, 702, or equivalent', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Higher Education', 'HIGHED 704', 'Research Project Design', 'Students will be introduced to and critically reflect on the breadth of higher education research methodologies and methods, and strategies for research project design and management. This will enable them to produce a research proposal, including a rationale, literature review, methodology and methods, for a higher education research project of their own.', 'Prerequisite: HIGHED 701 or 702 or 703', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Higher Education', 'HIGHED 793', '', '</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Higher Education', 'HIGHED 793A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Higher Education', 'HIGHED 793B', 'Dissertation - Level 9', '', 'Prerequisite: HIGHED 704 or 30 points from EDUC 735, 787, EDPROFST 754', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Human Services', 'HUMSERV 101', 'Psychology for Human Services', 'An introduction to the study of psychology and its application to working in human services. Students will explore key theorists and theories of psychology. A particular emphasis on learning theory, developmental processes of social, cognitive, moral and personality interaction. A central theme is an understanding of human behaviour in social settings.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Human Services', 'HUMSERV 102', 'Lifespan Development for Human Services', 'An introduction to the theories of lifespan development. Key issues affecting human development and its relevance and application to the work of human service practitioners will be explored. The consideration of social contexts of human development will be a central theme.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Human Services', 'HUMSERV 104', 'Introduction to Human Services', 'Introduces students to a wide range of human service organisations including government, social enterprise and voluntary. Examines the provision of service and support within human service contexts. The nature of professionalism and the role of `self \' in effective practice will be examined.', 'Restriction: HUMSERV 103', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Human Services', 'HUMSERV 201', 'Leadership in Human Services', 'An exploration of contemporary leadership concepts, organisation structures and models with a view to their implementation within human service settings. Various approaches to team structure found in human services will be examined. Organisational structures and culture will be explored with a view to understanding how they are created, sustained and changed.', 'Prerequisite: SOCWORK 111, or 30 points passed from the BHumServ Schedule', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Human Services', 'HUMSERV 202', 'Reflective Practice in Human Services', 'Developing the processes of reflective practice to evaluate \'self\' in their role as a human service practitioner. Using an experiential and collaborative approach, students will apply action learning and gather data on their own practice. In consultation with a colleague or mentor, students will implement and evaluate change in their professional practice.', 'Prerequisite: HUMSERV 104 and 30 points passed from the BHumServ Schedule', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Human Services', 'HUMSERV 203', 'Ethics and Social Justice', 'An introduction to major normative ethical theories and to the moral controversies of applied ethics that are relevant to the fields of disability studies and youth work. An examination of the application of the principles of justice to disabled people and youth as expressed in relevant universal declarations and conventions and national legislation.', 'Prerequisite: Any 30 points passed from the BHumServ Schedule', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Human Services', 'HUMSERV 211', 'Assessment, Planning and Coordination', 'An examination of the practical components and implications of assessment, planning and coordination in human services. The theory and practice of needs assessment, service coordination and budget management are examined. The professional ethics and related practice issues are examined in relation to these activities.', 'Prerequisite: Any 30 points passed from the BHumServ Schedule', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Human Services', 'HUMSERV 305', 'Field Work in Human Services 1', 'A service-learning experience during which students will connect with an organisation or group to apply classroom knowledge in a human service setting. With supervision, students will be assisted to reflect on their field work experiences to further develop their professional practice skills.', 'Prerequisite: HUMSERV 101, 102, 104, 201, 202, 203, 211, SOCWORK 111, 112, 114, 211', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Human Services', 'HUMSERV 306', 'Field Work in Human Services 2', 'An experiential learning course focused on a consolidation of understanding of the function of reflection and research in human service practice. Students will critically analyse their own practice, connecting it to theory and evidence. With supervision and using appropriate methodology students will implement and evaluate change in their professional practice.', 'Prerequisite: HUMSERV 101, 102, 104, 201, 202, 203, 211, SOCWORK 111, 112, 114, 211', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Human Services', 'HUMSERV 307', 'Advanced Practice in Cultural Responsiveness', 'Effective practice and social change occur when practitioners can locate self, power, and diversity appropriately within bicultural and multicultural contexts. Students will critically engage with theories of cultural competence, cultural responsiveness, and cultural humility to develop skills and strategies to work reflexively across diversity dimensions and contexts, including, but not limited to, ethnicity, sexuality, gender, age and ability.', 'Prerequisite: HUMSERV 101, 102, 104, 201, 202, 203, 211, SOCWORK 111, 112, 114, 211', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Physical Education', 'PHYSED 101', 'Games and Sport Education', 'Develops knowledge and appreciation of various game forms. Analyses game structures and processes. Develops performance competency in games through experiential learning. Explores and analyses potentially positive and negative outcomes of participating in games and sport.', 'Restriction: EDCURRIC 231, EDPROF 344', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Physical Education', 'PHYSED 102', 'Alternative Sport and Play', 'Develops understanding of alternative sport and games young people play in Aotearoa New Zealand and internationally. Explores a range of non-traditional and non-competitive physical activities. Reflects on the socio-cultural dimensions of these types of physical activities, including an examination of how alternative sport enables young people to learn, play, communicate, create, express themselves, and belong.', 'Restriction: EDCURRIC 232', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Physical Education', 'PHYSED 103', 'Outdoor Education 1', 'Explores the nature and purpose of outdoor education. Requires study and participation in selected outdoor activities to acquire field-specific skills, knowledge, and dispositions. Involves experiential learning and recognition of the contribution of outdoor education to personal and social development, including the facilitation of group processing. Develops basic knowledge of outdoor risk management and environmental care.', 'Restriction: EDCURRIC 236', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Physical Education', 'PHYSED 104', 'Aquatics and Water Safety', 'Studies aquatic activity with an emphasis on the practical competencies that underpin safe and engaging recreation in Aotearoa New Zealand. Includes drowning prevention promotion and water safety education with particular reference to high-risk activities and at-risk groups, including children and youth. Demonstrate responsibility in aquatic environments including developing a range of aquatic skills, identifying hazards, and care for aquatic environments.', 'Restriction: EDCURRIC 130, 232', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Physical Education', 'PHYSED 203', 'Outdoor Education 2', 'Examines the role of outdoor education as an educational process. Involves experiential learning to develop outdoor skills, knowledge, and behaviours for teaching outdoor education. Develops knowledge of risk management for safe, effective and pleasurable engagement in the outdoors. Develops knowledge and skills for environmental care and protection.', 'Restriction: EDCURRIC 236', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Physical Education', 'PHYSED 303', 'Outdoor Education Leadership', 'Develops specialist leadership knowledge and skills appropriate to leading educational experiences in the outdoors. Strengthens skills required to facilitate safe, challenging learning experiences in moderate and wilderness environments. Examines policies and legal requirements as they relate to safe industry practice and duty of care.', 'Prerequisite: PHYSED 103, 203', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Professional Counselling', 'PROFCOUN 700', 'Counselling In Youth Mentoring', 'Theories and concepts of youth mentoring and positive youth development will be examined in relation to practice as youth counsellors. Students will engage in weekly psychoeducational group and one to one counselling sessions on campus with at-risk youth as part of a therapeutic mentoring programme. Lecture topics include counselling skills for youth, effective and ethical mentoring relationships, and risk assessment.', 'Prerequisite: Approval from the Course Director', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Professional Counselling', 'PROFCOUN 701A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Professional Counselling', 'PROFCOUN 702', 'Special Topic', '</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Professional Counselling', 'PROFCOUN 703', 'Special Study', '</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Professional Counselling', 'PROFCOUN 704', 'Children\'s Stress and Coping', 'An in-depth exploration of the stressors that children experience in four domains: family, school, interpersonal and intrapersonal; as well as the broad range of coping strategies they employ. Content will be based on recent research with children in New Zealand, international studies and contemporary literature. Interventions and practices for supporting children will be explored.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Professional Counselling', 'PROFCOUN 705A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Professional Counselling', 'PROFCOUN 705B', 'The Counselling Process', 'An advanced examination of principles of counselling together with their application in the counselling process.', 'Corequisite: PROFCOUN 701', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Professional Counselling', 'PROFCOUN 706', 'Cultural Issues in Counselling', 'A critical examination of cultural dimensions present in counselling activities.', 'Restriction: EDPROFST 748', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Professional Counselling', 'PROFCOUN 707', 'Specialist Counselling Skills and Approaches', 'An advanced examination of the specialist counselling skills and knowledge required to work effectively with clients from a wide range of contexts and experiences. Topics may include working with sexual abuse, family violence, trauma, anxiety, depression, self-harm, suicidality, and loss and grief. The course will focus on the integration of theory, research and practice, and the development of working models to facilitate effective counselling.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Professional Counselling', 'PROFCOUN 708', 'Professional Issues in Counselling', 'An examination of significant professional issues in counselling, including supervision, ethics and accountability, and role development.', 'Restriction: EDPROFST 749', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Professional Counselling', 'PROFCOUN 709', 'Research and Counselling', 'An introduction to principles of critical analysis as applied to theory and practice in counselling, and to basic methodology in research with particular reference to research in counselling.', 'Restriction: EDPROFST 750', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Professional Counselling', 'PROFCOUN 797A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Professional Counselling', 'PROFCOUN 797B', 'Research Portfolio - Level 9', 'A supervised programme of coherent research activity related to 500 hours of counselling practice undertaken concurrently. The research portfolio will reflect the research and professional knowledge, understanding and skills developed during the course of the programme. It will include three case studies and other components reflecting additional, research-related activities, to be determined individually in consultation with the supervisor.', 'Prerequisite: EDPROFST 750 or PROFCOUN 709', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Professional Supervision', 'PROFSUPV 700', 'The Practice of Professional Supervision', 'A critical examination of the concept, role, purpose and benefits of supervision in a wide range of professions. The rationale for supervision within professional and organisational contexts in health, counselling and human services, with reference to the ethical requirements will be examined. The knowledge and skills required to offer supervision in professional contexts will be explored in depth.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Professional Supervision', 'PROFSUPV 701', 'Advanced Approaches in Professional Supervision', 'A critical examination of a range of theoretical and practical approaches to professional supervision. An in-depth exploration of the models of supervision for health, counselling and human service contexts, will include the development of anti-oppressive supervision practice and an integration of advanced knowledge, skills and values of supervision.', 'Prerequisite: PROFSUPV 700', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Professional Supervision', 'PROFSUPV 704', 'Counselling Supervision: Relationship and Process', 'A critical examination of the nature of the supervisory relationship in the context of counselling supervision and the facilitation of supervisory process. Topics include an in-depth exploration of the dynamics of supervisory relationships, the roles of supervisors and supervisees, and the integration of theory and practice in developing skills for facilitating supervisory process.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Professional Supervision', 'PROFSUPV 707', 'Supervision Folio', 'A supervised portfolio with a focus on an in-depth critical reflection on current practice in professional supervision, learning and development in human services and health contexts.', 'Prerequisite: PROFSUPV 700, 701', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Professional Supervision', 'PROFSUPV 710', 'Stress and Trauma in Health and Human Services', 'Explores the complex dimensions of stress and trauma encountered by health and human services workers. The historical, cultural and conceptual basis for an understanding of the impact of stress and trauma on both clients and workers is considered. Critically explores research-led strategies to address personal, professional and organisational responses to stressful environments.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Professional Supervision', 'PROFSUPV 712', 'Reflective Practice', 'A critical exploration of contemporary theories of professional learning for reflective practice in health, counselling and human services workplaces. A critical examination of strategies for ongoing professional development, including reflective practice, critical reflection, communities of practice, reflective practice and the learning organisation.', 'Restriction: PROFSUPV 703', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Professional Supervision', 'PROFSUPV 713', 'Critical Issues in Counselling Supervision', 'An advanced examination of ethical, process and relational issues that may arise in the practice of counselling supervision. Topics include ethical issues for supervisors and supervisees, cultural issues and the relationship between culture and ethics in supervision, the influences of socio-political contexts, power in supervisory relationships, evaluation and accountability.', 'Restriction: PROFSUPV 705', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Professional Supervision', 'PROFSUPV 714', 'Managing and Developing People in Human Services', 'A critical examination of strategies for effective management and development of professional staff in the health and human services. Includes critical reflection on effective management processes; indigenous management frameworks; recruitment and selection; supervision and performance planning; coaching and mentoring; training and development; unsatisfactory performance; and building resilience and staff care strategies.', 'Restriction: PROFSUPV 706', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Professional Supervision', 'PROFSUPV 715', 'Practice Teaching and Learning', 'Explores the teaching and learning strategies required in the provision of high quality field education in social services and health settings. Critically examines theoretical and evidence informed pedagogies for student practice learning.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Professional Supervision', 'PROFSUPV 716', 'Special Topic: Group Supervision', 'Critically explores the benefits and challenges of group supervision. The parameters and value of group supervision are identified and promoted for practice within the current context of health and social services. Specific models of group supervision are developed to support critical reflection managing boundaries, tasks, roles, structure and the creation of a positive group learning.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Professional Supervision', 'PROFSUPV 717', 'Special Study', '</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Professional Supervision', 'PROFSUPV 718', 'Special Topic', '</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work', 'SOCWORK 111', 'Professional Communication Skills', 'An introduction into effective personal and professional communication in human services. An experiential and collaborative approach will be used to assist students to explore the place of self in the communication process, to understand the stages, purpose and task of the communication process, and to develop effective interaction.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work', 'SOCWORK 112', 'Sociology for Human Services', 'Introducing the traditions of sociological analysis and its application to the human services. Key theory and theorists, the history of ideas and debates within sociology, and their relevance to practitioners\' understanding of contemporary New Zealand society will be explored. A central theme is that a critical sociological imagination is essential for practitioners in social service settings.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work', 'SOCWORK 113', 'Culture and Diversity', 'An introductory exploration of notions of culture and diversity that critically examines impacts on individuals, whnau and communities in Aotearoa. A range of cultural perspectives will lead to and encourage critical discovery of \'self\' in relation to realms of difference in the context of social and human services.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work', 'SOCWORK 115', 'The Social Work Environment', 'An introduction to the history and practice of social work and the wider policy environment that will be encountered while working in the profession. Examination of the roles played in the profession and familiarisation with traditions of care and policies of welfare that influence the work of social workers and social services.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work', 'SOCWORK 211', 'Social Policy Development', 'Explores the design and analysis required in policy advocacy and policy development. Consideration of the contexts where policy review and development occur, the procedures involved, the role and preparation of submissions outlining a case for change and the proposal for new policy, and the obligations required in policy innovation and evaluation.', 'Prerequisite: SOCWORK 112', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work', 'SOCWORK 212', 'Bicultural Social Work Practice', 'An introduction to study of the personal and professional impact of the Treaty of Waitangi in social work practice and social workers\' obligations to bicultural practice. Development of an understanding of the principles of bicultural practice and articulation of their professional stance.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work', 'SOCWORK 213', 'Social Work Practice Skills', 'Development of the key skills of practice including: engagement, assessment, intervention, referral and evaluation. Drawing on theoretical approaches provided in SOCWORK 214 this course will develop the skills for working with clients in a variety of settings and cultural contexts. To be studied concurrently with SOCWORK 214.', 'Corequisite: SOCWORK 214', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work', 'SOCWORK 214', 'Social Work Practice Theories', 'An introduction to a range of theories and models of practice in social work and their application to a variety of social work settings, including an exploration of personal and professional values within the framework of relevant codes of ethics, cultural practice and the importance of professional supervision. To be studied concurrently with SOCWORK 213.', 'Corequisite: SOCWORK 213', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work', 'SOCWORK 216', 'Law and the State in Social Work', 'A sociological exploration of the issues presented by the legal framework that impact on social workers\' mandate to practise. A critical investigation of the state\'s law-making functions and conflicting imperatives across a range of social work fields, including the family, justice, and mental health.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work', 'SOCWORK 310', 'Special Topic', '</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work', 'SOCWORK 311', 'Social Work Process and Practice', 'Students are required to integrate a defined range of approaches to practice comprising work with individuals, families and small groups. Knowledge, skills, values and ethics associated with direct practice will be extended. Students will apply the social work process of reflection and use of self in preparation for becoming a mindfully reflexive practitioner.', 'Prerequisite: SOCWORK 214', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work', 'SOCWORK 312', 'Applied Social Research', 'Social workers are reclaiming the research agenda in social work. Sound social work practice relies on evidence to inform theoretical frameworks, intervention decisions and practice evaluation. A practical introduction to the principles, theories and approaches that inform social research, with a particular emphasis on social work contexts.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work', 'SOCWORK 315', 'Organisations and Management', 'Examines the variety of organisational frameworks from which human services are practised and the impact of these contexts on professional identity and practice in the workplace. Contemporary management approaches will be examined with reference to theory and roles and responsibilities assumed by human service managers in a complex and dynamic environment.', 'Prerequisite: SOCWORK 211', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work', 'SOCWORK 317', 'Supervised Field Practice and Professional Development 1', 'A practicum course which focuses on developing a professional identity and a philosophy of practice that is shaped by integrating personal experience and professional knowledge, values and skills gained from the first two years of the Bachelor of Social Work programme. A significant part of this course includes a period of 12 weeks supervised agency-based practical experience for which the student will be prepared through developing individual learning goals.', 'Prerequisite: SOCWORK 213, 214', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work', 'SOCWORK 383', 'Special Topic', '</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work', 'SOCWORK 401', 'Statutory Social Work', 'An examination of social work practice in statutory settings and theoretical and research-informed intervention frameworks applicable across a range of fields of service. These include family violence, child welfare, youth justice, prisons, and working with vulnerable adults. Critical investigation of questions of context, relationships, power, ethics, human rights and social justice in authoritative settings will be undertaken.', 'Prerequisite: SOCCHFAM 332 or 314, SOCWORK 317', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work', 'SOCWORK 411', 'Social Work Interventions for Best Practice', 'An in-depth examination of contemporary issues in social work practice, focusing on evidence-based practice and intervention skills that have direct application to complex practice situations.', 'Prerequisite: SOCWORK 311, 317', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work', 'SOCWORK 413', 'The Social Work Discourse', 'The application of sociological analysis to consideration of the role and characteristics of social work practice. An exploration of the professional discourse is framed and how major social trends impact on that discourse in practice and the public domain.', 'Restriction: SOCWORK 713', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work', 'SOCWORK 414', 'Research and Evaluation in Social Practice', 'An exploration of relevant social work research literature and approaches to data analysis, and the undertaking of a research project. Develops confidence and skills in the application of practice research principles, problem definition, critical review of relevant literature and analysis of existing qualitative data to inform professional practice in social work and social services.', 'Prerequisite: SOCWORK 312, 317', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work', 'SOCWORK 415', 'Supervised Field Practice and Professional Development 2', 'An advanced practicum course which provides an exposition of the relationship between critical reflection, professional supervision and ethical practice to the knowledge, skills and practice experience of the social work profession. A period of 16 weeks supervised agency-based practical experience is included in this course that builds on participant knowledge and skills.', 'Prerequisite: SOCWORK 317', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work', 'SOCWORK 416', 'Professional Practice Project', 'A student directed project intended to develop a field of proficiency applicable to current or future professional interests. The project will involve research investigation, critical reflection and analysis, evaluation, and the preparation of resources or development of new practice. On completion students will showcase their project in a verbal presentation.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work', 'SOCWORK 426', 'Practice with Communities', 'An introduction to change-oriented social work practice with communities, with particular emphasis on diverse and indigenous communities and critical analysis of current and historical factors shaping community well-being. Building their own theories of change, students learn models and skills for integrating partnerships with communities into their practice, including engagement, capacity building, community development, organising, activism, and policy advocacy.', 'Prerequisite: Any 60 points passed at Stage III', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work', 'SOCWORK 484', 'Special Topic', '</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work', 'SOCWORK 700', 'Clinical Social Work - Level 9', 'Examines the area of clinical social work practice within the Aotearoa New Zealand context. Content will include a range of theoretical approaches to clinical practice. Theories will include Cognitive Behavioural theory, Narrative theory and Solution-Focused methods. There will be a strong focus on the use of clinical theories when working with Tangata whenua or when working across cultures. Individual, whnau/family, and group work methods will be explored.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work', 'SOCWORK 701', 'Statutory Social Work', 'An advanced examination of social work practice in statutory settings and theoretical and research-informed intervention frameworks applicable across a range of fields of service. These include family violence, child welfare, youth justice, prisons, and working with vulnerable adults. Critical investigation of questions of context, relationships, power, ethics, human rights and social justice in authoritative settings will be undertaken.', 'Prerequisite: SOCCHFAM 332 or 314, SOCWORK 317', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work', 'SOCWORK 702', 'Social Work with Older People', 'Critically explores advanced research, theories and practice of social work with older people from an ecological systems perspective. Content will examine opportunities and challenges presented as people live longer and develop the contributions of social work and social policy to positive aging strategies. Consideration will be given to the bicultural and diverse contexts of practice in Aotearoa New Zealand.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work', 'SOCWORK 711', 'Social Work Interventions for Best Practice', 'An in-depth examination of contemporary developments in social work practice, with an emphasis on the employment of evidence-informed interventions that have direct application to complex practice situations.', 'Prerequisite: SOCWORK 311, 317', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work', 'SOCWORK 712', 'Social Work in Statutory Settings', 'An advanced examination of fields of practice in statutory social work. Will include areas such as family violence, child welfare, disability, health, mental health, and working with vulnerable adults. A critical investigation of context, relationships, power, ethics, interventions and best practice in these settings will be undertaken.', 'Prerequisite: SOCWORK 721, 722, 723, 724 and 725', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work', 'SOCWORK 713', 'The Social Work Discourse', 'A critical analysis of contemporary social work practice, utilising sociological perspectives and contemporary social theory. An in-depth exploration of how the professional discourse of social work is framed and how major social trends impact on that discourse in practice and the public domain.', 'Restriction: SOCWORK 413', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work', 'SOCWORK 715', 'Supervised Field Practice and Professional Development', 'An advanced practicum course which includes a critical interrogation of the relationships between critical reflection, professional supervision and ethics and their application to professional social work practice. This course includes a minimum of 65 days supervised agency-based practical experience, building on the knowledge and skills gained in the first practicum and subsequent coursework.', 'Prerequisite: SOCWORK 317', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work', 'SOCWORK 718', '', '</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work', 'SOCWORK 718A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work', 'SOCWORK 718B', 'Applied Research in Social Services', 'Examines the role of applied research within professional practice. An in-depth examination of research methods, traditions and techniques particularly used in analysing, evaluating and auditing social service programmes and practices. Aims to enhance and develop the knowledge and understanding of students with regard to the nature and application of a broad range of research methods, the role of theory, ethics and politics in research and in developing a research proposal.', 'To complete this course students must enrol in SOCWORK 718 A and B, or SOCWORK 718', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work', 'SOCWORK 719', 'Special Study', '</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work', 'SOCWORK 721A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work', 'SOCWORK 721B', 'Theories and Skills in Social Work Practice', 'An in-depth examination of theoretical perspectives, skills and approaches in social work practice related to interpersonal work with individuals, families and groups living through challenging situations. Content will include a critical engagement with contemporary literature and the examination of evidence-informed interventions and critically reflective strategies that help guide professional practice in collaborative and safe environments.', 'To complete this course students must enrol in SOCWORK 721 A and B', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work', 'SOCWORK 722', 'Developing Social Work Professional Identity', 'Examines contemporary and historical social work cultural identity, language and discourse as a global profession. Socialisation to the profession and its values is explored through a defined range of practice fields, premised on a human rights and social justice framework. Systemic models of practice are reviewed. Inter-professional practice, professional ethics, anti-oppressive and bicultural practice and registration are analysed in the New Zealand setting.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work', 'SOCWORK 723', 'Social Work in the New Zealand Context', 'Examines the history, policy, law, social patterns, trends and issues that contribute to the working environment for bicultural social work practice in Aotearoa New Zealand. Content will encourage an understanding of the organisational, statutory and community context of social services, professional practice and the reflective social worker operating in settings that can be examined, challenged and changed.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work', 'SOCWORK 724', 'Applied Social Work Research Methods - Level 9', 'Consolidates critical awareness of the role of research and knowledge in a specialised field of social work practice, leading to the development of an independent research proposal. Develops advanced knowledge and understanding of the nature and application of a range of applied research methods and traditions, ethics, and the role of theory in research and practice.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work', 'SOCWORK 725', 'Supervised Field Placement I', 'A first practicum course which focuses on developing a professional identity and a philosophy of practice that is shaped by integrating personal experience and professional knowledge, values and skills. A significant part of this course includes a period of a minimum of 50 days of supervised agency-based practical experience for which the student will be prepared through developing individual learning goals.', 'Prerequisite: SOCWORK 722, 723', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work', 'SOCWORK 726', 'Practice with Communities', 'An advanced consideration of change-oriented social work practice with communities, with particular emphasis on diverse and indigenous communities and critical analysis of current and historical factors shaping community well-being. Building their own theories of change, students learn models and skills for integrating partnerships with communities into their practice, including engagement, capacity building, community development, organising, activism, research and policy advocacy.', 'Prerequisite: 60 points passed at Stage III', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work', 'SOCWORK 734A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work', 'SOCWORK 734B', 'Professional Social Work Research in Practice - Level 9', 'An independent, applied research-based project relating to an aspect of social work practice and undertaken in a practice context. Students will gather and critically analyse authentic data using appropriate research strategies and ethical practice principles, and produce a substantial research report.', 'Prerequisite: SOCWORK 721-725', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work', 'SOCWORK 735', 'Supervised Field Placement II', 'An advanced practicum course which includes a critical interrogation of the relationships between critical reflection, professional supervision and ethics and their application to professional social work practice. Includes a minimum of 70 days supervised agency-based practical experience, building on the knowledge and skills gained in the first practicum and concurrent coursework.', 'Prerequisite: SOCWORK 721-725', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work', 'SOCWORK 739', 'Integration Portfolio', 'An integrative summary with critical reflection on student development as a social work practitioner. The portfolio demonstrates an understanding of the student\'s professional and skills development within the context of their academic programme and practice learning.', 'Prerequisite: SOCWORK 721-725', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work', 'SOCWORK 757', 'Policy Appraisal and Innovation in Human Services', 'Enables students to assess the construction and performance of policy in fields of social and human service practice. Explores contemporary policy, comparative policy analysis, research-led policy development, programme monitoring and evaluation. Examines practitioner responsibility for policy appraisal to enable practitioners to become conversant with policy innovation and change in professional settings.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work', 'SOCWORK 758', 'Special Topic - Level 9', '</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work', 'SOCWORK 759', 'Special Topic', '</p>', 'null', 'null', 'null');
