INSERT INTO `educationandsocialwork` VALUES ('Social Work', 'SOCWORK 780A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work', 'SOCWORK 780B', 'Research Project - Level 9', 'An integrated approach to social work research where students apply specialised practice research principles and data analysis software to existing qualitative and quantitative datasets to engage in problem definition, critical review of relevant literature, research strategies and design, generating data analysis and reporting of research findings to inform professional practice – and integrated practice research – in social work and social services.', 'Prerequisite: SOCWORK 312, 317', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work', 'SOCWORK 796A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work', 'SOCWORK 796B', 'Thesis - Level 9', '', 'To complete this course students must enrol in SOCWORK 796 A and B', 'null', 'null');
