INSERT INTO `educationandsocialwork` VALUES ('Social Work', 'SOCWORK 797B', 'Research Portfolio - Level 9', '', 'To complete this course students must enrol in SOCWORK 797 A and B', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work Child and Family Practice', 'SOCCHFAM 215', 'Whnau-Family-Aiga Practice', 'An exploration of the structural, emotional, social and cultural dimensions of families, encompassing the relevance of cultural knowledge and appropriate interventions in diverse family practice and examination of the significance and impact of social issues on whnau-family-aiga systems.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work Child and Family Practice', 'SOCCHFAM 332', 'Working with Children and Whanau', 'An exploration of effective approaches, policies, practices and principles used to engage with children and their whanau within the context of Aotearoa New Zealand. This course will develop the skills and knowledge necessary for working with children and their whanau in community and statutory settings and include consideration of poverty, family violence and child protection.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work Child and Family Practice', 'SOCCHFAM 382', 'Special Topic', '</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work Child and Family Practice', 'SOCCHFAM 431', 'Child and Adolescent Mental Health Issues', 'A critical examination of specific diagnoses and disorders of childhood and adolescence that impact on their mental health and wellbeing, with emphasis on the current successful treatments for severely emotionally distressed children and young people within Aotearoa New Zealand.', 'Restriction: SOCCHFAM 731', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work Child and Family Practice', 'SOCCHFAM 482', 'Special Topic', '</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work Child and Family Practice', 'SOCCHFAM 700', 'Domestic Violence: Challenges and Responses - Level 9', 'An in-depth examination of the prevalence, consequences, risk and protective factors of domestic violence in Aotearoa New Zealand. Draws on local and international research to explore conceptual models, theories, practice and current research concerns, aimed at prevention and intervention activities at the individual, family/whnau, organisational, community and societal levels.', 'Restriction: SOCHLTH 751', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work Child and Family Practice', 'SOCCHFAM 731', 'Child and Adolescent Mental Health Issues', 'An in-depth examination of specific diagnoses and disorders of childhood and adolescence that impact on their mental health and wellbeing, with emphasis on contemporary literature and evidence informed practice with children and young people within Aotearoa New Zealand.', 'Restriction: SOCCHFAM 431', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work Child and Family Practice', 'SOCCHFAM 734', 'Issues in Child Welfare and Protection', 'Explores the critical issues in child welfare and protection encountered by education, health and human services workers. The historical, social and conceptual basis for an understanding of child abuse and neglect is considered. Explores research-led strategies to address personal, professional, and societal responses to the needs of vulnerable children.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work Child and Family Practice', 'SOCCHFAM 735', 'Special Topic', '</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work Child and Family Practice', 'SOCCHFAM 736', 'Special Topic', '</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work Health Practice', 'SOCHLTH 231', 'Hauora and Social Work Practice', 'Critically explores the role of social work within community and institutional health settings. Develops an understanding of the social determinants of health and their impact on wellbeing. Considers emotional, psychological, cultural and social impacts of health status and illness on people in Aotearoa New Zealand and identifies core skills for working in this area of social work practice.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work Health Practice', 'SOCHLTH 313', 'Mental Health in Social Practice', 'An exploration of the dynamics of social practice with service users and their whnau/family with mental health issues. This course includes an examination of mental health policy and broad approaches to care and recovery. An overview knowledge of the major mental health illnesses and associated recovery strategies will be provided. The impact of mental health issues in Mori, Pasifika, people with disabilities, young people and refugee and migrant communities will be examined.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work Health Practice', 'SOCHLTH 334', 'Effective Social Work in Health and Disability Services', 'Explores the role of social work with people who have disabilities or experience disabling conditions through accident, illness and aging. Examines policy and strategies of support for recovery and rehabilitation. Develops skills to address the psychosocial impact of physical loss and change with individuals, carers and families.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work Health Practice', 'SOCHLTH 381', 'Special Topic', '</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work Health Practice', 'SOCHLTH 432', 'Working with Grief and Loss', 'An in-depth examination of theoretical and cultural perspectives of grief and loss that includes loss associated with trauma, terminal and chronic illness and suicide. Personal experience of loss will also be explored. Content will include developing social work skills and interventions that can assist adults and children experiencing grief, loss and change.', 'Restriction: SOCHLTH 732', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work Health Practice', 'SOCHLTH 481', 'Special Topic', '</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work Health Practice', 'SOCHLTH 700', 'Health, Social Justice and Social Work - Level 9', 'A critical examination of health disparities, the social dimensions of health and wellbeing and the role of social work. Explores contemporary literature and research to evaluate development strategies in micro and macro practice in health social work. Changes in the delivery of health care and the impact on the social work role and professional identity will be explored with reference to contemporary challenges and opportunities.', 'Restriction: SOCHLTH 753', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work Health Practice', 'SOCHLTH 732', 'Working with Grief and Loss', 'An in-depth examination of theoretical and cultural perspectives of grief and loss that includes loss associated with trauma, terminal and chronic illness and suicide. Personal experience of loss will also be explored. Content will include contemporary literature and the examination of evidence-informed interventions that can assist adults and children experiencing grief, loss and change.', 'Restriction: SOCHLTH 432', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work Health Practice', 'SOCHLTH 756', '', '</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work Health Practice', 'SOCHLTH 756A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work Health Practice', 'SOCHLTH 756B', 'Special Topic', '', 'To complete this course students must enrol in SOCHLTH 756 A and B, or SOCHLTH 756', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work Health Practice', 'SOCHLTH 757', 'Special Study', '</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work Youth Practice', 'SOCYOUTH 300', 'Therapeutic Youth Mentoring', 'Theories and concepts of youth mentoring and positive youth development will be examined in relation to practice as youth mentors. Students will engage in weekly mentoring sessions on campus with local at-risk youth as part of the Campus Connections therapeutic mentoring programme. Lecture topics include adolescent development, effective mentoring relationships, communication and counselling, ethical youth practice, and risk assessment.', 'Prerequisite: Any 60 points passed at Stage II or above and approval from the Course Director', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work Youth Practice', 'SOCYOUTH 483', 'Special Topic', '</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social Work Youth Practice', 'SOCYOUTH 736', 'Special Topic', '</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social and Community Leadership', 'SOCCLEAD 700', 'Leadership: Ethics and Actions', 'Effective social and community leadership requires a strong and critical ethical foundation. Topics include the professional identity and values of social and community sector leadership, social justice, ethical and authentic leadership, and human systems thinking. Leadership that honours the Treaty of Waitangi, diversity, and equity, and actions based on those values, will be explored.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social and Community Leadership', 'SOCCLEAD 701', 'Leading Social Innovation', 'Evolving approaches to innovating social change will be examined. Topics include theories and change models of social innovation, venture creation, programme design and social enterprise development. Case study analysis will examine contemporary debates on and approaches to evidence-based programming, collaboration and co-design, social impact measurement, ethical social profit ventures, scaling impact and creating sustainable social change.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social and Community Leadership', 'SOCCLEAD 702', 'Special Topic', '</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social and Community Leadership', 'SOCCLEAD 703', 'Leadership, Ethics, Systems', 'Effective social and community leadership requires a critical ethical and analytic foundation. Themes examined include ethical leadership values aligned to social justice, in combination with the capacity for analysis of complex social issues, systems, and change processes. Leadership that honours the Treaty of Waitangi, diversity, and equity, and formulates actions based on these values will be explored.', 'Restriction: SOCCLEAD 700', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social and Community Leadership', 'SOCCLEAD 704', 'Special Study', '</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social and Community Leadership', 'SOCCLEAD 706', 'Innovation, Design, Evaluation', 'Evolving approaches to innovating social change and evaluation practice are examined. Topics include contemporary debates on, and approaches to, co-design, collaboration, ethical social innovation, evidence-informed programme design, evaluation models and impact measurement. Using experiential learning, groups of students will be guided through a social innovation design process in response to user needs, and develop robust evaluation proposals.', 'Restriction: EDUC 726, SOCCLEAD 701', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social and Community Leadership', 'SOCCLEAD 794A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Social and Community Leadership', 'SOCCLEAD 794B', 'Thesis - Level 9', '', 'To complete this course students must enrol in SOCCLEAD 794 A and B', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Sport Studies', 'SPORT 101', 'Making a Difference in Sport', 'Explores the skills needed to successfully deliver sport and recreation activities. Examines differential community provision and develops skills to liaise with and engage diverse participants using psychological and sociological theories. Considers emerging trends in the field. Students will participate in community mapping to identify existing and potential sport and recreation opportunities in diverse communities.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Sport Studies', 'SPORT 202', 'Sport and Recreation', 'Explores sport, recreation, and physical activity environments and cultures in Aotearoa New Zealand, including organised and alternative sport. Examines how children, youth and adults engage with sport and physical culture. Explores issues of access, policy and leadership in the field and investigates how different concepts influence understandings of, and engagement with, sport and physical culture.', 'Restriction: EDCURRIC 237', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Sport Studies', 'SPORT 203', 'Sport, Media and Marketing', 'Explores the roles and consequences of media representations of sport and physicality. Develops knowledge of the discourses that influence how sport, the body, and health are understood. Develops skills in marketing communications used to promote sport and physical wellbeing. Investigates issues emerging from the use of social media by sporting organisations, groups and individuals.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Sport Studies', 'SPORT 204', 'Coaching Sport', 'Examines and applies effective coaching practices, including coaching principles and the nature of practice. Focuses on developmentally and culturally appropriate coaching contexts throughout the lifespan (including childhood, adolescence and adulthood). Attention is given to coaching for diverse players and inclusive practices. Applies player-centred coaching principles.', 'Prerequisite: Students are required to consent to the disclosure of criminal convictions and safety checks as required by the Children\'s Act 2014', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Sport Studies', 'SPORT 302', 'Sport Leadership', 'Develops the knowledge and skills for leading people and organisations in sporting contexts. Involves the study of leadership theories and styles from a range of different cultures. Includes critical examination of contemporary leadership strategies, issues, politics and policy. Develops interpersonal skills and leadership philosophies.', 'Prerequisite: SPORT 203, 202', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Sport Studies', 'SPORT 303', 'Managing Sport and Recreation', 'Critically examines the societal value and management of sport and recreation events at local, regional, national and international levels. Investigates aspects such as feasibility, community needs, site selection, scheduling, risk and volunteer management, logistics, publicity, marketing and evaluation. Emphasis is given to practice in applied settings.', 'Prerequisite: 30 points from SPORT 202, 203, 204, SPORTHPE 201, 202, 203', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Sport Studies', 'SPORT 304', 'Sport Psychology and Coaching', 'Critically examines recognised principles and practices of coaching and managing sport teams, including contemporary knowledge and theories. Individual and team psychological and social practices are examined and critiqued. Explores how coaches analyse the playing environment and the needs of players, including life-sport balance.', 'Prerequisite: 30 points from SPORT 202, 203, 204, SPORTHPE 201, 202, 203', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Sport, Health and Physical Education', 'SPORTHPE 101', 'Sociocultural Foundations', 'Explores the sociological, historical, psychological and philosophical foundations of health, sport and movement cultures. Examines how health and human movement are culturally and socially conceptualised with regard to contemporary concerns and trends. Includes studies of different cultural (Pakeha, Mori, Pacific, Asian) concepts of, and engagement with, health, sport and physical culture.', 'Restriction: EDCURRIC 135, EDUC 142', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Sport, Health and Physical Education', 'SPORTHPE 102', 'Learning and Pedagogy', 'An introduction to how humans learn and how such learning is applied within the fields of health, physical education, and sport. Examines the way humans develop and apply knowledge, skills, and dispositions and their implications for pedagogical practices in health, physical education, and sport contexts.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Sport, Health and Physical Education', 'SPORTHPE 103', 'Biophysical Foundations', 'Introduces students to the anatomical, physiological and biomechanical foundations of human movement. Examines the functions of the musculo-skeletal system, the circulo-respiratory system and the nervous system, during rest and activity. Studies the biomechanical principles required to improve mechanical efficiency in human movement.', 'Restriction: EDCURRIC 133', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Sport, Health and Physical Education', 'SPORTHPE 201', 'Whakatinanahia', 'Examines Mori approaches to embodiment, forms of physicality and movement valuable for educational and health settings in Aotearoa New Zealand. Students will gain knowledge through engaging in forms of Mori physical culture in a range of contexts.', 'Prerequisite: EDUCSW 101', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Sport, Health and Physical Education', 'SPORTHPE 202', 'Skill Learning', 'Develops knowledge and understanding of skill learning, teaching and coaching in human movement. Examines concepts of skill and theories of learning, including: psychological theories underpinning physical competency, child and youth development, motivation, inclusion and success. Develops an understanding of learner-centred, inquiry-based practice, and the development of pedagogical practices.', 'Prerequisite: SPORTHPE 102', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Sport, Health and Physical Education', 'SPORTHPE 203', 'Physiology, Exercise, Fitness', 'Develops knowledge and understanding of exercise physiology. Examines physiological responses during and as a result of exercise. Examines the nature and purpose of deliberate exercise and fitness programmes. Explores the pedagogy of teaching exercise and fitness.', 'Prerequisite: SPORTHPE 103', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Sport, Health and Physical Education', 'SPORTHPE 301', 'School Health and Physical Education', 'Examines health education and physical education in primary and secondary school contexts. Develops knowledge of pedagogy, curriculum, and programming. Critically examines how contemporary issues in the fields of health, sport and physical education impact schools.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Sport, Health and Physical Education', 'SPORTHPE 303', 'Health, Fitness and Culture', 'Critically examines the nature of incidental and deliberate exercise, lifestyle choices, and concepts of health. Critiques the veracity of evidence linking physical activity and health. Critically explores contemporary health issues associated with sedentary lifestyles, and the impact of twenty-first century lifestyle changes including globalisation and digitalisation. Examines how education can contribute to the diverse exercise and health needs of society.', 'Prerequisite: SPORTHPE 203', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Tertiary Foundation Certificate Education', 'TFCEDUC 12F', 'Introduction to Computing', 'Develops computer literacy skills in word processing, spreadsheets, presentation software, document collaboration and common multi-media technologies. These skills will be embedded in the context of tertiary study.', 'Restriction: EDFOUND 12F', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Tertiary Foundation Certificate Education', 'TFCEDUC 13F', 'Child Development and Learning', 'Presents an overview of language and learning development, and examines strategies for helping children to develop as learners and readers.', 'Restriction: EDFOUND 13F', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Tertiary Foundation Certificate Education', 'TFCEDUC 14F', 'An Introduction to the New Zealand Education System', 'Introduces students to the education system of New Zealand. Illustrates the historical development of the New Zealand education system, and addresses issues such as changes to governance and curriculum and ethnic diversity in New Zealand schools.', 'Restriction: EDFOUND 14F', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Tertiary Foundation Certificate Education', 'TFCEDUC 15F', 'Mathematics for Education', 'Development of fundamental mathematics concepts including an understanding of arithmetic ideas as expressed in fractions, decimals and percentages, ratio and proportion, and algebraic thinking. Application of these concepts in contexts such as financial literacy, problem solving, and real-life mathematics will form the basis of this course.', 'Restriction: EDFOUND 15F', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Tertiary Foundation Certificate Education', 'TFCEDUC 16F', 'Mathematics for Teaching Science and Technology', 'Students will develop critical thinking skills by designing and critiquing investigative methods for science and mathematics.', 'Restriction: EDFOUND 16F', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Tertiary Foundation Certificate Mori', 'TFCMAORI 10F', 'Te P', 'Introduction to functional and instructional Mori including everyday vocabulary, basic sentence structures, pronouns, possessives and positional language. Aspects of tikanga will include meeting and greeting people with waiata, karakia and hmene, and values such as whnau, whakawhanaungatanga and aroha. Referring to their own hap/iwi students will introduce and locate themselves in relation to their whakapapa and carry out a short mihi.', 'Restriction: EDFOUND 10F', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Tertiary Foundation Certificate Social Work', 'TFCSOCW 17F', 'Aotearoa Society in Context', 'Considers the migration stories of the many peoples who make up Aotearoa New Zealand society and explores some contemporary issues and trends with particular reference to education and social services. Particular consideration will be given to Te Tiriti o Waitangi.', 'Restriction: SOCWORK 11F', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Youth Work', 'YOUTHWRK 152', '', '</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Youth Work', 'YOUTHWRK 152G', 'Understanding New Zealand Youth', 'Examines the concept of \'youth\' and the historical, economic and political contexts in which young people live and are schooled in New Zealand society. The concept of youth is explored as a fundamental aspect of human development, identity and culture. The ways that we learn about what it is to be a young person in New Zealand today, including sport, body image, media, music, technology and social networking will be explored.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Youth Work', 'YOUTHWRK 253', 'Addictions and Youth', 'An examination of addictions related to intoxicating commodities such as alcohol, intimacy and sex, food and exercise, money and information technologies and their impact on health outcomes for young people in Aotearoa New Zealand. It examines the patterns and effects of discourses and practices related to various addictions and their treatment where youth are concerned.', 'Prerequisite: Any 30 points passed from the BHumServ Schedule', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Youth Work', 'YOUTHWRK 281', 'Special Topic', '', 'Prerequisite: Any 30 points passed from the BHumServ Schedule', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Youth Work', 'YOUTHWRK 381', 'Special Topic', '', 'Prerequisite: Any 30 points passed at Stage II from the BHumServ Schedule', 'null', 'null');
INSERT INTO `engineering` VALUES ('Academic Integrity', 'ACADINT A01', 'Academic Integrity Course', 'The Academic Integrity Course is an online course designed to increase student knowledge of academic integrity, university rules relating to academic conduct, and the identification and consequences of academic misconduct. Students work through a series of modules, outlining scenarios that they may encounter while studying at university. Each scenario provides information on relevant rules, resources and expected behaviour.\n</p>', 'null', 'null', 'null');
INSERT INTO `engineering` VALUES ('Bioengineering', 'BIOENG 796A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `engineering` VALUES ('Bioengineering', 'BIOENG 796B', 'ME Thesis (Bioengineering)', 'Students are required to submit a thesis on a topic assigned by the Director of Bioengineering.', 'To complete this course students must enrol in BIOENG 796 A and B', 'null', 'null');
INSERT INTO `engineering` VALUES ('Biomedical Engineering', 'BIOMENG 221', 'Mechanics of Engineered and Biological Materials', 'Introduction to the laws of conservation of mass, linear momentum, angular momentum and energy and their application to engineering problems. Topics include control volume analysis, fluid statics, Bernoulli’s equation, heat conduction, diffusion, linear elasticity, stresses and strains specific to direct and torsional loading, material constitutive relationships (including anisotropy, nonlinearity, and viscoelasticity), axial and transverse loading, and pressure loading of engineering structures and biomaterials.', 'Prerequisite: ENGGEN 150, or ENGSCI 111, or B+ or higher in MATHS 108 or 110 or 150 or 153, or B+ or higher in MATHS 120 and 130', 'null', 'null');
INSERT INTO `engineering` VALUES ('Biomedical Engineering', 'BIOMENG 241', 'Instrumentation and Design', 'An introduction to engineering instrumentation related to the measurement of biological signals. Topics include: Fundamentals of measurement systems (electric circuits, basic electronics, frequency domain signal analysis and transient analysis, measurement systems). This course will cover the design methodology of instrumentation systems and include an instrumentation design project.', 'Prerequisite: ELECTENG 101', 'null', 'null');
INSERT INTO `engineering` VALUES ('Biomedical Engineering', 'BIOMENG 261', 'Tissue and Biomolecular Engineering', 'Overview of molecular and tissue engineering principles emphasising biochemical kinetics, gene regulation, cell behaviour and biomedical ethics. Laboratory practice and design project in cell culture and molecular biology techniques. Topics include enzymes and regulation of metabolic pathways, thermodynamic principles of biochemical reactions, systems biology and regulatory motifs in biochemical networks, cell culture techniques, research and medical ethics.', 'Prerequisite: BIOSCI 107, ENGSCI 211', 'null', 'null');
INSERT INTO `engineering` VALUES ('Biomedical Engineering', 'BIOMENG 321', 'Continuum Modelling in Bioengineering', 'An introduction to continuum modelling approaches to bioengineering problems across a range of spatial scales. Topics include: tensor analysis, molecular and cellular mechanics of striated muscle; finite deformation elasticity and constitutive relations for soft biological materials; conservation equations for momentum, mass and heat transfer in fluids; viscous flow; boundary layers; pure conduction and diffusion; advective transport of mass and heat.', 'Prerequisite: BIOMENG 221, ENGSCI 211', 'null', 'null');
INSERT INTO `engineering` VALUES ('Biomedical Engineering', 'BIOMENG 341', 'Bioinstrumentation and Design', 'Sensors and actuators (temperature, position, force, pressure, flow, bioelectric, optical sensors and instruments). Signals, systems and controls (s-domain signal notation, transfer functions, frequency response functions, block diagrams, the Laplace transform, first and second order systems, characterisation methods, fundamentals of control). Bioinstrumentation design methodology, Biomedical instrumentation design project.', 'Prerequisite: BIOMENG 241', 'null', 'null');
INSERT INTO `engineering` VALUES ('Biomedical Engineering', 'BIOMENG 771', 'Musculoskeletal and Orthopaedic Biomechanics', 'Topics that biomechanical and orthopaedic engineers use in research and industry. Includes guest lectures from practitioners. Orthopaedic engineering topics cover implant design, material choice, implant stress shielding and bone loss, implant wear and bone remodelling. Musculoskeletal biomechanics topics cover motion capture, inverse kinematics and dynamics, muscle force evaluation, electromyography (EMG), inertial sensors and applications in sports medicine and rehabilitation.', 'Prerequisite: 15 points from ENGSCI 311, 313, 314', 'null', 'null');
INSERT INTO `engineering` VALUES ('Biomedical Engineering', 'BIOMENG 791', 'Advanced Biomedical Engineering Design', 'An engineering project requiring the application and integration of material taught in lecture courses to the design of medical devices and software to meet client needs. The project also requires consideration of ethical issues, social impact, safety risks, and international regulations.', 'Prerequisite: BIOMENG 341', 'null', 'null');
