INSERT INTO `engineering` VALUES ('Chemical and Materials Engineering', 'CHEMMAT 776A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `engineering` VALUES ('Chemical and Materials Engineering', 'CHEMMAT 776B', 'Food Process Engineering Research Portfolio - Level 9', 'A structured supervised research portfolio addressing a topic relevant to the development and commercialisation of food process engineering technologies.', 'Restriction: CHEMMAT 774, 775, 777', 'null', 'null');
INSERT INTO `engineering` VALUES ('Chemical and Materials Engineering', 'CHEMMAT 777A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `engineering` VALUES ('Chemical and Materials Engineering', 'CHEMMAT 777B', 'Food Process Engineering Research Portfolio - Level 9', 'A structured supervised research portfolio addressing a topic relevant to the development and commercialisation of food process engineering technologies.', 'Restriction: CHEMMAT 774, 775, 776', 'null', 'null');
INSERT INTO `engineering` VALUES ('Chemical and Materials Engineering', 'CHEMMAT 778', 'Dairy Process Engineering - Level 9', 'Industry-focused advanced topics in post-farm-gate processing of milk including liquid milk, powdered dairy and fermented products. Waste and high value product recovery from milk processing. Trends in global dairy industry including new development in food physics and chemistry, new products and processes, design and production of novel foods. Includes individual project-based work, laboratory work and completion of a group-based project. Includes independent research to create unique innovative solutions to an open-ended problem.\n</p>', 'null', 'null', 'null');
INSERT INTO `engineering` VALUES ('Chemical and Materials Engineering', 'CHEMMAT 779A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `engineering` VALUES ('Chemical and Materials Engineering', 'CHEMMAT 779B', 'Food Engineering Research Project - Level 9', 'A structured supervised research project addressing a topic relevant to the development and commercialisation of food process engineering technologies.', 'Restriction: CHEMMAT 774, 775, 776, 777', 'null', 'null');
