INSERT INTO `engineering` VALUES ('Civil Engineering', 'CIVIL 779B', 'Project in Transportation - Level 9', 'Students are required to submit a report on a topic in transportation assigned by the Head of Department.', 'To complete this course students must enrol in CIVIL 779 A and B', 'null', 'null');
INSERT INTO `engineering` VALUES ('Civil Engineering', 'CIVIL 782', 'Water Resources Engineering', 'A selection from the following: reservoir design and optimisation, flood control and design of flood control structures, micro to large scale hydroelectric engineering, river engineering and sedimentation. A water resources engineering design project.', 'Prerequisite: ENVENG 333 or equivalent', 'null', 'null');
