INSERT INTO `engineering` VALUES ('Civil Engineering', 'CIVIL 790', 'Civil Engineering Administration', 'The application of legal principles to problems in civil engineering and environmental engineering management. Examines the administration of national and international engineering contracts. Discusses statutes affecting engineering business. Investigates the implications of resource management and natural resource allocation legislation on engineering projects. Analyses processes for resolving engineering disputes.', 'Restriction: CIVIL 401, 490, ENGGEN 734', 'null', 'null');
INSERT INTO `engineering` VALUES ('Civil Engineering', 'CIVIL 791', 'Construction Management', 'Understanding topics necessary for effective construction management. Using a generic construction project life cycle, essential aspects of construction projects including the tendering process, preparing tenders, tender evaluation, project planning, resource allocation, teamwork, site safety, and contract types are covered. Case studies are used to reinforce the application of theoretical ideas to the successful running of construction projects.', 'Restriction: CIVIL 409', 'null', 'null');
INSERT INTO `engineering` VALUES ('Civil Engineering', 'CIVIL 792', 'Discrete-event Simulation in Construction - Level 9', 'Application of discrete-event simulation (DES) modelling to advanced planning and design construction operations and management of the construction supply chain. Critical assessment of the improvements in efficiency of planning methods and decisions patterns in construction management using DES. Individual and team research projects will apply advanced DES concepts and methods to complex, real-world construction projects.\n</p>', 'null', 'null', 'null');
