INSERT INTO `engineering` VALUES ('Computer Systems Engineering', 'COMPSYS 201', 'Fundamentals of Computer Engineering', 'Digital systems and binary coding; binary numbers; Boolean algebra and computer logic; combinational logic circuits; sequential logic circuits; hardware description language; digital design flow; register transfer level descriptions and design; data paths and control units; from circuits to microprocessors; basic computer organisation; introduction to modern microprocessors; timers and interfacing; C and assembly language for microprocessors; designing digital systems using microprocessors.', 'Prerequisite: ELECTENG 101', 'null', 'null');
INSERT INTO `engineering` VALUES ('Computer Systems Engineering', 'COMPSYS 202', 'Object Oriented Design and Programming', 'A project-based course with extensive hands-on programming experience. Includes: an introduction to object oriented design including UML, sequence diagrams, use-case analysis; an introduction to object oriented programming in a modern high level language, algorithms, data abstraction and elementary data structures.', 'Prerequisite: ENGGEN 131 or ENGSCI 131', 'null', 'null');
INSERT INTO `engineering` VALUES ('Computer Systems Engineering', 'COMPSYS 209', 'Computer Systems Design', 'Project-based course introducing real-world design aspects of hardware and software components of computer systems using appropriate design methodology. Practical skills will be gained in computer aided design tools, printed circuit board design and construction.', 'Prerequisite: COMPSYS 201, ELECTENG 291, SOFTENG 250 or 281', 'null', 'null');
INSERT INTO `engineering` VALUES ('Computer Systems Engineering', 'COMPSYS 301', 'Design: Hardware Software Systems', 'An appreciation of the engineering design process as applied to computer systems. Design skills are enhanced through engineering projects which typically include elements of: computer hardware design, computer software design, system design and control, sensing, actuation and interfacing.', 'Prerequisite: COMPSYS 305, and COMPSYS 209 or ELECTENG 209, and COMPSYS 202 or SOFTENG 281', 'null', 'null');
INSERT INTO `engineering` VALUES ('Computer Systems Engineering', 'COMPSYS 302', 'Design: Software Practice', 'A project-based course to gain experience in software design emphasising problem solving techniques and applications in computer systems engineering. The course includes practical, real-world project(s) involving a representative subset of the following topics: algorithm and data structure selection and implementation, parsing and translation, object-oriented and multi-threaded programming, scripting languages, peer-to-peer communication over internet.', 'Prerequisite: COMPSYS 202 or SOFTENG 281', 'null', 'null');
INSERT INTO `engineering` VALUES ('Computer Systems Engineering', 'COMPSYS 303', 'Microcomputers and Embedded Systems', 'Embedded applications. Microprocessors, microcontrollers, architecture, organisation, programming memories, I/O interfacing. Sensors, actuators, analog interfaces. Hardware/Software partitioning and interfacing. Concurrency. Implementing data transformations and reactivity. Case studies.', 'Prerequisite: COMPSYS 201, and COMPSYS 202 or SOFTENG 251 or 281', 'null', 'null');
INSERT INTO `engineering` VALUES ('Computer Systems Engineering', 'COMPSYS 304', 'Computer Architecture', 'Modern processor architectures. Principles of modern processor design; pipelining; memory hierarchies; I/O and network interfacing; compiler and OS support; embedded processors; performance; multiprocessing.', 'Prerequisite: COMPSYS 201', 'null', 'null');
INSERT INTO `engineering` VALUES ('Computer Systems Engineering', 'COMPSYS 305', 'Digital Systems Design', 'Digital Systems implementation technologies with emphasis on hardware description languages and design abstraction levels; structural, architectural and behavioral modelling; register-transfer level design; datapath and control units; functional and timing simulations; FPGA-based implementation design flow and case studies.', 'Prerequisite: COMPSYS 201', 'null', 'null');
INSERT INTO `engineering` VALUES ('Computer Systems Engineering', 'COMPSYS 306', 'Artificial Intelligence and Machine Learning', 'Fundamentals of artificial intelligence, including topics from artificial neural networks, fuzzy models, genetic algorithms. Using machine learning as an application of artificial intelligence to use data for training and inference, including topics from convolutional neural networks, deep learning, pattern classification and recognition.', 'Prerequisite: COMPSYS 201, and COMPSYS 202 or SOFTENG 281', 'null', 'null');
INSERT INTO `engineering` VALUES ('Computer Systems Engineering', 'COMPSYS 700A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `engineering` VALUES ('Computer Systems Engineering', 'COMPSYS 700B', 'Research Project - Level 9', 'Students are required to submit a report on project work carried out on a Computer Systems Engineering topic assigned by the Head of Department. The work shall be supervised by a member of staff.', 'Prerequisite: COMPSYS 301, and 45 points from COMPSCI 313, COMPSYS 302-305, ELECTENG 303', 'null', 'null');
INSERT INTO `engineering` VALUES ('Computer Systems Engineering', 'COMPSYS 701', 'Advanced Digital Systems Design - Level 9', 'Advanced concepts in digital design including: System-on-Chip (system level description, behavioural and register-transfer descriptions); advanced modelling techniques and design flows; design space exploration and optimisation; hardware-software partitioning and trade-offs; component reusability; reconfigurable systems; low-power systems; case studies (speech, image, video algorithms implementation, application specific processor design); individual research projects to analyse the problem, model and implement the required hardware-software components.', 'Prerequisite: COMPSYS 305', 'null', 'null');
INSERT INTO `engineering` VALUES ('Computer Systems Engineering', 'COMPSYS 704', 'Advanced Embedded Systems - Level 9', 'Selected advanced topics from current research in embedded systems such as: embedded systems based on formal models of computation; centralised and distributed architectures for embedded systems; static and dynamic embedded systems; languages and frameworks for distributed embedded systems; actor and agent systems; verification. Includes a significant individual research project.', 'Prerequisite: COMPSYS 723, and 202 or SOFTENG 281', 'null', 'null');
INSERT INTO `engineering` VALUES ('Computer Systems Engineering', 'COMPSYS 705', 'Formal Methods for Engineers - Level 9', 'Mathematical modelling techniques for embedded, automation, and mechatronic systems; advanced techniques for validation and verification; techniques for formal specification; methods of verification such as bisimulation and model checking; state space explosion problem and solutions such as BDDs, symbolic model checking, and modular verification; verification of HDL/C using model checking tools. Includes a significant individual research project.', 'Prerequisite:COMPSYS 202 or 281 or ENGSCI 233 or MECHENG 270 or 313 or SOFTENG 211', 'null', 'null');
