INSERT INTO `engineering` VALUES ('Computer Systems Engineering', 'COMPSYS 770', 'Capstone Project', 'Final year team exercise with students in multi-disciplinary roles, with focus on computer systems engineering and integrating technical learning into realistic design outcomes. Comprehensive investigation of an open ended, complex, real or synthetic computer, electrical and software engineering problem with simulated professional design office constraints. Includes technical, economic and environmental impact components to complete a scheme assessment report.', 'Prerequisite: 75 points from Part III courses listed in the BE(Hons) Schedule for the Computer Systems Engineering specialisation', 'null', 'null');
