INSERT INTO `engineering` VALUES ('Disaster Management', 'DISMGT 701', 'Disaster Risk Management - Level 9', 'A broad based understanding of the critical elements of risk and risk management in pre- and post-disaster scenarios. Key elements include risk identification with regard to the forms and types of risk inherent in areas prone to disasters. Risk management approaches are explored and applied to different aspects of disaster management.\n</p>', 'null', 'null', 'null');
INSERT INTO `engineering` VALUES ('Disaster Management', 'DISMGT 703', 'Disaster Management and Resilience - Level 9', 'Disaster management concepts and approaches related to urban resilience, including societal and infrastructure resilience. Key elements include exploring holistic approaches to disaster management and assessment of the relationship between resilience and disaster management. This includes systems and complexity, policy and general regulatory environment. This course involves group work and a course project.\n</p>', 'null', 'null', 'null');
