INSERT INTO `engineering` VALUES ('Energy', 'ENERGY 721', 'Energy Resources', 'Past, present and likely future uses of various forms of energy focused on electricity generation. Energy resources. Energy economics, prices and markets. Environmental considerations in energy production and use. Climate change, carbon sequestration, carbon trading and carbon taxes.\n</p>', 'null', 'null', 'null');
INSERT INTO `engineering` VALUES ('Energy', 'ENERGY 722', 'Energy Technology', 'First and second laws of thermodynamics and thermodynamic cycles. Chemical and biological thermodynamics. Geotechnology for resource exploration and delineation. Engineering technology for production and use of oil and gas, coal, wind, geothermal, tidal, solar, nuclear, bio-fuels. Energy storage, batteries, fuel cells. Energy efficiency.\n</p>', 'null', 'null', 'null');
