INSERT INTO `engineering` VALUES ('Engineering General', 'ENGGEN 623', 'Advanced Topics in Engineering 2', 'Courses on topics determined each year by the Associate Dean Postgraduate in the Faculty of Engineering.\n</p>', 'null', 'null', 'null');
INSERT INTO `engineering` VALUES ('Engineering General', 'ENGGEN 701', 'Professional Project', 'A comprehensive investigation, analysis and reporting of a complex engineering design, development or professional engineering problem. Problem synthesis, solution specification, development and reporting as approved by the Head of Department of Mechanical Engineering.', 'Prerequisite: Departmental approval', 'null', 'null');
INSERT INTO `engineering` VALUES ('Engineering General', 'ENGGEN 705', 'Engineering Product Development', 'Advanced topics in the engineering design and development of new manufactured products, taking an integrated approach including technical, commercial, and user aspects. Theory is linked to practice through multidisciplinary teams engaging in projects and case studies.', 'Prerequisite: B grade or higher in ENGGEN 303', 'null', 'null');
