INSERT INTO `engineering` VALUES ('Engineering General', 'ENGGEN 792B', 'Research Project - Level 9', 'A research project which requires students to undertake a practical application in a temporary endeavour to deliver a product, service or specified outcome. May take the form of surveys, interviews, action research, project implementation and evaluation of modern advances in project management practices, or a project management oriented case study. Projects conducted by students working in pairs. Each student must prepare a separate individual report.', 'Prerequisite: Departmental approval required', 'null', 'null');
