INSERT INTO `engineering` VALUES ('Engineering Science', 'ENGSCI 700B', 'Research Project - Level 9', 'An investigation carried out under the supervision of a member of staff on a topic assigned by the Head of Department of Engineering Science. A written report on the work must be submitted.', 'To complete this course students must enrol in ENGSCI 700 A and B', 'null', 'null');
INSERT INTO `engineering` VALUES ('Engineering Science', 'ENGSCI 701', 'Studies in Engineering Science', 'An advanced course on topics to be determined each year by the Head of Department of Engineering Science.', 'Prerequisite: Departmental approval', 'null', 'null');
