INSERT INTO `engineering` VALUES ('Engineering Science', 'ENGSCI 711', 'Advanced Mathematical Modelling', 'A selection of modules on mathematical modelling methods in engineering, including theory of partial differential equations, integral transforms, methods of characteristics, similarity solutions, asymptotic expressions, theory of waves, special functions, non-linear ordinary differential equations, calculus of variations, tensor analysis, complex variables, wavelet theory and other modules offered from year to year.', 'Prerequisite: 15 points from ENGSCI 311, 313, 314', 'null', 'null');
INSERT INTO `engineering` VALUES ('Engineering Science', 'ENGSCI 712', 'Computational Algorithms for Signal Processing', 'Advanced topics in mathematical modelling and computational techniques, including topics on singular value decomposition, Principle Component Analysis and Independent Component Analysis, eigen-problems, and signal processing (topics on neural network models such as the multi-layer perception and self organising map).', 'Prerequisite: 15 points from ENGSCI 311, 313, 314', 'null', 'null');
INSERT INTO `engineering` VALUES ('Engineering Science', 'ENGSCI 721', 'Advanced Numerical Methods', 'An advanced course on finite elements, boundary elements and finite differences.', 'Prerequisite: Departmental approval', 'null', 'null');
INSERT INTO `engineering` VALUES ('Engineering Science', 'ENGSCI 740', 'Advanced Mechanics in Research and Technology', 'Applications of continuum mechanics to problems in biomechanics, fluid mechanics and solid mechanics. Including topics such as large deformation elasticity theory applied to soft tissues, inviscid flow theory, compressible flows, viscous flows, meteorology, oceanography, coastal ocean modelling, mixing in rivers and estuaries. Fracture, composite materials and geomechanics.', 'Prerequisite: BIOMENG 321 or ENGSCI 343', 'null', 'null');
INSERT INTO `engineering` VALUES ('Engineering Science', 'ENGSCI 741', 'Waves and Fracture', 'Advanced topics in mechanics including: waves and wave motion with applications to acoustics, optics, fluid flow problems and shock discontinuities using numerical methods. Fracture: modes of, displacement discontinuity in linear elasticity, stress intensity factor, spectral solution methods, finite friction. Applications include: hydraulic fracturing, earthquakes, macroscale strength of materials.', 'Prerequisite: BIOMENG 321 or ENGSCI 343', 'null', 'null');
INSERT INTO `engineering` VALUES ('Engineering Science', 'ENGSCI 742', 'Studies in Continuum Mechanics', 'An advanced course in continuum mechanics covering topics in the mechanics of solids and fluids and other continua.', 'Prerequisite: Departmental approval', 'null', 'null');
INSERT INTO `engineering` VALUES ('Engineering Science', 'ENGSCI 745', 'Petroleum Engineering', 'Interdisciplinary introduction to topics in geology, geophysics, reservoir engineering, drilling and production engineering relevant to the production of oil and gas. Mathematical models of multiphase fluid flow in porous media. Reservoir engineering tools for analysis and forecasting of reservoir performance. Unconventional petroleum resources.', 'Prerequisite: 15 points from ENGSCI 311, 313, 314', 'null', 'null');
INSERT INTO `engineering` VALUES ('Engineering Science', 'ENGSCI 753', 'Computational Techniques in Mechanics and Bioengineering', 'Theoretical and applied finite element and boundary element methods for static and time dependent problems of heat flow, bioelectricity, linear elasticity and non-linear mechanics.', 'Prerequisite: ENGGEN 131 or equivalent, and 15 points from ENGSCI 311, 313, 314', 'null', 'null');
INSERT INTO `engineering` VALUES ('Engineering Science', 'ENGSCI 755', 'Decision Making in Engineering', 'Introduction to techniques for decision making in engineering systems including decision heuristics, simple prioritisation, outranking approaches, analytic hierarchy process, application to group decision making.', 'Prerequisite: Departmental approval', 'null', 'null');
INSERT INTO `engineering` VALUES ('Engineering Science', 'ENGSCI 760', 'Algorithms for Optimisation', 'Meta-heuristics and local search techniques such as Genetic Algorithms, Simulated Annealing, Tabu Search and Ant Colony Optimisation for practical optimisation. Introduction to optimisation under uncertainty, including discrete event simulation, decision analysis, Markov chains and Markov decision processes and dynamic programming.', 'Prerequisite: COMPSCI 101 or ENGGEN 131', 'null', 'null');
INSERT INTO `engineering` VALUES ('Engineering Science', 'ENGSCI 761', 'Integer and Multi-objective Optimisation', 'Computational methods for solving optimisation problems. Algorithms for integer programming including branching, bounding, cutting and pricing strategies. Algorithms for linear and integer programmes with multiple objective functions.', 'Prerequisite: ENGSCI 391', 'null', 'null');
INSERT INTO `engineering` VALUES ('Engineering Science', 'ENGSCI 762', 'Scheduling and Optimisation in Decision Making', 'A course of advanced topics arising in the practical application of optimisation models for machine and resource scheduling, routing applications, staff rostering and performance measurement.', 'Prerequisite: ENGSCI 391', 'null', 'null');
INSERT INTO `engineering` VALUES ('Engineering Science', 'ENGSCI 763', 'Advanced Simulation and Stochastic Optimisation', 'Advanced simulation topics with an emphasis on optimisation under uncertainty. Uniform and non-uniform random variate generation, input distribution selection, output analysis, variance reduction. Simulation-based optimisation and stochastic programming. Two-stage and multi-stage programs with recourse. Modelling risk. Decomposition algorithms. Scenario construction and solution validation.', 'Prerequisite: ENGSCI 391', 'null', 'null');
INSERT INTO `engineering` VALUES ('Engineering Science', 'ENGSCI 768', 'Advanced Operations Research and Analytics', 'Advanced Operations Research and Analytics topics including selected theory, algorithms and applications for non-linear programming, smooth and non-smooth optimisation, equilibrium programming and game theory.', 'Prerequisite: ENGSCI 391', 'null', 'null');
INSERT INTO `engineering` VALUES ('Engineering Science', 'ENGSCI 772', 'Whole Organ Modelling', 'Advanced computational modelling of whole organs with an emphasis on integrative physiology and coupled field problems in bioengineering. Finite deformation elasticity theory and computational aspects. Current flow in excitable tissue. Finite element collocation techniques. Coupled finite element – boundary element problems.', 'Prerequisite: BIOMENG 321 or ENGSCI 343', 'null', 'null');
INSERT INTO `engineering` VALUES ('Engineering Science', 'ENGSCI 773', 'Capstone Project', 'Engineering science projects that require the application and integration of material taught in courses for the design of solutions requiring computational engineering, data analytics and operations research to meet client needs. Projects also require consideration of ethical issues, social impact, safety risks, and engineering practice.', 'Prerequisite: ENGSCI 363 and 60 points from courses listed in Part III of the BE(Hons) Schedule for Engineering Science', 'null', 'null');
