INSERT INTO `engineering` VALUES ('Environmental Engineering', 'ENVENG 200', 'Fundamentals of Environmental Engineering', 'Introduction to environmental engineering principles. Role of environmental engineers in the twenty-first century. Environmental measurements, environmental standards and impact assessment. Material mass balance. Drinking water, wastewater and stormwater treatment. Air quality parameters. Solid waste management. Sustainability. Environmental Impact Assessment.', 'Prerequisite: ENGGEN 140', 'null', 'null');
INSERT INTO `engineering` VALUES ('Environmental Engineering', 'ENVENG 244', 'Environmental Engineering 1', 'Water quality, water and wastewater characteristics – physical, chemical and biological treatments (unit operations and processes). Solid waste characteristics and disposal, hazardous waste treatment. Stormwater management.', 'Restriction: ENVENG 243', 'null', 'null');
INSERT INTO `engineering` VALUES ('Environmental Engineering', 'ENVENG 300', 'Natural and Built Environment Processes', 'Chemical and Biological treatment processes. Surface water quality modelling. Soil chemistry. Contaminant fate and transport in soil and groundwater. Contaminated sites remediation.', 'Prerequisite: ENVENG 200', 'null', 'null');
INSERT INTO `engineering` VALUES ('Environmental Engineering', 'ENVENG 333', 'Engineering Hydrology', 'Hydrologic processes, analysis of rainfall-runoff relationships. Statistical analysis of hydrological data. Groundwater movement.\n</p>', 'null', 'null', 'null');
INSERT INTO `engineering` VALUES ('Environmental Engineering', 'ENVENG 341', 'Environmental Engineering 2', 'Examines natural environmental processes and their relevance to engineering. Soil and water chemistry, equilibrium and organic chemistry, microbiology, biochemistry and biological processes will be examined, focusing on the application of these in engineering design, practice and management.\n</p>', 'null', 'null', 'null');
INSERT INTO `engineering` VALUES ('Environmental Engineering', 'ENVENG 342', 'Environmental Engineering Design', 'The applications of design practice in environmental engineering with a number of design projects. Elements of water and wastewater engineering. Landfill design and air pollution control.', 'Restriction: ENVENG 405', 'null', 'null');
INSERT INTO `engineering` VALUES ('Environmental Engineering', 'ENVENG 400', 'Special Topic', '</p>', 'null', 'null', 'null');
INSERT INTO `engineering` VALUES ('Environmental Engineering', 'ENVENG 702', 'Engineering Decision Making in Aotearoa - Level 9', 'Advanced systems engineering based decision making; complex problem framing including ontology analysis; cultural opportunity mapping; absolute sustainability analysis; risk threshold determination; temporal cumulative effects; and effective consultation. Independent research is undertaken to solve a complex engineering decision making problem.\n</p>', 'null', 'null', 'null');
INSERT INTO `engineering` VALUES ('Environmental Engineering', 'ENVENG 703', 'Studies in Environmental Engineering 3 - Level 9', 'A course on a topic in environmental engineering to be determined each year by the Head of Department of Civil and Environmental Engineering. The course will include the independent application of highly specialised knowledge and skills related to the study area.\n</p>', 'null', 'null', 'null');
INSERT INTO `engineering` VALUES ('Environmental Engineering', 'ENVENG 705', 'Special Topic in Environmental Engineering 1 - Level 9', 'A course on a topic in environmental engineering to be determined each year by the Head of Department of Civil and Environmental Engineering. The course will include the independent application of highly specialised knowledge and skills related to the study area.', 'Restriction: ENVENG 402', 'null', 'null');
INSERT INTO `engineering` VALUES ('Environmental Engineering', 'ENVENG 706', 'Special Topic in Environmental Engineering 2', 'A course on a topic in environmental engineering to be determined each year by the Head of Department.', 'Restriction: ENVENG 403', 'null', 'null');
INSERT INTO `engineering` VALUES ('Environmental Engineering', 'ENVENG 707', 'Advanced Water Treatment and Reuse - Level 9', 'Advanced water, wastewater, greywater, stormwater treatment technologies including advanced oxidation processes, photochemistry, electrochemistry, membrane treatment, and fundamentals of water reuse, applications, and case studies for potable reuse, industrial reuse, and aquifer recharge. Includes an individual research project.', 'Prerequisite: ENVENG 244, 342', 'null', 'null');
INSERT INTO `engineering` VALUES ('Environmental Engineering', 'ENVENG 719', 'Design Project', 'A design project requiring input from more than one engineering subdiscipline. The department will offer a number of projects from which the students may select. It will be possible for groups of students to work together on a project. Assessment will be based on a report and an oral presentation of the outcome of the project.', 'Restriction: ENVENG 419', 'null', 'null');
INSERT INTO `engineering` VALUES ('Environmental Engineering', 'ENVENG 740', 'Water and Wastewater Engineering', 'Chemistry and microbiology of water and wastewater treatment, flow models and reactors. Unit operations and process analysis and design. Treatment plant design and operation. Nutrient removal processes. Effluent and residues disposal.\n</p>', 'null', 'null', 'null');
INSERT INTO `engineering` VALUES ('Environmental Engineering', 'ENVENG 744', 'Environmental Engineering Processes Laboratory', 'Laboratory research methods (safety, sampling procedures, sample preservation, data analysis and report writing). Laboratory experiments exploring various physical, chemical and biological processes, such as sedimentation, chemical coagulation and precipitation, chlorination, reactor residence time distribution, activated carbon and anaerobic digestion.\n</p>', 'null', 'null', 'null');
INSERT INTO `engineering` VALUES ('Environmental Engineering', 'ENVENG 746', 'Surface Water Quality Modelling - Level 9', 'Advanced specialist topics in modelling of lakes and rivers. Specific topics covered include response to different loadings applied to surface water systems, and modelling of organic matter, dissolved oxygen consumption, eutrophication, and toxic substances. The core taught skills are extended by an individual project in which independent research is undertaken to solve a challenging surface water quality engineering problem.', 'Prerequisite: ENVENG 341, 342', 'null', 'null');
INSERT INTO `engineering` VALUES ('Environmental Engineering', 'ENVENG 747', 'Soil-Contaminant Fate Processes and Modelling - Level 9', 'Focuses on modelling sorption, degradation kinetics, and leaching of chemicals in the soil environment. Topics include deriving sorption parameters, parent and metabolite fitting with statistical rigours, calculating degradation end-points, novel adsorbents for removing contaminants in soil and water. The core taught skills are extended by an individual project in which independent research is undertaken to solve an environmental issue.', 'Prerequisite: ENVENG 341 or equivalent', 'null', 'null');
INSERT INTO `engineering` VALUES ('Environmental Engineering', 'ENVENG 750', 'Advanced Sustainability Engineering - Level 9', 'Focuses on an advanced understanding of the science of sustainability and its application to engineering practice and management, including complex systems thinking, tools to assess sustainability, management, leadership and decision making leading to sustainability, global directions towards sustainability across cultural systems. Develops critical analytical thinking and research based knowledge through debates and an applied research project.\n</p>', 'null', 'null', 'null');
INSERT INTO `engineering` VALUES ('Environmental Engineering', 'ENVENG 752', 'Risk, LCA and Sustainability', 'The objectives of this course are to provide students with an understanding of sustainability, life cycle assessment, impact assessment and risk assessment and how these can be used to measure sustainability. The format will include discussions on sustainability, assessment methods and sustainability assessment, including scoping, sustainable levels, inventory, impact and risk assessment and mitigations measures.\n</p>', 'null', 'null', 'null');
