INSERT INTO `engineering` VALUES ('Structural Engineering', 'STRCTENG 200', 'Introductory Structural Mechanics', 'Introduction to structural analysis for civil engineers. Equilibrium, internal actions and deformations, structural forms, structural systems, analysis of determinate systems, plane section properties, elasticity, engineering beam theory, failure theories. Prepares students to embark on further studies in structural design.', 'Prerequisite: ENGGEN 121', 'null', 'null');
INSERT INTO `engineering` VALUES ('Structural Engineering', 'STRCTENG 201', 'Civil Engineering Materials and Design', 'Properties and manufacturing of civil engineering material including concrete, steel, timber structural products and roading material. Design principles and examples for concrete, steel and timber members.', 'Restriction: CIVIL 250', 'null', 'null');
INSERT INTO `engineering` VALUES ('Structural Engineering', 'STRCTENG 300', 'Design Loads and Dynamic Response of Structures', 'Determination of design loads according to AS/NZS1170 and the response of structures under dynamic loadings.', 'Prerequisite: CIVIL 210 or STRCTENG 200', 'null', 'null');
INSERT INTO `engineering` VALUES ('Structural Engineering', 'STRCTENG 301', 'Timber Structures Design', 'Structural analytical techniques including computer based approaches to simple indeterminate structures. Design procedures for members and structural systems of timber and engineered wood products. Design project.', 'Prerequisite: CIVIL 210 or STRCTENG 200', 'null', 'null');
INSERT INTO `engineering` VALUES ('Structural Engineering', 'STRCTENG 302', 'Steel Structures Design', 'Mechanical properties of steel and contextualises the application of steel and steel/concrete into buildings and bridges. Comprehensive introduction to design of structural steel members and connections and their use in structures. Application to vertical load carrying systems and steel building behaviour in earthquake and fire.', 'Prerequisite: CIVIL 210 or STRCTENG 200', 'null', 'null');
INSERT INTO `engineering` VALUES ('Structural Engineering', 'STRCTENG 303', 'Concrete Structures Design', 'Design of reinforced concrete members including beams, columns, walls, foundations. Introduction to prestressed and precast concrete design and applications. Use of the NZ Concrete Structures Standard, NZS 3101.', 'Prerequisite: CIVIL 210 or STRCTENG 200', 'null', 'null');
INSERT INTO `engineering` VALUES ('Structural Engineering', 'STRCTENG 304', 'Structural Design for Civil Engineers', 'Structural loading for gravity and wind in accordance with the loading code AS/NZS1170. Design principles and examples for concrete and timber members and design for timber framed buildings using NZS3604. Introduction to seismic building behaviour at a conceptual level.', 'Prerequisite: CIVIL 210 or STRCTENG 200', 'null', 'null');
INSERT INTO `engineering` VALUES ('Structural Engineering', 'STRCTENG 700', 'Low Rise Structures Design', 'Structural systems for low-rise buildings, including seismic design and analysis techniques. Design and detailing of low-rise structures in structural steel, reinforced concrete, reinforced masonry, and timber. Strut and tie design for reinforced concrete. Introduction to fire engineering. Techniques in the checking of existing structures and lessons learnt from failures.', 'Prerequisite: STRCTENG 302, and CIVIL 313 or STRCTENG 303', 'null', 'null');
INSERT INTO `engineering` VALUES ('Structural Engineering', 'STRCTENG 701', 'Multistorey Structures Design', 'Techniques for the design of multistorey structures to resist seismic loading. Derivation of design actions, alternative structural systems for resisting these loads, design of structural components subject to cyclic inelastic action, detailing of members and joints to enhance earthquake resistance. Techniques of seismic isolation.', 'Prerequisite: STRCTENG 302, and CIVIL 313 or STRCTENG 303', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Academic Integrity', 'ACADINT A01', 'Academic Integrity Course', 'The Academic Integrity Course is an online course designed to increase student knowledge of academic integrity, university rules relating to academic conduct, and the identification and consequences of academic misconduct. Students work through a series of modules, outlining scenarios that they may encounter while studying at university. Each scenario provides information on relevant rules, resources and expected behaviour.\n</p>', 'null', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Accounting', 'ACCTG 151G', 'Financial Literacy', 'People who understand the basic principles of finance are likely to get much more mileage out of their money – whether spending, borrowing, saving or investing – than those who do not. Develop an understanding of how to be in control of spending and saving; understand borrowing; make informed investment decisions; know broadly what to insure and what not to; recognise scams and consider whether money is the key to happiness.', 'Restriction: May not be taken by students with a concurrent or prior enrolment in Accounting or Finance courses', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Anthropology', 'ANTHRO 104G', 'Peoples and Cultures of the Pacific', 'A survey of the peoples of Pacific Islands through the perspectives of archaeology, biological anthropology, ethnomusicology, linguistics and social anthropology.\n</p>', 'null', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Anthropology', 'ANTHRO 105G', 'Question of Race and Racism', 'International, cross-cultural and interdisciplinary perspectives question \'race\' and \'racism\'. Why does the concept have such social and political potency? What are the impacts of concepts of race and practices of racism and anti-racism on individuals, families, communities, nation-states and empires, and in Aotearoa New Zealand and the Pacific in particular?\n</p>', 'null', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Anthropology', 'ANTHRO 106G', 'Issues and History in Popular Music', 'A survey of popular music styles, artists, sub-cultures and issues that explores facets such as genre, the music industry, music and politics, music videos, the sales process, race and identity, and gender theory. Core theory and writers in popular music studies are introduced and popular music is used to explore societal changes in class, ethnicity, gender, sexuality, youth, and global economic and cultural processes. Note: Does not meet the General Education requirement for BMus or BMus conjoints.', 'Restriction: POPMUS 106, 106G', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Architectural History, Theory and Criticism', 'ARCHHTC 102G', 'Modern Architecture and Urbanism', 'Examines through case studies the cultural contexts that shaped the development of architecture, urban design, landscape and the environment during the twentieth century. Emphasis is placed on the historical developments that influenced changes in style and the theoretical contexts that shaped attitudes towards inhabitation, social organisation, national identity, and cultural self-expression, amongst other things.', 'Restriction: ARCHHTC 100', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Art History', 'ARTHIST 114G', 'Understanding Art: Leonardo to Warhol', 'Is seeing learned? Can an image be read in the same way as a text? Understanding images from different historic periods, from Leonardo da Vinci to Andy Warhol, is central to everyday life. Visual literacy is fundamental to all disciplines. This course provides students with tools for making sense of various kinds of images and objects: photographs, advertisements, paintings, film, television, monuments, buildings, maps, landscape, digital and internet images.', 'Restriction: ARTHIST 109', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Art History', 'ARTHIST 115G', 'Global Art Histories', 'A broad survey of visual art spanning from the early modern period to the contemporary. Students will be introduced to a range of art practices situated within a global context and will consider art works produced in Mori and Pacific cultures alongside Indian, South Asian, Middle Eastern, European and American traditions.\n</p>', 'null', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Asian Studies', 'ASIAN 140G', 'New Zealand and Asia', 'Explores Asia and its interrelationship with New Zealand, including Asia\'s growing presence in New Zealand in all its manifestations, and the evolving political, social, economic, cultural, and strategic relations between this country and Asia. Topics will include historical and contemporary ties with Asia, Asian migration, literature, media and films. The course will focus especially on South-East and East Asia.\n</p>', 'null', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Astrosciences', 'ASTRO 100G', 'Planets, Stars and Galaxies', 'The story of our place in the Universe. Key topics are the exploration of the solar system, searches for planets around other stars, the structure and evolution of stars and galaxies, high-energy astrophysics, and the origin and overall properties of the Universe. No background in physics or mathematics is assumed.', 'Restriction: PHYSICS 107, 107G', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Astrosciences', 'ASTRO 200G', 'Astrobiology', 'Astrobiology examines the potential of the universe to harbour life and is interdisciplinary, combining Geology, Biology, Astronomy, Chemistry, Physics, Philosophy, Ethics. Course focus is on how these disciplines combine with technology, addressing questions of life in the universe. Key topics include origin and evolution of life, definitions and environmental limits of life, and how to search for life beyond Earth.', 'Prerequisite: 60 points passed', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Biological Sciences', 'BIOSCI 100G', 'Antarctica: The Frozen Continent', 'A general introduction to Antarctica and its environs including the Southern Ocean and the sub-Antarctic islands. Emphasis will be placed on the evolution of Antarctica and how resident plants, animals and micro-organisms have adapted to cope with the extreme environment. Specific topics to be addressed include: the history of Antarctic exploration and its impact on the development of Antarctic science, Antarctic ecosystems, Antarctica as a wilderness region, and the impact of humans including the exploitation of resources and the effects of pollution. This course is suitable for students with both science and non-science backgrounds.\n</p>', 'null', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Business', 'BUSINESS 151G', 'Communication in a Multicultural Society', 'Communication knowledge and skills are essential in business careers and for interpersonal and intercultural relationships. This course offers a theory-based approach combined with applied communication practices. Communication knowledge, competencies and skills are developed through exploring relationships, mediated communication, writing, team dynamics, oral presentation and technologies.', 'Restriction: BUSINESS 101, 291, MGMT 291', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Business Analytics', 'BUSAN 100G', 'Digital Information Literacy', 'Introduces students to skills, technologies, and techniques for the effective use of digital information. Information in all spheres of personal and professional life is increasingly created, stored, analysed, exchanged and communicated in digital forms. Digital information literacy will help students be more productive in the digital age.', 'Restriction: Cannot be taken with or after INFOSYS 110-345', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Career', 'CAREER 100G', 'Crafting your Career', 'What is employability? The world of work is changing rapidly. Crafting your Career uses project-based, collaborative, problem-solving exercises to assist students in readying themselves for life after the degree. The course aims to build students’ work-readiness by enhancing their understanding of a variety of workplaces, while developing the skills employers regard as essential to career success.', 'Prerequisite: 60 points passed', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Chemical and Materials Engineering', 'CHEMMAT 100G', 'Materials of the Modern World', 'Every aspect of daily living is influenced in some way by the materials that surround us. Ceramics, metals, polymers, and composites; each has its own properties which have, over time, influenced the development of modern technological societies. Take a moment to imagine a world without metal, for example, to see how central the science of materials is to everyday life. This course will explore, at a non-specialist level, the basic principles governing the properties and behaviour of a wide variety of common materials and examine their applications and limitations.\n</p>', 'null', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Chemistry', 'CHEM 100G', 'Molecules that Changed the World', 'The impact of chemistry on the modern world will be explored by focusing on the stories of specific molecules, including penicillin, DDT and nylon. Their discovery, the underlying chemical principles that explain their behaviour, their impact on our lives including social and scientific issues that arise from their use, and their likely impact on the future will be investigated. No formal prerequisite, but the course assumes a science background at Year 11 or higher.\n</p>', 'null', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Chinese', 'CHINESE 100G', 'Beginning Modern Chinese 1', 'Introduces students to modern Standard Chinese (Mandarin, <i>Putonghua</i> ) through exercises and activities to develop speaking, listening, reading and writing skills. Also introduces the social and cultural background of the language.', 'Putonghua', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Classical Studies', 'CLASSICS 110G', 'Classical Mythology', 'A study of ancient Greek and Roman mythology – its gods, heroes and monsters – through the works of major writers and artists from the Greco-Roman world.\n</p>', 'null', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Communication', 'COMMS 104G', 'Advertising and Society', 'A critical examination of advertising and advertisements focusing on the role advertising plays in consumer culture. Advertisements from a diverse range of media are studied in order to analyse how advertisements construct and disseminate meaning. The course investigates how advertising engages with the logic of wider cultural and global transformations with consideration given to both consumer and industry perspectives.', 'Restriction: FTVMS 110, 110G', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Computer Science', 'COMPSCI 111G', 'An Introduction to Practical Computing', 'A practical introduction to computing. Topics include: web design, an overview of computer hardware and operating systems, effective use of common applications, using the internet as a communication medium, applying programming concepts, and social implications of technology.\n</p>', 'null', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Cook Islands Mori', 'COOKIS 101G', 'Introduction to Cook Islands Mori', 'Gives students an introduction to the structure of Cook Islands Mori as well as allowing them to develop basic skills in listening, speaking, reading and writing. Designed for students with little or no knowledge of the language, and for those with some fluency wishing to understand simple sentence structure and composition.', 'Restriction: COOKIS 102. May not be taken if a more advanced language acquisition course in this subject has previously been passed', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Dance Studies', 'DANCE 101G', 'Introduction to Dance and Creative Processes', 'To develop an understanding of our moving bodies through movement awareness, dance improvisation, choreography and creative and analytic writing. Students will undertake both theoretical and practical classes focusing on a range of practices that dancers and movement practitioners use to facilitate kinaesthetic awareness, experimentation, communication and choreography. Students will explore somatic theory and practice, improvisation scores, choreography and dance analysis. DANCE 101G not available for BDanceSt.\n</p>', 'null', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Dance Studies', 'DANCE 200G', 'Dance and Culture', 'Examines the interrelationship between dance and wider political and cultural movements through practical dance classes and theoretical investigations into diverse cultural environments around the world. Students physically and theoretically engage in the study of various dance forms such as Tango, Salsa, Dabkeh, traditional Chinese dance and Bharata Natyam.', 'Prerequisite: 60 points passed', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Design', 'DESIGN 102G', 'Design Futures', 'New opportunities are continually emerging in the field of design. This course demonstrates how contemporary design practices have evolved, responded to and influenced change. Students learn how a design approach complements current practice and expands career prospects.\n</p>', 'null', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Disability Studies', 'DISABLTY 113G', 'Making Disabilities: The Construction of Ideas', 'Examines the expression of social and cultural ideas of disability in popular culture through film, television and print media. The course aims to develop skills to examine the construction and maintenance of concepts of disability and disabling identities in popular culture. The consequences of these processes are also discussed and their implications for perpetuating social devaluation, discrimination, and disadvantage.\n</p>', 'null', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Drama', 'DRAMA 100G', 'Presentation and Performance Skills: Taking the Stage', 'Focuses on enhancing oral communication and performance skills through interactive workshops with speakers and performers highlighting the transferable skills of acting in three main areas: public speaking, improvising and group-devised performance.\n</p>', 'null', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Earth Sciences', 'EARTHSCI 105G', 'Natural Hazards in New Zealand', 'New Zealanders are exposed to extreme natural events and processes including earthquakes, volcanic eruptions, weather bombs, storm surge, tsunami, flooding, landslides and erosion. The physical context for each hazard is provided, drawing on the disciplines of geology, geomorphology and climatology. The frequency and magnitude of natural hazards for New Zealand are considered using different sources. Impacts on modern society are discussed using case studies and scenario modelling.', 'Restriction: GEOG 105, 105G, GEOLOGY 110', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Earth Sciences', 'EARTHSCI 205G', 'New Zealand: Half a Billion Years on the Edge', 'Take a 500 million year journey through time following the geologic and biologic development of New Zealand from humble beginnings on the edge of the ancient supercontinent Gondwana to the present day geologically dynamic land mass beset by volcanic eruptions, earthquakes and massive erosion as a consequence of being located on the edge of the Earth\'s largest tectonic plate.', 'Prerequisite: 75 points passed', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Economics', 'ECON 151G', 'Understanding the Global Economy', 'Economics affects our daily lives and the global environment in many ways. Through the media we are constantly made aware of price increases, interest rate changes, exchange rate movements and balance of payments problems, growth and recessions, standard of living comparisons, regional trading agreements. What does it all mean and how does it all work?', 'Restriction: ECON 101, 111, 152, 191', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Education', 'EDUC 100G', 'The Creative Process', 'Theories and practices of creativity will be examined and practically explored through a variety of disciplines, such as the arts, biology, psychology, sociology, philosophy and education. What is creativity? Can creativity be learnt? What happens in the brain when we are creative? These are some of the questions addressed in this course.\n</p>', 'null', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Education', 'EDUC 104G', 'Sport in Society', 'Critically examines the socio-cultural, political and economic significance of sport within Aotearoa New Zealand. Examines how sport is embedded in the lives of people, constitutes identities, and is connected to major spheres of social life and various social issues. Through focusing on select sporting issues it analyses how New Zealanders negotiate understandings of self, ethnicity, gender, sexualities, health, and lifestyle.\n</p>', 'null', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Education', 'EDUC 105G', 'Teaching: Tales and Traditions', 'Introduction to key ideas on teachers and teaching. Explores teaching traditions, their origins, stories of teaching in New Zealand; stories of teachers that generate change; and how teaching and teachers are understood in a variety of disciplines such as Science, Health, Arts, and Sport. Considers the following: How should we teach? What counts as knowledge? What contradictions do teachers encounter?\n</p>', 'null', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Education', 'EDUC 121G', 'How People Learn', 'Focuses on learning in formal and informal settings and addresses such questions as: why do some things seem easier to learn than others, why do we forget things we once knew, and why do some people learn faster or better than others? Examines the nature of intelligence and how to help personal learning or the learning of others.', 'Restriction: EDUC 111, 117', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Education', 'EDUC 122G', 'Learning Sexualities', 'How and what do we learn about sexualities in New Zealand? Learning about sexualities is viewed as occurring both formally (e.g., through sexuality education) and informally (e.g., through the media) in a diversity of social sites. Schools are examined as one significant site where students are offered sexual meanings. The historical derivation and current context of contemporary education about sexuality along with its social effects are investigated.\n</p>', 'null', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Engineering General', 'ENGGEN 100G', 'Technological Choices for the Future', 'A consideration of technological choices to support informed decision making in the use of technology in modern society. The course focuses on important questions such as: What is the future direction of power generation in New Zealand? How can we create a sustainable future? Where will current developments in robotics and mobile communications lead us?\n</p>', 'null', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('English', 'ENGLISH 102G', 'Great Books: Seduction and Betrayal', 'Surveys a selection of literary masterpieces by major authors from different periods in the history of English literature. Selection of texts is organised around the theme of seduction and betrayal, understood more particularly as a story-arc exploring attitudes to love and sex, to politics and ambition, to ethical conduct, and to the activity of reading itself.\n</p>', 'null', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('English', 'ENGLISH 121G', 'Reading/Writing/Text', 'Develops University-wide skills of reading, writing and analysis. Addresses the needs of students in both English and other disciplines where both writing and reading have an important role in learning. The course fosters personal writing skills and also introduces writing as a subject of study in itself.\n</p>', 'null', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Environmental Science', 'ENVSCI 101G', 'Environment, Science and Management', 'Explores the science behind key environmental issues to recognise the role environmental science plays in understanding the interaction between humans and the environment. The complexity of environmental problems and the difficult task of integrating science, knowledge and values are discussed.\n</p>', 'null', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('European Studies', 'EUROPEAN 100G', 'Europe and the World', 'An introduction to the study of Europe, organised around a number of major themes, including linguistic and ethnic groupings, historical periods, literary and cultural movements, religious and philosophical traditions, and political and cultural figures. An ideal course for students wishing to explore European culture and civilisation.\n</p>', 'null', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Exercise Sciences', 'EXERSCI 100G', 'Exercise and Fitness: Myths and Reality', 'An introduction to the principles of physical exercise, with a focus on understanding how the body moves and responds to exercise, how performance can be measured, and how fitness can be developed and maintained to optimise health. Particular emphasis will be placed on the debunking of common myths about exercise, and offering evidence-based advice on the benefits of appropriate physical activity.', 'Restriction: BIOSCI 107, EXERSCI 101, 105, SPORTSCI 100G, 101, 105, MEDSCI 142', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Fine Arts', 'FINEARTS 210G', 'Understanding Contemporary Visual Arts Practice', 'How does the contemporary artist and designer work? This course presents first hand, locally engaged practitioners\' perspectives on creative practice. On successful completion of this course students will be able to access local, contemporary art and design with confidence, and to discuss examples of practitioners\' methodologies and areas of investigation. Course work will involve active exploration of the local art world.', 'Prerequisite: 60 points passed', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Fine Arts', 'FINEARTS 211G', 'Understanding Contemporary Fashion Design', 'Investigates the relationship between fashion design and identity to build understanding of the increasing rapidity of clothing change as both the product of individual choice and the manifestation of a need for community. The emphasis will be on the consumption of fashion and its relationship to the human body with reference to fashion theory in the context of the broader literatures of gender, class and ethnicity.', 'Prerequisite: 60 points passed', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('French', 'FRENCH 101G', 'Introductory French Language 1', 'Introduces students to spoken and written French. It is delivered through two 90-minute sessions per week on campus, blended with an on-line component that uses up-to-date methodology and extensive multimedia materials. It is open to beginners or near beginners. Students who have achieved 24 recent credits in Level 1 NCEA French or 12-16 recent credits in Level 2 NCEA French (or equivalent previous study) should enrol in FRENCH 102. FRENCH 101 does not count towards a major in French. May not be taken if a more advanced language acquisition course in this subject has previously been passed.\n</p>', 'null', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Geography', 'GEOG 103G', 'Mapping Our World', 'An introduction to contemporary geospatial technologies such as web-mapping, GPS and tracking devices and GIS. Covers key concepts and principles behind these tools and their use, along with practical experiences through laboratories. Critical and theoretical perspectives on the tools, their use, and their social impacts will be discussed.\n</p>', 'null', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Geography', 'GEOG 104G', 'Cities and Urbanism', 'What makes a great city? This course explores \'urbanism\' in both historical and contemporary cities to determine the essence of urbanity and the way that citizens (and visitors) experience city life. The dynamics and character of cities are considered in terms of their built environment, environmental systems, population, social diversity, and planning policies and practices.\n</p>', 'null', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('German', 'GERMAN 101G', 'German Language Introductory 1', 'Written and oral use of German for students with no previous knowledge of the language or with fewer than 16 credits in NCEA Level 2 German.', 'Restriction: May not be taken if a more advanced language acquisition course in this subject has previously been passed', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Global Studies', 'GLOBAL 101G', 'Global Issues, Sustainable Futures', 'The basis for sustainability – social issues such as population and consumption, environmental issues such as climate change, limited resources and environmental degradation. Discusses the roles that various disciplines (law, business, engineering and urban planning) will play in developing solutions, including consideration of human rights and good governance, new concepts in economics and business management which will lead to sustainable businesses, developments in science and technology which will change how we manage resources and new visions for cities and communities which will support sustainable ways of life.', 'Restriction: GENED 101G', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('History', 'HISTORY 103G', 'Global History', 'It is only since the fifteenth century that a truly global dimension to history can be identified. This course examines key determinants that have bound the fate of peoples together including the emergence of world trade networks, the growth of world religions, the spread of epidemic diseases, the formation of empires, and the migration of peoples across continents.\n</p>', 'null', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Humanities', 'HUMS 100G', 'Digital Humanities: From Text to txt', 'An interdisciplinary course designed to introduce students to the Humanities using digital tools and resources. Students will study the approaches, texts and digital technologies of disciplines in the Humanities such as Art History, English, History, Philosophy, and Theological and Religious Studies. Students will expand their knowledge of the Humanities, extend their digital literacy and build critical and creative thinking skills.', 'Restriction: ARTSGEN 100G', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Innovation', 'INNOVATE 100G', 'Innovation through Design', 'Introduces design practice and tools to students in order to develop a user-centred approach to innovation. Students learn about design and, through cycles of empathy, creativity and prototyping, learn to design for innovation.\n</p>', 'null', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Innovation and Entrepreneurship', 'INNOVENT 203G', 'The Entrepreneurial Mindset', 'Stimulates new ways of thinking about enterprising behaviour in a multi-disciplinary manner relevant to understanding and addressing real world challenges of today. Introduces skills needed to identify and assess opportunities, solve problems creatively, communicate persuasively, work effectively in teams, and understand individual and organisational impact.', 'Prerequisite: BUSINESS 102 or MGMT 101 or 90 points passed or 60 points from Part I of the BE(Hons) Schedule', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('International Business', 'INTBUS 151G', 'Business across Borders', 'Business on a global scale presents unique challenges and unrivalled opportunities to companies equipped to cross national boundaries. Set against a background of current events, the course explores the influence of international trade and multinational corporations on the contemporary global economy.', 'Restriction: BUSINESS 101, INTBUS 201, 202', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Italian', 'ITALIAN 100G', 'Introductory Italian Language', 'Learn basic Italian language structures and communication skills, including common words and basic phrases concerning everyday life. Acquire skills of interacting verbally by asking and answering straightforward questions on familiar topics. The course is delivered through a combination of class instruction and interactive online activities. For students with no previous knowledge of Italian.', 'Restriction: ITALIAN 106. May not be taken if an equivalent or more advanced language acquisition course in this subject has previously been passed.', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Italian', 'ITALIAN 106G', 'Italian Language for Beginners 1', 'Students learn to speak, read and write Italian, studying aspects of contemporary Italian society and thought. This course does not count towards a major in Italian. For students with no previous knowledge of Italian.', 'Restriction: May not be taken if a more advanced language acquisition course in this subject has previously been passed', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Japanese', 'JAPANESE 130G', 'Japanese Language 1A', 'An integrated basic course in modern Japanese covering reading, writing, speaking and listening.', 'Restriction: May not be taken if a more advanced language acquisition course in this subject has previously been passed', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Korean', 'KOREAN 110G', 'Korean for Beginners 1', 'Basic written and spoken skills in modern Korean. Through the practice of listening to and reading basic Korean sentences, fundamental grammar and vocabulary are taught so that students will be able to carry out basic conversation and comprehend simple Korean texts.', 'Restriction: KOREAN 100, 250. May not be taken if a more advanced language acquisition course in this subject has previously been passed', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Latin', 'LATIN 100G', 'Introduction to Latin Language 1', 'An introduction to the vocabulary and the grammar of simple sentences in Latin.\n</p>', 'null', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Law', 'LAW 121G', 'Law and Society', 'An introduction to theories of the nature, functions and origins of law and legal systems, including sources of law; comparative concepts of law; an overview of constitutional and legal arrangements in New Zealand, including the role of the courts; the operation of the legal system in historical and contemporary New Zealand with a focus on concepts of property rights, the Treaty of Waitangi, Treaty Settlements and proposals for constitutional change. Note: Does not meet the General Education requirement for LLB, LLB(Hons), LLB conjoints or LLB(Hons) conjoints.', 'Restriction: LAW 101', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Linguistics', 'LINGUIST 101G', 'Language, Mind and Society', 'A survey of three areas: the interaction between language structure and use on the one hand, and social structure and social norms on the other (sociolinguistics); the relationship between linguistic and cultural knowledge (anthropological linguistics); and the inter-relationship of language and other cognitive structures, especially as it is revealed through language acquisition (psycholinguistics).\n</p>', 'null', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Marine Science', 'MARINE 100G', 'The Oceans Around Us', 'An interdisciplinary approach to understanding the importance of our oceans as the driver of our climate, source of sustenance, and focus of domestic and international political, economic and legal negotiations. It is framed around physical and biological processes in the ocean which raise questions for ocean management in NZ and internationally, allowing real-world debate about the future of the ocean realm.\n</p>', 'null', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Marketing', 'MKTG 151G', 'Essential Marketing', 'Introduces fundamental marketing ideas and skillsets. Explores the world of customer value creation and marketing communications through the eyes of marketing and creative experts. Covers current topics in marketing including digital and social media, social entrepreneurship, big data analytics, green marketing and sustainability.\n</p>', 'null', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Mathematics', 'MATHS 190G', 'Great Ideas Shaping our World', 'Mathematics contains many powerful and beautiful ideas that have shaped the way we understand our world. This course explores some of the grand successes of mathematical thinking. No formal mathematics background is required, just curiosity about topics such as infinity, paradoxes, cryptography, knots and fractals.\n</p>', 'null', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Medical Science', 'MEDSCI 100G', 'Human Mind and Body Relationships', 'Humans share with other living things the features of physical self-generation and adaptation to the environment. Humans also live in a mental (mind) world and maintain relationships with our perceived environments. Minds and bodies mutually affect one another. This mind/body dance, which is explored in this course, is what gives rise to all of human behaviour from simple daily activities to the highest forms of creativity.\n</p>', 'null', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Medical Science', 'MEDSCI 101G', 'Environmental Threats to Human Health', 'Our environment sustains our lives but at times threatens our health. These threats may occur naturally, or arise from damage we have inflicted on the environment. This course considers health impacts of climate change, pollution, lifestyle choices, poverty and affluence, workplace hazards, emerging infectious diseases, and dangers affecting cancer risk.\n</p>', 'null', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Music', 'MUS 144G', 'Turning-points in Western Music', 'A study of significant people, major discoveries and inventions, and key factors (artistic, intellectual, social, technical) that were important agents of change in Western music. No previous knowledge of music is assumed.\n</p>', 'null', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Music', 'MUS 149G', 'Rock to Reggae: Tracking Popular Music in New Zealand', 'An introduction to New Zealand’s home-grown popular music, from the 1950s to the present day. A broad range of musical styles will be considered and situated within various social contexts. The issue of cultural identity in music – at national and local levels – will also be explored.\n</p>', 'null', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Mori Studies', 'MAORI 101G', 'Introduction to Written Mori', 'An introduction to listening, reading, writing and translation techniques used in the composition, reading and understanding of basic Mori. Designed for students with little or no knowledge of the language, and for those with some fluency wishing to understand simple sentence structure and composition.', 'Restriction: May not be taken if a more advanced language acquisition course in this subject has previously been passed', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Mori Studies', 'MAORI 130G', 'Te Ao Mori: The Mori World', 'An introduction to Mori analyses of topics that are often discussed and sometimes controversial, and that continue to shape contemporary life in New Zealand. Topics include aspects of world view, philosophy and social organisation; the Declaration of Independence, the Treaty of Waitangi and European immigration; and contemporary issues including Treaty claims, ownership of the foreshore and seabed and constitutional issues.\n</p>', 'null', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Optometry and Vision Science', 'OPTOM 101G', 'How We See', 'Overview of the interdisciplinary study of human vision. The course introduces the biological/physiological organisation of the visual system, discusses the subjective nature of perception, and the implications of studies of biological visual systems for machine vision. Interdisciplinary understandings of vision will be enriched by the examination of historical paintings and artists’ visual experiences.\n</p>', 'null', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Pacific Studies', 'PACIFIC 100G', 'Introduction to Pacific Studies', 'Introduces students to the discipline of Pacific Studies, framed by Pacific ways of knowing and doing and the expression and understanding of Pacific cultures. Topics covered include: health and wellbeing, ethnic and gender identities, spirituality, history, politics, sports and society, languages, performing arts, leadership and innovation and sustainability.\n</p>', 'null', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Pharmacy', 'PHARMACY 111G', 'Drugs and Society', 'The use of drugs in society including historical perspectives. Selected examples of the use of medicines in disease, recreational drug use and drug misuse, and cultural and ethnic influences on drug use. Differences between conventional and complementary medicines. The role of the pharmaceutical industry in drug discovery, manufacture and promotion. Legal and ethical issues pertaining to access to pharmaceuticals.\n</p>', 'null', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Philosophy', 'PHIL 105G', 'Critical Thinking', 'Dialogue, argument and discussion are analysed. Distinctions are drawn between persuasive, logically good and materially good arguments. The focus is on well reasoned persuasive dialogue, and mistakes in persuasive reasoning. Topics include the point of an argument, strength of arguments, fallacious reasoning, relevance of reasons, and burden of proof.\n</p>', 'null', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Physics', 'PHYSICS 107G', 'Planets, Stars and Galaxies', 'This course tells the story of our place in the Universe. Key topics are the exploration of the solar system, searches for planets around other stars, the structure and evolution of stars and galaxies, high-energy astrophysics, and the origin and overall properties of the Universe. No background in physics or mathematics is assumed.\n</p>', 'null', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Planning', 'PLANNING 100G', 'Creative Communities: An Introduction to Planning', 'The evolution of modern planning as an intellectual and professional movement.\n</p>', 'null', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Politics and International Relations', 'POLITICS 107G', 'New Zealand Politics', 'An introduction to understanding who governs New Zealand and in whose interests. Topics include national identity, institutions of government, leadership, voting and elections, the place of Mori within the political system, parties and political participation. The course draws on current research in NZ politics and provides knowledge that can be applied to a variety of careers, including law, business and public service.\n</p>', 'null', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Population Health', 'POPLHLTH 103G', 'Epidemics: Black Death to Bioterrorism', 'Epidemics have devastated human populations and will continue to do so. This course looks at how epidemics can run rampant through society and how we can control them. It will include examples from the past and present, as well as outline future threats. A diversity of epidemics will be covered, from the plague, gambling, depression, pandemics, nun-biting and alien abduction.\n</p>', 'null', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Psychology', 'PSYCH 109G', 'Mind, Brain and Behaviour', 'Topics covered may include: the nature of sensory and perceptual processes, the cause of perceptual illusions, the structure and function of the human brain, approaches to animal and human learning, models of human language and memory, and the design of psychological experiments. A laboratory component, in which students are required to participate as subjects, forms part of the course.\n</p>', 'null', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Russian', 'RUSSIAN 100G', 'Beginners\' Russian 1', 'A beginner\'s course using multi-media (computer) materials that presumes no prior knowledge of Russian, with emphasis on a range of language skills –listening comprehension, speaking, reading, writing, and the essential grammar of Russian.', 'Restriction: May not be taken if a more advanced language acquisition course in this subject has previously been passed', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Samoan', 'SAMOAN 101G', 'Samoan Language 1', 'Gives students an introduction to the structure of Samoan as well as allowing them to develop basic language skills in listening, speaking, reading and writing. Designed for students with little or no knowledge of the language, and for those with some fluency wishing to understand simple sentence structure and composition.', 'Restriction: May not be taken if a more advanced language acquisition course in this subject has previously been passed', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Science General', 'SCIGEN 101G', 'Communicating in a Knowledge Society', 'Effective communication is required for specialists in all fields to engage meaningfully with society. In this course students gain an understanding of the important role communication plays in a knowledge society. Through case studies and practical experience students learn about the responsibilities and skills required to communicate with a variety of audiences. They learn how to effectively manage and present data and practice oral, written, visual and electronic communication.\n</p>', 'null', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Science General', 'SCIGEN 201G', 'Innovating in a Knowledge Society', 'Interdisciplinary examination of science innovation at policy, organisational and project levels including context, impacts and roles of business and research organisations, and ways innovations are presented and received. Case study analysis of the business environment including how innovation is both enabled and constrained in science-based organisations and society, and innovation strategies in science–based organisations.\n</p>', 'null', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Science General', 'SCIGEN 301G', 'Engaging in a Knowledge Society', 'Addressing complex issues requires knowledge experts to engage with a variety of people. Solutions will be gained from collaborations that co-produce knowledge in transdisciplinary partnerships that lead to new ways of thinking. This course explores meaningful ways to engage with communities, and reassesses current ways of knowing and doing.\n</p>', 'null', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Sociology', 'SOCIOL 101G', 'Understanding Aotearoa New Zealand', 'Provides an introduction to the sociological analysis of New Zealand society. Looks at familiar events, institutions, social processes from a sociological point of view and offers ways to understand them in new and different ways. Focuses on the structure of New Zealand society and on social and political changes which affect the lives of New Zealanders and shape their society.\n</p>', 'null', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Spanish', 'SPANISH 104G', 'Beginners\' Spanish 1', 'Provides a solid grounding in the basic grammar and vocabulary of Spanish for beginners or near beginners, emphasising communicative competence in the present tense. Develops speaking, listening, reading and writing skills, and prepares students at the A1 Level of the Common European Framework of Reference for Languages.', 'Restriction: SPANISH 107. May not be taken if a more advanced language acquisition course in this subject has previously been passed', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Statistics', 'STATS 101G', 'Introduction to Statistics', 'Intended for anyone who will ever have to collect or make sense of data, either in their career or private life. Steps involved in conducting a statistical investigation are studied with the main emphasis being on data analysis and the background concepts necessary for successfully analysing data, extrapolating from patterns in data to more generally applicable conclusions and communicating results to others. Other topics include probability; confidence intervals, statistical significance, t-tests, and p-values; nonparametric methods; one-way analysis of variance, simple linear regression, correlation, tables of counts and the chi-square test.', 'Restriction: STATS 102, 107, 108, 191', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Statistics', 'STATS 150G', 'Lies, Damned Lies, and Statistics', 'Examines the uses, limitations and abuses of statistical information in a variety of activities such as polling, public health, sport, law, marketing and the environment. The statistical concepts and thinking underlying data-based arguments will be explored. Emphasises the interpretation and critical evaluation of statistically based reports as well as the construction of statistically sound arguments and reports. Some course material will be drawn from topics currently in the news.\n</p>', 'null', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Sustainability', 'SUSTAIN 100G', 'Sustainability and Us', 'What is sustainability? Discussion of what ‘sustainability’ means, its underpinning values, and the role individuals and organisations play in creating solutions. Students work in groups to investigate a sustainability issue on campus and design a solution, which they present via a film, poster, presentation or model. Examines sustainability issues – water and overpopulation.\n</p>', 'null', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Theological and Religious Studies', 'THEOREL 101G', 'The Bible and Popular Culture', 'An exploration of biblical themes, images, and metaphors in contemporary film, music and cultural arts through which religion and culture intersect. It develops tools appropriate for analysing popular culture, as it moves from the local to the national to the global.', 'Restriction: THEOLOGY 101, 101G', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Theological and Religious Studies', 'THEOREL 106G', 'Islam and the Contemporary World', 'Since the religion of Islam has become a very significant aspect of contemporary global and local societies, this course seeks to introduce students to an understanding of key aspects of Islam and an analysis of its significant contribution to New Zealand society as well as to societies and cultures across the world.', 'Restriction: THEOLOGY 106, 106G', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Tongan', 'TONGAN 101G', 'Tongan Language 1', 'Gives students an introduction to the structure of Tongan as well as allowing them to develop basic language skills in listening, speaking, reading and writing. Designed for students with little or no knowledge of the language, and for those with some fluency wishing to understand simple sentence structure and composition.', 'Restriction: May not be taken if a more advanced language acquisition course in this subject has previously been passed', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Translation Studies', 'TRANSLAT 100G', 'Translation for Global Citizens', 'Covers the foundations of translation and interpreting as an academic discipline and as a critically important communication enabler which serves a multicultural and multilingual society. The course is designed to equip monolingual students, as well as students with language skills, with the literacy in translation and interpreting increasingly needed to navigate today’s globalised world and to detect and overcome communication gaps in diverse business and private contexts.\n</p>', 'null', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Urban Planning', 'URBPLAN 101G', 'Introduction to Urban Planning', 'An introduction to the city, urban planning and sustainability; professional roles, practices and ethics.', 'Restriction: PLANNING 100G', 'null', 'null');
INSERT INTO `generaleducation` VALUES ('Youth Work', 'YOUTHWRK 152G', 'Understanding New Zealand Youth', 'Examines the concept of \'youth\' and the historical, economic and political contexts in which young people live and are schooled in New Zealand society. The concept of youth is explored as a fundamental aspect of human development, identity and culture. The ways that we learn about what it is to be a young person in New Zealand today, including sport, body image, media, music, technology and social networking will be explored.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Academic Integrity', 'ACADINT A01', 'Academic Integrity Course', 'The Academic Integrity Course is an online course designed to increase student knowledge of academic integrity, university rules relating to academic conduct, and the identification and consequences of academic misconduct. Students work through a series of modules, outlining scenarios that they may encounter while studying at university. Each scenario provides information on relevant rules, resources and expected behaviour.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Commercial Law', 'COMLAW 740A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Commercial Law', 'COMLAW 740B', 'The Tax Base - Level 9', 'An advanced study of the breadth of the New Zealand income tax base, including the different concepts of income, its timing and recognition. Comparisons between the nature of capital and income, and the differing treatment of each, provides a deeper understanding of the policy behind the New Zealand income tax regime. Provides a theoretical background and detailed technical knowledge of the scope and application of the most significant regimes for income, deduction and timing in the Income Tax Act 2007. Involves individual research resulting in a substantial individual research essay.', 'To complete this course students must enrol in COMLAW 740 A and B', 'null', 'null');
INSERT INTO `law` VALUES ('Commercial Law', 'COMLAW 747', 'Goods and Services Tax', 'An advanced study of Goods and Services Tax. Provides both a theoretical background and high level of technical knowledge of the GST Act 1985. Comparisons with other indirect taxes and overseas variations of GST (notably Australian GST and UK VAT) provide a deeper understanding of the policy behind the New Zealand GST regime. Major topics include taxable activities, input tax, output tax, registration, adjustments, taxable supplies, timing and the GST anti-avoidance provisions.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Commercial Law', 'COMLAW 748', 'Tax Disputes', 'An advanced study of the statutory disputes and challenge procedures in the Tax Administration Act 1994. Covers the power of the Commissioner to propose adjustments, conduct investigations and raise assessments. Reviews the administrative law obligations imposed on the Commissioner, taxpayer rights and the power of the Courts to supervise and review the assessment process.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Commercial Law', 'COMLAW 757', 'Special Topic in Taxation Law', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Commercial Law', 'COMLAW 789', 'Research Essay in Taxation Law', '(For the MTaxS.)\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law', 'LAW 121G', 'Law and Society', 'An introduction to theories of the nature, functions and origins of law and legal systems, including sources of law; comparative concepts of law; an overview of constitutional and legal arrangements in New Zealand, including the role of the courts; the operation of the legal system in historical and contemporary New Zealand with a focus on concepts of property rights, the Treaty of Waitangi, Treaty Settlements and proposals for constitutional change. Note: Does not meet the General Education requirement for LLB, LLB(Hons), LLB conjoints or LLB(Hons) conjoints.', 'Restriction: LAW 101', 'null', 'null');
INSERT INTO `law` VALUES ('Law', 'LAW 131', 'Legal Method', 'An introductory study of how law is made and applied in New Zealand – an overview of the law-making roles of the legislative, executive and judicial branches of government; other influences on the development of the law; an introduction to case law, including judicial reasoning and the doctrine of precedent; an introduction to statute law, including the legislative process and techniques of statutory interpretation and application; the interaction between case law and legislation.', 'Prerequisite: B- or higher in LAW 121 or 121G', 'null', 'null');
INSERT INTO `law` VALUES ('Law', 'LAW 141', 'Legal Foundations', 'An overview of the classification, sources and operation of core aspects of New Zealand law, including state law and tikanga Mori. An introduction to other sources, perspectives and explanations of law.', 'Prerequisite: B- or higher in LAW 121 or 121G', 'null', 'null');
INSERT INTO `law` VALUES ('Law', 'LAW 201A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law', 'LAW 201B', 'Criminal Law', 'An introduction to the principles and practice of New Zealand criminal law; an analysis of doctrines of liability including defences to criminal charges; a study in detail of selected indictable and summary offences; and a critical survey of the chief elements of procedure relating to offences chargeable indictably or summarily. (Apart from the rules concerning burden of proof, no detailed study is made in this course of the law of evidence.)', 'Corequisite: LAW 298 or 299', 'null', 'null');
INSERT INTO `law` VALUES ('Law', 'LAW 211A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law', 'LAW 211B', 'Public Law', 'The principles and workings of the New Zealand constitution; the powers, privileges and immunities of the three branches of government; the exercise and control of public power; and the relationship between the individual and the State (including the position of Mori under the Treaty of Waitangi).', 'Corequisite: LAW 298 or 299', 'null', 'null');
INSERT INTO `law` VALUES ('Law', 'LAW 231A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law', 'LAW 231B', 'Law of Torts', 'The general principles of civil liability for non-consensual wrongs. The principles of liability applying to selected torts, including the intentional torts such as: assault, battery, false imprisonment, intentionally inflicting emotional distress, trespass to land, wrongs to goods, negligence, strict liability, nuisance and defamation. The law relating to compensation for personal injury.', 'Corequisite: LAW 298 or 299', 'null', 'null');
INSERT INTO `law` VALUES ('Law', 'LAW 241A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law', 'LAW 241B', 'Law of Contract', 'The general principles of contract law including: the formation of contracts at common law, New Zealand contract legislation, breach of contract, and remedies for breach of contract. An introduction to the general principles of agency.', 'Corequisite: LAW 298 or 299', 'null', 'null');
INSERT INTO `law` VALUES ('Law', 'LAW 298A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law', 'LAW 298B', 'Legal Research, Writing and Communication', 'Legal research, writing, mooting and other requirements, as determined by the Dean of Faculty of Law.', 'Restriction: LAW 299', 'null', 'null');
INSERT INTO `law` VALUES ('Law', 'LAW 301A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law', 'LAW 301B', 'Land Law', 'A study of the history and principles of land law including: estates and interests in land, the effect of registration and indefeasibility of title, leasehold estates, easements and profits, mortgages, and concurrent interests in land, and covenants affecting freehold land.', 'Prerequisite: LAW 201, 211, 231, 241', 'null', 'null');
INSERT INTO `law` VALUES ('Law', 'LAW 306A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law', 'LAW 306B', 'Equity', 'A study of the central principles and remedies of equity including: the fiduciary principle, relationships of confidence, unconscionable conduct, undue influence, estoppel, assignments, trusts (express, resulting and constructive), charities, tracing, third-party liability, the assignment in equity of choses in action, and priorities. Basic principles of the law of succession and of the administration of estates.', 'Prerequisite: LAW 201, 211, 231, 241', 'null', 'null');
INSERT INTO `law` VALUES ('Law', 'LAW 316', 'Jurisprudence', 'A study of the nature of law, including the nature of legal reasoning, its sources, its methodology, the extent to which legal questions are indeterminate, fundamental legal concepts, and the structure of a legal system; ng tikanga Mori and its relation to wider Mori views of the world and contemporary issues faced by Mori in their relation to contemporary law.', 'Prerequisite: LAW 201, 211, 231, 241', 'null', 'null');
INSERT INTO `law` VALUES ('Law', 'LAW 399', 'Legal Research 2', 'An introduction to multi-jurisdictional legal information sources and advanced research skills.', 'Prerequisite: LAW 201, 211, 231, 241, 298 or 299', 'null', 'null');
INSERT INTO `law` VALUES ('Law', 'LAW 400', 'Legal Research 3', 'Completion of legal research requirements as approved by the Faculty of Law, including moot participation and opinion writing.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law', 'LAW 456', 'Supervised Research', 'A research paper, approved by the Dean of Faculty of Law, written under the supervision of a teacher in the Faculty of Law.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law', 'LAW 458', 'Legal Ethics', 'A study of legal ethics and professional responsibility including: an introduction to ethical analysis which examines various theories of ethics; the applicability of ethical analysis to legal practice; the concept of a profession and the ethical and professional duties of practitioners (which will include, amongst other topics, conflicts of interest, confidentiality, duties to the court, duties of loyalty and fidelity); the wider responsibilities of lawyers in the community.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law', 'LAW 498', 'Advanced Legal Research, Writing and Communication', 'Satisfactory completion of such advanced legal research, writing, communication and other requirements as determined by the Dean of Faculty of Law.', 'Prerequisite: LAW 201, 211, 231, 241', 'null', 'null');
INSERT INTO `law` VALUES ('Law', 'LAW 499', 'Legal Practice', 'Such work and practical experience in the detailed application of the law and in relation to the provision of legal services as approved by the Faculty of Law.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law', 'LAW 700', 'Legal Research Methodology and Advanced Writing - Level 9', 'Multi-jurisdictional legal information sources and advanced legal research, research problem formulation and refinement, legal and social science research methodologies, research ethics and evaluative research trail. Legal writing for different purposes and different audiences.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law', 'LAW 701', 'The Legal System: Sources, Structure and Method - Level 9', 'Examination of the core substantive components of the New Zealand legal system, in comparison with other municipal legal systems and international law. Analysis of the sources of New Zealand law, including statute, case law and custom, and the influence of international law. Legal methodology in theory and practice, including: judicial reasoning and the doctrine of precedent, techniques of statutory interpretation, and the resolution of disputes. Different modes of legal analysis and approaches to legal theory.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law', 'LAW 760', 'Directed Study - Level 9', 'Supervised research paper on an advanced legal topic, approved by the Dean of Faculty of Law.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law', 'LAW 789', 'Dissertation - Level 9', 'A dissertation of approximately 15,000 words resulting from original research of the student, having the scope, and depth of research, of a competent law review article. The topic of the dissertation needs the approval of the Dean of Faculty of Law.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law', 'LAW 790', 'Dissertation - Level 9', 'A dissertation of approximately 15,000 words resulting from original research of the student, having the scope, and depth of research, of a competent law review article.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law', 'LAW 792', 'Dissertation - Level 9', '', 'Restriction: COMLAW 792', 'null', 'null');
INSERT INTO `law` VALUES ('Law', 'LAW 794A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law', 'LAW 794B', 'Research Portfolio 1 - Level 9', 'Supervised research comprising a portfolio of research work within an area of specialisation culminating in a linking paper that together creates a coherent body of scholarly work.', 'To complete this course students must enrol in LAW 794 A and B', 'null', 'null');
INSERT INTO `law` VALUES ('Law', 'LAW 796A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law', 'LAW 796B', 'Thesis 1 - Level 9', 'A thesis of approximately 30,000 words resulting from original research of the student, displaying at a minimum: thorough research, a competent advanced understanding of the topic studied, and an ability to present the student\'s understanding of that topic in an orderly way.', 'To complete this course students must enrol in LAW 796 A and B', 'null', 'null');
INSERT INTO `law` VALUES ('Law', 'LAW 797A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law', 'LAW 797B', 'Thesis 2 - Level 9', 'A thesis of approximately 40,000 words resulting from original research of the student, displaying comprehensive understanding of the topic studied and an ability to contribute to the better understanding of that topic.', 'To complete this course students must enrol in LAW 797 A and B', 'null', 'null');
INSERT INTO `law` VALUES ('Law', 'LAW 798A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law', 'LAW 798B', 'Research Portfolio 2 - Level 9', 'Supervised research comprising a portfolio of research work within an area of specialisation culminating in a linking paper that together creates a coherent body of scholarly work.', 'To complete this course students must enrol in LAW 798 A and B', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 401', 'Commercial Law', 'An introduction to selected areas of business law, in particular relating to the sale of goods and personal property securities but extending to aspects of consumer laws, guarantees and the impact of new business methods.', 'Prerequisite: LAW 201, 211, 231, 241', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 402', 'Company Law', 'The law relating to companies incorporated under the Companies Act 1993 including: the nature of corporate personality, pre-incorporation contracts, the rights and liabilities of promoters, an introduction to the raising of debt and equity capital and the regulation of the securities market, the rights of shareholders, and the duties of directors.', 'Prerequisite: LAW 201, 211, 231, 241', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 403', 'Tax Law', 'A general introduction to tax law including: aspects of tax policy; the structure of the tax system; residence; source; the meaning of income; the deductibility of expenditure; the distinction between capital and revenue; depreciation; avoidance; disputes and rulings; GST.', 'Prerequisite: LAW 211, 241', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 405', 'Restitution', 'A study of the general principles of the law of restitution, including an analysis of the concept of unjust enrichment, selected applications of restitutionary principle for the recovery of value upon a flawed or conditioned transfer, recovery outside contract for labour expended on another\'s behalf, and stripping wrong-doers of profits.', 'Prerequisite: LAW 306', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 406', 'International Sales and Finance', 'Study of the law relating to international trade and transnational business transactions, including international sales contracts and international trade finance, and conflict of laws issues arising out of international trade.', 'Prerequisite: LAW 211, 231, 241', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 407', 'Conflict of Laws', 'An introduction to private international law (i.e., the body of law dealing with international civil or commercial issues or disputes that are not governed by substantive conventions) including: a study of the jurisdiction of the New Zealand courts, recognition and enforcement of foreign judgments and decrees, and choice of the governing legal system.', 'Prerequisite: LAW 211, 231, 241', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 408', 'Special Topic', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 409', 'Special Topic', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 420', 'Advanced Tax Law', 'A more advanced study of tax law covering topics such as tax history; tax theory; the taxation of companies; dividends; imputation; groups; losses; qualifying companies; trusts; withholding obligations; accruals; avoidance; international tax; profit reduction techniques; transfer pricing; controlled foreign corporations (CFCs); foreign investment funds (FIFs); tax treaties.', 'Prerequisite: LAWCOMM 403', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 421', 'Commercial Arbitration', 'The law and procedure relating to the settlement of domestic and international commercial disputes by arbitration, including a study of key arbitration principles, governing law issues, appointment and duties of arbitrators, the conduct of proceedings, enforcement and judicial review of awards, and international investment arbitration.', 'Prerequisite: LAW 211, 231, 241', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 422', 'Competition Law', 'A study of the principles of competition law in New Zealand including the effect on competition law of the CER Agreement with Australia. Comparison with the competition laws of other countries including the United States, the European Union and Australia.', 'Prerequisite: LAW 241 or COMLAW 201 and 203', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 423', 'Company Liquidations', 'Examination of the legal process by which companies are placed in liquidation including: the law on corporate insolvency, and the procedures and the enforcement mechanisms used to give effect to the law; current law and new approaches to insolvency.', 'Prerequisite: LAW 241', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 424', 'Insurance Law', 'A consideration of the law governing insurance contracts, including the duty of utmost good faith; the interpretation of the policy; the scope of cover; warranties and conditions; the claims process and fraudulent claims; and quantification of the insurer’s obligations; subrogation and recoupment; and third party rights.', 'Prerequisite: LAW 231, 241', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 425', 'International Trade', 'Study of the law relating to international trade and transnational business transactions; contracts of carriage; the law and policy surrounding the regulation of the international transportation of goods by sea, land and air.', 'Prerequisite: LAW 211, 231, 241', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 426', 'Law and Information Technology', 'An introduction to the use of information systems in legal practice and research and the impact of the law on information technology including: computer crimes, torts, intellectual property, evidence, privacy, and the assistance given to lawyers by information retrieval, office management and litigation support systems.', 'Prerequisite: LAW 201, 231', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 427', 'Vendor and Purchaser', 'A study of the law relating to contracts for the sale and purchase of land, including the formation of the contract, the application of relevant statutes, the basic terms of such contracts and their significance, matters of title, settlement and completion, and remedies for breach.', 'Corequisite: LAW 301', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 428', 'Maritime Law', 'An introduction to shipping law, including: ownership and proprietary interests in ships, ship registration, charter parties, ship mortgages, shipboard crimes and torts, the law of collisions, salvage and wrecks, the admiralty jurisdiction, enforcement of maritime liens and other maritime claims, national shipping law and policy, the international regulatory framework, and conflict of laws issues applicable to maritime disputes.', 'Prerequisite: LAW 211, 231, 241', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 429', 'Advanced Tort', 'Explores in depth issues touched on in LAW 231 and introduces new causes of action. Topics will include some or all of the following: the recovery of economic loss in negligence, negligent misrepresentation, vicarious liability, factual causation, nonfeasance, the economic torts, the effect of statutes and contracts on the law of tort, and damages.', 'Prerequisite: LAW 231', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 432', 'Corporate Transactions', 'A study of corporate transactions in operation, with particular reference to the legal and practical processes involved in the formation and implementation of such major business transactions as are typical in the life cycle of a business. The particular transactions focused upon may vary from year to year.', 'Prerequisite: LAW 241', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 433', 'Advanced Intellectual Property Law', 'Studies in selected topics in intellectual property law including international developments and comparative aspects.', 'Corequisite: LAW 432 or LAWCOMM 404', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 434', 'Advanced Contract', 'Advanced studies in selected areas of Contract Law.', 'Prerequisite: LAW 241', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 435', 'European Commercial Litigation', 'A study of jurisdiction, choice of law and enforcement of judgments in cross-border commercial litigation within the European Union; consideration of specific EU Regulations, such as Brussels I, Rome I and Rome II.', 'Prerequisite: LAW 241', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 436', 'Advanced Company Law', 'Study in selected areas of company law including theories of the company, companies viewed from a law and economics perspective, the relationship between the board and shareholders, the place of corporate social responsibility, comparative corporate law and current issues in company law.', 'Prerequisite: LAWCOMM 402', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 437', 'Iwi Corporate Governance', 'An examination of the common governance structures employed by iwi, why those structures are chosen and the legal and practical issues that arise as a result. Aspects of the law related to trusts, limited partnerships, charities and Mori Authorities, and how they may be interwoven within one overarching structure.', 'Prerequisite: LAW 211, 241', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 440', 'Guarantees and Indemnities', 'An introduction to the law regarding guarantees and indemnities in New Zealand. The course will mainly focus on guarantees, although indemnities will also be covered.', 'Prerequisite: LAW 241', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 441', 'Creditors\' Remedies', 'Examination of the diverse and seemingly unrelated areas of the law as they concern the rights of unsecured judgement creditors.', 'Prerequisite: LAW 241', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 442', 'Law of Personal Property', 'Introduction to the concepts and legal rights associated with personal property, covering: possessory rights and relationships, including bailment, reservation of title and security interests in goods, and principles relevant to the transfer and acquisition of personal property.', 'Prerequisite: LAW 231', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 444', 'Financial Markets Law', 'An examination of the law regulating the promotion of companies, duties and liabilities of directors and promoters for the promotion of a company, public fund raising in New Zealand, insider trading laws and takeovers, and limited liability partnerships.', 'Corequisite: LAW 417 or LAWCOMM 402', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 445', 'Takeovers', 'A consideration of the role of takeovers in the economy and the manner in which they are regulated. The principal focuses will be upon the Takeovers Code and upon the workings of the Takeover Panel.', 'Corequisite: LAW 417 or LAWCOMM 402', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 446', 'Special Topic: Aspects of Iwi Corporate Governance', '', 'Prerequisite: LAW 211, 241', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 449', 'Selected Aspects of Intellectual Property Law', 'The law relating to patents including what is an \'invention\', problem areas of subject matter, practice and procedural aspects, validity and infringement. Registered design law; trade secrets; technology transfer licensing. International comparative IP law.', 'Corequisite: LAW 432 or LAWCOMM 404', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 450', 'International Tax Law', 'The globalisation of business presents a significant challenge to governments and revenue authorities and opportunities for multinational businesses to be able to locate productive activities, risks, and importantly, profits to any jurisdiction that they wish. This course looks at cross border taxation with emphasis on double tax treaties.', 'Prerequisite: LAW 211, 241', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 451', 'Construction Law', 'Examines construction law, drawing on concepts and principles in contract and tort. The relevant statutory and regulatory framework. Legal issues arising during the life-cycle of a construction or infrastructure project at three stages: foundational concepts; the project; post-project claims and latent defects.', 'Prerequisite: LAW 231, 241', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 452', 'Commercial and Consumer Law', 'A study of the law relating to commercial and consumer law including: the general regime on the sale of goods and its contrast to the Consumer Guarantees Act 1993, selected aspects of carriage of goods and of agency, and the basic disciplines of the Fair Trading Act 1986.', 'Prerequisite: LAW 201, 211, 231, 241', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 453', 'Aspects of Insurance Law', 'Covers aspects of the law governing insurance contracts, including the duty of utmost good faith; the interpretation of the policy; the scope of cover; warranties and conditions; the claims process; and quantification of the insurer’s obligation.', 'Prerequisite: LAW 231, 241', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 454', 'Trade Mark Law', 'An in-depth examination of the law related to the protection of trade marks, including the history of, and justifications for, trade mark protection, the operation of the registration system, the commercial exploitation of trade marks, infringement of registered trade marks, and the protection of unregistered trade marks.', 'Prerequisite: LAWCOMM 404', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 455', 'Theories of Contract Law', 'Survey and analysis of the main schools of thought and positions in contemporary contract law theory, including: promise theory, transfer theory, economic analysis, communitarian theories and distributive justice theories. Examination of key questions in contract law, such as formation, interpretation and remedies in light of said theories.', 'Prerequisite: LAW 231, 241', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 456', 'Secured Credit', 'A detailed study of the law relating to securities over personal property, and related aspects of credit contracts.', 'Prerequisite: LAW 201, 211, 231, 241', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 457', 'Consumer Law', 'An in-depth examination of selected aspects of consumer law, including (but not limited to) misleading and deceptive conduct, other unfair practices, unfair contract terms in standard form consumer contracts, consumer guarantees and uninvited direct sales.', 'Prerequisite: LAW 201, 211, 241', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 458', 'Intellectual Property', 'Study of the laws which protect the products of intellectual endeavour including: passing-off (and section 9 Fair Trading Act), the Trade Marks Act, the Copyright Act, the action for \'breach of confidence\', the Designs Act and the Patents Act.', 'Prerequisite: LAW 231', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 459', 'Special Topic', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 460', 'Special Topic', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 461', 'Special Topic: Corporate Insolvency', 'A study of New Zealand corporate insolvency law, focusing on the commencement of the liquidation and administration process, the inter-relationship of those processes and liquidators’ and administrators’ powers and liabilities. Topics covered: reckless trading and recovery from directors, the disclaimer of onerous contracts and voidable transactions. Comparative material will be drawn from Australian, Canadian and United Kingdom jurisprudence.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 462', 'Special Topic', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 700', 'Special Topic: Foundations of Tax Law - Level 9', 'Provides a theoretical background and detailed technical knowledge of the scope and application of the most significant regimes for income, deduction and timing in the Income Tax Act 2007. Involves individual research resulting in a substantial individual research essay.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 702', 'International Arbitration - Level 9', 'A comparative study of negotiation, litigation, arbitration and mediation in commercial contexts; New Zealand law relating to arbitration, international arbitration; and the operating and utility of mediation in commercial contexts. Involves individual research resulting in a substantial individual research essay.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 705', 'Commercial Leases - Level 9', 'An examination and analysis of the obligations, rights and powers of parties in modern commercial leases, including problems arising from assignments, subleases, mortgages of leases, enforcement of obligations and remedies. Involves individual research resulting in a substantial individual research essay.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 706', 'Competition Law and Policy - Level 9', 'Advanced studies in competition law and its application to international transactions and commercial relationships, with comparative study of New Zealand law with that of other countries. Involves individual research resulting in a substantial individual research essay.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 707', 'Conflict of Laws - Level 9', 'An advanced study of private international law, including a study of the jurisdiction of the New Zealand courts and arbitrators, the recognition and enforcement of foreign judgments and decrees and arbitral awards, and choice of the governing legal system.', 'Restriction: LAW 712', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 709', 'Corporate Governance - Level 9', 'The principles of the law as to corporations with special reference to companies, directors’ duties and the status and rights of shareholders. Involves individual research resulting in a substantial individual research essay.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 710', 'Dispute Resolution - Level 9', 'The history and current practice of dispute resolution outside the courtroom, dispute settlement theories, and the principles of negotiation and mediation in the context of family, commercial, environmental, international and urban community disputes.', 'Restriction: LAW 717', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 711', 'Commercial Equity - Level 9', 'A detailed study of the history and principles of equity as they impact on modern society, including: the fiduciary obligation, the nature of equitable estates and interests, equitable priorities, estoppel, oppression and unconscionable dealing, specific equitable doctrines (such as contribution and subrogation), modern uses of the trust and equitable remedies. Involves individual research resulting in a substantial individual research essay.', 'Restriction: LAW 718', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 712', 'Insolvency Law - Level 9', 'Legal problems arising where a debtor is in financial difficulties including: study of the rights of creditors in bankruptcy and company liquidation, corporate failure and re-organisation, and insolvency law reform in this and other jurisdictions. Involves individual research resulting in a substantial individual research essay.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 713', 'Intellectual Property - Level 9', 'Aspects of the law protecting the products of intellectual endeavour selected from: the law of trade marks and passing off, the law of copyright, patent law and the law of breach of confidence. Involves individual research resulting in a substantial individual research essay.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 714', 'International Litigation and Arbitration - Level 9', 'The institutional and procedural framework applicable to the resolution of international civil or commercial disputes; and strategic planning in multi-jurisdictional litigation.', 'Restriction: LAW 731', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 715', 'International Sales - Level 9', 'Advanced study of selected topics in international trade law including: import and export of goods by sea and air, treaties affecting New Zealand’s foreign trade, and transnational aspects of doing business abroad. Involves individual research resulting in a substantial individual research essay.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 717', 'Law of Agency - Level 9', 'An advanced study of the principles of agency law, and selected applications thereof. Involves individual research resulting in a substantial individual research essay.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 720', 'Law of Insurance Contracts - Level 9', 'The principles and operation of the law relating to insurance. Involves individual research resulting in a substantial individual research essay.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 723', 'Maritime Law - Level 9', 'Advanced studies in shipping law, including: ownership and proprietary interests in ships, ship registration, charter parties, ship mortgages, shipboard crimes and torts, the law of collisions, salvage and wrecks, the admiralty jurisdiction, enforcement of maritime liens and other maritime claims, national shipping law and policy, the international regulatory framework, and conflict of laws issues applicable to maritime disputes. Involves individual research resulting in a substantial individual research essay.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 724', 'Mergers and Acquisitions - Level 9', 'Advanced study in the law relating to business and corporate acquisitions and corporate mergers, takeovers and amalgamations, including issues affecting choice of method, directors’ duties, and public and private regulation. Involves individual research resulting in a substantial individual research essay.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 726', 'Restitution in Commercial Contexts - Level 9', 'Advanced problems focusing on situations and available remedies where benefits have been wrongfully or unfairly acquired. Involves individual research resulting in a substantial individual research essay.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 727', 'Selected Issues in Property Law - Level 9', 'The history and principles of the law of property; contemporary issues.', 'Restriction: LAW 734', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 730', 'Special Topic: International Trade Law - Level 9', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 731', 'Special Topic: International Banking Law - Level 9', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 732', 'Special Topic: Securities Regulation - Level 9', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 733', 'Special Topic: Comparative Corporate Governance - Level 9', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 734', 'Comparative Free Trade Agreements - Level 9', 'The relationship between the multilateral and bilateral trade agreements; the dynamics and models promoted by the US, EU and South-South agreements; the web of existing and prospective agreements involving the New Zealand government; and the particular issues and challenges relating to the trans-Pacific Partnership Agreement. Involves individual research resulting in a substantial individual research essay.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 735', 'Special Topic: Artificial Intelligence: Law and Policy - Level 9', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 736', 'Special Topic: Comparative Company Law - Level 9', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 737', 'Special Topic: Future Directions of the World Trade Organization - Level 9', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 738', 'Special Topic: Twenty-first Century Trade Agenda - Level 9', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 739', 'Special Topic: Mergers and Acquisitions - Level 9', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 740', 'Special Topic: Corporate Governance - Level 9', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 741', 'Secured Transactions - Level 9', 'Technical and practical aspects of the law of secured transactions. Involves individual research resulting in a substantial individual research essay.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 742', 'Remedies Law - Level 9', 'Advanced study of selected aspects of civil remedies for breach of civil obligations including those arising at common law and under statute, and discretionary relief in equity, and the assessment of damages and compensation. Involves individual research resulting in a substantial individual research essay.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 744', 'Selected Topics in Taxation - Level 9', 'Selected topics in taxation including: works of some of the great tax theorists and their relevance to modern tax policy formulation; current debates on questions of tax policy; aspects of international taxation; aspects of international tax planning. These are examined by reference to the tax systems of a variety of jurisdictions (such as New Zealand, the UK, the USA, Hong Kong and China). Involves individual research resulting in a substantial individual research essay.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 745', 'Public Law in Commercial Contexts - Level 9', 'Selected topics in the application of judicial review and other parts of public law in commercial contexts. Involves individual research resulting in a substantial individual research essay.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 746', 'Data Privacy and the Law - Level 9', 'A comparative study of evolving global and New Zealand standards governing data privacy, the challenges they face from technological developments and the implications for business, government.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 747', 'Special Topic: Conflict of Laws - Level 9', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 748', 'Special Topic: Advanced Torts - Level 9', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 749', 'Special Topic: International Litigation - Level 9', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 754', 'Copyright Law - Level 9', 'Detailed study of the law of copyright. Involves individual research resulting in a substantial individual research essay.', 'Restriction: LAWCOMM 713', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 755', 'Corporate Finance - Level 9', 'Detailed study of the law relating to corporate finance. Involves individual research resulting in a substantial individual research essay.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 758', 'Franchising Law - Level 9', 'A study of the law relating to franchising. Involves individual research resulting in a substantial individual research essay.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 763', 'Sports Law - Level 9', 'A detailed study of legal issues relating to sport. Involves individual research resulting in a substantial individual research essay.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 767', 'Special Topic: Consumer Law - Level 9', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 768', 'Special Topic: Economic Analysis of the Law - Level 9', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 769', 'Special Topic: Economic Regulation: Principles and Practice - Level 9', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 770', 'Private International Law - Level 9', 'The theory and practice of transnational jurisdiction, choice of law and recognition and enforcement of foreign judgments. Involves individual research resulting in a substantial individual research essay.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 771', 'Cross-Border Commercial Litigation - Level 9', 'Entails an in depth comparative study of the most important cross-border commercial litigation regimes, including the typical Anglo-common law regime, the European Union regime and the trans-Tasman regime. Involves individual research resulting in a substantial individual research essay.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 772', 'Intellectual Property - Level 9', 'Designed to provide an analysis of the important intellectual property laws in New Zealand covering trademarks, copyright, confidential information and, quite briefly, patents and designs. Involves individual research resulting in a substantial individual research essay.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 773', 'Corporate Governance in New Zealand - Level 9', 'The governance of companies in New Zealand, with a focus on the role of directors and the board. Topics include corporate theory, legal characteristics of the company and internal governance. Involves individual research resulting in a substantial individual research essay.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 774', 'Comparative Corporate Governance - Level 9', 'A comparison of corporate governance regimes across the world. Current issues in corporate governance are examined – topics may include directors’ remuneration, corporate scandals and responses to corporate scandals. Discussion of convergence of corporate governance regimes is included. Involves individual research resulting in a substantial individual research essay.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 775A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 775B', 'International Taxation', 'Tests of residence for individuals and corporations. The wider tax base for off-shore income of New Zealand residents. Income derived by overseas residents from New Zealand activities. The Double Tax Treaty System. Selecting the country of residence. Anti-avoidance measures directed at transnational activity. The use of tax havens. As well as New Zealand taxation law, the course also examines the municipal revenue law of some of our trading partners. Involves individual research resulting in a substantial individual research essay.', 'Restriction: COMLAW 741', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 777', '', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 777A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 777B', 'Special Topic', '', 'Restriction: COMLAW 755', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 778', 'Special Topic: Selected Topics in Tort Law - Level 9', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 779', 'Special Topic - Level 9', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 780', 'Corporation and Investor Taxation', 'An advanced study of the tax liability and issues affecting companies and their shareholders. Considers the different corporate tax regimes, including dividends, imputations, losses and groupings, amalgamations, LTCs and Unit Trusts. Comparison with other entities is intended to provide a deeper understanding of the policy behind New Zealand\'s corporate tax regimes and the allocation of the tax burden between companies, shareholders and other investors.', 'Restriction: COMLAW 746', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 784', 'Taxation of Property Transactions', 'Examines all of the tax consequences of acquiring, holding, developing, building on, leasing or otherwise dealing with land and personal property.', 'Restriction: COMLAW 751', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 786', 'Tax Administration and Disputes', 'An advanced study of the Public Law and procedural issues arising from administration of the Revenue Acts in New Zealand. Provides an analysis of the powers, discretions and responsibility of the Commissioner and the Inland Revenue Department. Major topics include the Department\'s assessment function and taxpayer self-assessment, the Binding Ruling regime, the Commissioner\'s statutory powers of investigation and information gathering, the exercise of discretions and administrative decisions, and the role of judicial review.', 'Restriction: COMLAW 753', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 787', 'Taxation of Trusts and Non-corporate Entities', 'An advanced study of the tax liability of different business structures and their members, particularly non-corporate entities. Considers the different tax regimes applicable to trusts, partnerships and limited partnerships, Portfolio Investment Entities (PIEs), charities and Mori authorities. Comparison between these entities provides a deeper understanding of the policy behind New Zealand\'s tax regimes and the allocation of the tax burden between companies and other entities.', 'Restriction: COMLAW 756', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 788', 'Special Topic: Organisation for Economic Co-operation and Development and Tax', '', 'Restriction: COMLAW 758', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 789', 'Research Essay in Taxation Law', '', 'Restriction: COMLAW 789', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 790', 'Dissertation in Taxation Law - Level 9', '', 'Restriction: COMLAW 790, 792', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 792', 'Dissertation in Taxation Law - Level 9', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 794A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Commercial', 'LAWCOMM 794B', 'Thesis in Taxation Law - Level 9', '', 'Restriction: COMLAW 794', 'null', 'null');
INSERT INTO `law` VALUES ('Law Environmental', 'LAWENVIR 401', 'Resource Management Law', 'An introduction to the law relating to resource management and environmental regulation including: evolution of the sustainable management concept, consideration of national objectives, application of the Treaty of Waitangi, national standards, coastal policies, regional statements and plans, district plans, designations, heritage and conservation powers, resource consent procedures, and remedial powers and enforcement procedures.', 'Prerequisite: LAW 211', 'null', 'null');
INSERT INTO `law` VALUES ('Law Environmental', 'LAWENVIR 402', 'Special Topic', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Environmental', 'LAWENVIR 403', 'Special Topic', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Environmental', 'LAWENVIR 420', 'Global Environmental Law', 'Concepts, principles, customs, and treaties of international law as related to the protection of the global environment including: prevention of pollution, protection of the marine environment, ozone layer protection, climate change, biodiversity, the UNCED process and the legal framework for sustainable development.', 'Prerequisite: 30 points at Stage II in Global Environment and Sustainable Development or LAW 211', 'null', 'null');
INSERT INTO `law` VALUES ('Law Environmental', 'LAWENVIR 421', 'Energy and Natural Resources Law', 'An examination of the common law principles, legislation, and administrative controls in New Zealand relating to ownership of, prospecting for, extraction and use of, minerals (including oil and gas), alternative energy resources, forestry and fisheries resources.', 'Corequisite: LAW 301', 'null', 'null');
INSERT INTO `law` VALUES ('Law Environmental', 'LAWENVIR 424', 'Special Topic', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Environmental', 'LAWENVIR 425', 'Special Topic', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Environmental', 'LAWENVIR 426', 'Special Topic', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Environmental', 'LAWENVIR 427', 'Special Topic', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Environmental', 'LAWENVIR 430', 'Environmental Constitutionalism', 'Comparative constitutional examination into concepts and principles of the law related to environmental protection and sustainable development. Key areas include environmental ethics, sustainability, human rights and responsibilities, and state obligations.', 'Prerequisite: 30 points at Stage II in Global Environment and Sustainable Development or LAW 211', 'null', 'null');
INSERT INTO `law` VALUES ('Law Environmental', 'LAWENVIR 431', 'Special Topic', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Environmental', 'LAWENVIR 432', 'Special Topic', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Environmental', 'LAWENVIR 702', 'Comparative Environmental Law - Level 9', 'Selected topics in environmental law from an internationally comparative perspective including: concepts of sustainable development, the precautionary principle, environmental impact assessment procedures, risk evaluation schemes and advanced environmental legislation in various jurisdictions including the United States, the European Union, Japan and New Zealand. Involves individual research resulting in a substantial individual research essay.', 'Restriction: ENVLAW 701', 'null', 'null');
INSERT INTO `law` VALUES ('Law Environmental', 'LAWENVIR 710', 'International Environmental Law - Level 9', 'Selected problems of international law as related to the protection of the global environment including the present concept of international environmental law and current trends toward a global law of sustainable development, law on climate changes, the ozone layer, the marine environment, biodiversity and the implications of international environmental issues for municipal law. Involves individual research resulting in a substantial individual research essay.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Environmental', 'LAWENVIR 712', 'Mining and Energy Law - Level 9', 'An examination of the legal principles, government policy, regulation, and administrative control relating to ownership and exploitation of minerals (including oil and gas), and alternative energy resources in New Zealand.', 'Restriction: ENVLAW 710, 723, LAWENVIR 713', 'null', 'null');
INSERT INTO `law` VALUES ('Law Environmental', 'LAWENVIR 713', 'Mining, Energy and Natural Resource Law - Level 9', 'Study of the common law, legislation and administrative controls in New Zealand relating to ownership of, prospecting for, extraction and use of minerals (including oils and gas), alternative energy resources, forestry and fisheries resources. Involves individual research resulting in a substantial individual research essay.', 'Restriction: LAWENVIR 712, 714', 'null', 'null');
INSERT INTO `law` VALUES ('Law Environmental', 'LAWENVIR 714', 'Natural Resources Law - Level 9', 'An examination of the legislative framework and legal principles relating to agriculture, forestry, and fisheries activities in New Zealand. Selected topics will include: legal ownership and interests in rural land and natural resources, the role of government, Mori claims and resource development conflict resolution. Involves individual research resulting in a substantial individual research essay.', 'Restriction: LAWENVIR 713', 'null', 'null');
INSERT INTO `law` VALUES ('Law Environmental', 'LAWENVIR 716', 'Resource Management Law - Level 9', 'Studies in the New Zealand law relating to resource management and environmental regulation including: evolution of the sustainable management concept, consideration of national objectives, Treaty of Waitangi application, national standards, coastal policies, regional statements and plans, district plans, designations, heritage and conservation powers, resource consent procedures, and remedial powers and enforcement procedures. Involves individual research resulting in a substantial individual research essay.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Environmental', 'LAWENVIR 718', 'Special Topic: European Union Environmental Law and Governance - Level 9', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Environmental', 'LAWENVIR 719', 'Special Topic: Food Law - Level 9', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Environmental', 'LAWENVIR 720', 'Special Topic - Level 9', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Environmental', 'LAWENVIR 721', 'Special Topic: Ocean Governance Law - Level 9', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Environmental', 'LAWENVIR 723', 'Climate Change Law - Level 9', 'Explores the interconnected science, policy and legal issues involved in addressing climate change. Involves individual research resulting in a substantial individual research essay.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Environmental', 'LAWENVIR 725', 'Corporate Environmental Governance - Level 9', 'Comparative and global perspectives exploring the regulatory and governance frameworks that shape how corporations address environmental and related social issues. Involves individual research resulting in a substantial individual research essay.\n</p>', 'null', 'null', 'null');
