INSERT INTO `law` VALUES ('Law General', 'LAWGENRL 401', 'Evidence', 'An overview of the rules related to the presentation of proof in New Zealand courts (civil and criminal) and tribunals, including the rules relating to hearsay, opinion evidence, privilege, examination of witnesses, confessions and the exclusion of illegally obtained evidence.', 'Prerequisite: LAW 201, 231', 'null', 'null');
