INSERT INTO `law` VALUES ('Law General', 'LAWGENRL 405', 'Community Law Internship', 'Participation in and report on an approved internship involving at least 150 hours internship with an approved organisation in a community context, and evaluation of the issues arising therefrom.', 'Prerequisite: LAW 201, 211, 231, 241, 298 or 299', 'null', 'null');
INSERT INTO `law` VALUES ('Law General', 'LAWGENRL 420', 'Advocacy', 'An introduction to the general principles of trial and appellate advocacy in civil and criminal cases, the study of trial preparation and performance with a focus on practical instruction (including demonstrations and exercises which are videotaped and critiqued) and the study of tactical and ethical issues facing litigators.', 'Prerequisite: LAW 301, 306', 'null', 'null');
INSERT INTO `law` VALUES ('Law General', 'LAWGENRL 421', 'Civil Procedure', 'Advanced studies in procedure in civil actions and other civil proceedings with reference to the evolution and history of the present form of civil action, pleadings, discovery and other pre-trial devices; joinder of parties; jurisdiction (including equitable jurisdiction); and appellate procedure.', 'Prerequisite: LAW 231', 'null', 'null');
INSERT INTO `law` VALUES ('Law General', 'LAWGENRL 422', 'Women and the Law', 'A study of the dual role of law in addressing and maintaining gender inequality, feminist thought on the gendered nature of law, and specific legal issues relevant to the status and interests of women in society.', 'Prerequisite: LAW 201, 211', 'null', 'null');
INSERT INTO `law` VALUES ('Law General', 'LAWGENRL 423', 'Legal History', 'Historical analysis of problems currently facing the law in Aotearoa New Zealand with reference to both English sources and indigenous developments.', 'Prerequisite: LAW 211', 'null', 'null');
INSERT INTO `law` VALUES ('Law General', 'LAWGENRL 424', 'Negotiation, Mediation and Dispute Resolution', 'An introduction to negotiation, mediation and dispute resolution covering: (i) a conceptual study of these processes including the study of the function of law, roles of lawyer, ethics, analysis, cultural and socio-economic factors; and (ii) a practical study of these processes including criteria for choosing resolution methods, techniques, attitudes, problem solving strategies, communication techniques, and effectiveness.', 'Restriction: LAW 447', 'null', 'null');
INSERT INTO `law` VALUES ('Law General', 'LAWGENRL 425', 'Psychiatry and the Law', 'A general introduction to the formal relationship between psychiatry and law; the legal processes affecting compulsory assessment and treatment, including consideration of constitutional and cultural issues, patients rights and the review process; and the law and practice concerning forensic patients.', 'Prerequisite: LAW 201', 'null', 'null');
INSERT INTO `law` VALUES ('Law General', 'LAWGENRL 426', 'Roman Law', 'The significance of Roman Law as an enduring legacy from the ancient world to the modern; a study of the sources and historical development of Roman law; and a study of selected aspects of Roman law including the law of actions (procedure), the law of obligations (contract and delict), family and succession law, and criminal law.', 'Restriction: LAW 453', 'null', 'null');
INSERT INTO `law` VALUES ('Law General', 'LAWGENRL 427', 'Equitable Remedies', 'Examination of the more important remedies and orders granted in the court\'s equitable jurisdiction (excluding constructive trusts). Particular attention is directed to remedies in aid of judgment and interlocutory orders to maintain the court\'s authority over the parties or their property.', 'Prerequisite: LAW 306', 'null', 'null');
INSERT INTO `law` VALUES ('Law General', 'LAWGENRL 428', 'South Pacific Legal Studies', 'Legal study of Pacific Island states located in the regions of Micronesia, Melanesia and Polynesia. Distinctive features of law arising in Pacific states, including: sources of law; relationships between custom and law; corruption and anti-corruption measures; democracy and governance; constitutional crises and constitutional futures; environmental and trade issues; regional issues; human rights issues.', 'Prerequisite: 30 points at Stage II in International Relations and Business or LAW 211', 'null', 'null');
INSERT INTO `law` VALUES ('Law General', 'LAWGENRL 429', 'Law of Family Property', 'Advanced study of the law of property in family contexts, including trusts, succession, and matrimonial property.', 'Corequisite: LAW 306', 'null', 'null');
INSERT INTO `law` VALUES ('Law General', 'LAWGENRL 430', 'Advanced Family Law', 'Advanced problems in selected areas of family law.', 'Prerequisite: LAWGENRL 402 or 433', 'null', 'null');
INSERT INTO `law` VALUES ('Law General', 'LAWGENRL 432', 'Healthcare Law', 'An introduction to the legal and ethical issues related to health care delivery including: the purchase and provision of health services, the relationship between health providers and consumers, professional accountability, codes of rights, legal and ethical issues at the start and end of life, and biomedical research.', 'Prerequisite: LAW 211, 231', 'null', 'null');
INSERT INTO `law` VALUES ('Law General', 'LAWGENRL 433', 'Family Law', 'The law relating to cohabitation and marriage, the establishment of parenthood, and the relationship between parent and child. Study of the interrelationship between the state, the family and child protection and support.', 'Prerequisite: LAW 211', 'null', 'null');
INSERT INTO `law` VALUES ('Law General', 'LAWGENRL 434', 'Trial Advocacy', 'Examines the principles of trial advocacy in both civil and criminal cases; practical instruction; related procedural, tactical and ethical issues.', 'Prerequisite: LAW 301, 306', 'null', 'null');
INSERT INTO `law` VALUES ('Law General', 'LAWGENRL 435', 'Theories of Private Law', 'Different theories of private law and how it leads to different solutions to concrete legal questions. This course explores some of the main schools of normative thought in contemporary private law theory. It examines the key concepts and values associated with each theory before considering different theoretical frameworks for understanding two of the main categories of private law: property and contract.', 'Prerequisite: LAW 231, 241', 'null', 'null');
INSERT INTO `law` VALUES ('Law General', 'LAWGENRL 436', 'Air and Space Law', 'Examines important aspects of international air and space law using examples of how the international law has been implemented and applied in the New Zealand legal system including topical aviation industry issues.', 'Prerequisite: LAW 211, 241', 'null', 'null');
INSERT INTO `law` VALUES ('Law General', 'LAWGENRL 438', 'Housing Law and Policy', 'An examination of the law and the policy considerations that relate to residential housing including: the historical development and current state of residential tenancy protection legislation; the relationship between social policy and housing regulation; human rights and social equity considerations; economic measures to achieve government policy objectives for housing; regulating the private rental market; property rights and security of tenure issues; ‘consumer protection\' measures to ensure safe and habitable housing; housing and natural disasters; retirement housing; new forms of housing ownership; and dispute resolution.', 'Prerequisite: LAW 301', 'null', 'null');
INSERT INTO `law` VALUES ('Law General', 'LAWGENRL 440', 'Youth Justice', 'An examination of why children and young people may be treated differently by criminal justice systems; comparison of the unique New Zealand youth justice system with international developments; consideration of particular topics, including the response to Mori young people and issues arising from the gender of young offenders.', 'Prerequisite: LAW 201, 211', 'null', 'null');
INSERT INTO `law` VALUES ('Law General', 'LAWGENRL 442', 'Animals and the Law', 'The history, philosophy, and ethics of humanity\'s treatment of animals; relevant legislation and case law. Topics include the development of the humane movement; consideration of whether all animals should be treated as property and the justification for such an approach; issues such as standing (whether people should be able to raise legal claims on behalf of animals), the development of animal protection legislation and what it does for animals; and the emergence of a concept of Animal Rights; the use of animals in medical and cosmetic research, hunting, and factory farming; certain international agreements on animals.', 'Prerequisite: LAW 211', 'null', 'null');
INSERT INTO `law` VALUES ('Law General', 'LAWGENRL 443', 'Introduction to Common Law', 'The history, nature and evolution of the Common Law; common law reasoning; the interaction of case law and legislation in a common law system.', 'Restriction: LAW 472', 'null', 'null');
INSERT INTO `law` VALUES ('Law General', 'LAWGENRL 444', 'Contemporary Issues in Land Law', 'Study of selected contemporary issues in real property. Topics may include: legal theory of real property; the constitution and takings of private property; state regulation of private property; the law of public recreational access; particularly to the waterfront; indigenous challenges to Crown ownership and governance of land, including the beds of water bodies and national parks; the aims of the Torrens system and proposals for reform of the Land Transfer Act 1952, in particular relating to land covenants, fraud and exceptions to indefeasibility.', 'Prerequisite: LAW 301', 'null', 'null');
INSERT INTO `law` VALUES ('Law General', 'LAWGENRL 445', 'The History of the Law of Obligations', 'The doctrinal history of the law of contract, tort and unjust enrichment from the twelfth century to the twentieth century. Original primary materials in the form of case law and legal treatises are considered.', 'Prerequisite: LAW 231, 241', 'null', 'null');
INSERT INTO `law` VALUES ('Law General', 'LAWGENRL 446', 'Pacific People in Aotearoa: Legal Peripheries', 'Examines and critiques certain areas and aspects of the law and legal system in New Zealand of particular relevance for Pacific people and communities.', 'Prerequisite: LAW 211', 'null', 'null');
INSERT INTO `law` VALUES ('Law General', 'LAWGENRL 447', 'Community Law Project', 'Participation in and report on an approved project involving at least 75 hours with an approved organisation in a community context, and evaluation of the issues arising therefrom.', 'Prerequisite: LAW 201, 211, 231, 241, 298 or 299', 'null', 'null');
INSERT INTO `law` VALUES ('Law General', 'LAWGENRL 448', 'Selected Topics in Health Care Law', 'A selection of topics designed to consolidate and advance understanding of the theory and practice of health care law. The topics covered will vary according to current legal developments, but are likely to include: the law relating to human research and experimentation; legal and ethical issues in abortion, human reproduction, and assisted reproductive technologies; organ and tissue donation; the sterilisation of mental incompetents; the regulation of health professions, medical manslaughter and the disciplinary process; legal issues arising in human genetics, stem cell research, human reproductive cloning. An opportunity for an in-depth examination of relevant, current legal developments relating to health professional practice and patients\' rights.', 'Prerequisite: LAW 211, 231', 'null', 'null');
INSERT INTO `law` VALUES ('Law General', 'LAWGENRL 449', 'Selected Topics in Media Law', 'The law governing the media and journalists. Topics to be covered will include some of: defamation, contempt of court, breach of confidence, privacy, the broadcasting legislation, censorship and copyright.', 'Prerequisite: LAW 211, 231', 'null', 'null');
INSERT INTO `law` VALUES ('Law General', 'LAWGENRL 450', 'Public Authority Liability', 'Detailed consideration of the bases in both public and private law on which public authorities or the Crown may be liable to compensate private individuals or entities, and the debates surrounding this issue; the interface between private and public law.', 'Prerequisite: LAW 201, 211, 231, 241', 'null', 'null');
INSERT INTO `law` VALUES ('Law General', 'LAWGENRL 452', 'Appellate Advocacy', 'General principles of appellate advocacy in both civil and criminal cases; practical instruction; related procedural, tactical and ethical issues.', 'Prerequisite: LAW 301, 306', 'null', 'null');
INSERT INTO `law` VALUES ('Law General', 'LAWGENRL 453', 'Special Topic: Privacy Law', 'An examination of the Law relating to privacy in New Zealand with special reference to the common law protection of privacy; the protection of privacy under the Broadcasting Act 1989; and the scope and application of the Privacy Act 2020.', 'Prerequisite: LAW 211, 231', 'null', 'null');
INSERT INTO `law` VALUES ('Law General', 'LAWGENRL 454', 'Special Topic: Youth Justice', 'A study of how children and young people interact with and are treated by the criminal justice system of Aotearoa New Zealand, with comparison to developments in other jurisdictions. Topics may include causes and responses to youth offending, youth as victims and participants in the criminal process, responses to Mori youth, gender, Family Group Conferences, Rangatahi Courts, and child imprisonment.', 'Prerequisite: LAW 201', 'null', 'null');
INSERT INTO `law` VALUES ('Law General', 'LAWGENRL 455', 'Economic Analysis of Public and Private Law', 'Examines the role economic thinking can play in legal reasoning. Key economic concepts. Economic analysis applied to tort and contract law, constitutional and public international law, and judicial decision-making.', 'Prerequisite: LAW 211, 231, 241', 'null', 'null');
INSERT INTO `law` VALUES ('Law General', 'LAWGENRL 456', 'Special Topic: “Justice” in Sentencing', 'Concepts of “Justice” in the sentencing process in Aotearoa are examined and critiqued from multiple perspectives with a particular focus on te Ao Mori and an emphasis on the practical elements involved in sentencing advocacy.', 'Prerequisite: LAW 201', 'null', 'null');
INSERT INTO `law` VALUES ('Law General', 'LAWGENRL 457', 'Special Topic', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law General', 'LAWGENRL 458', 'Special Topic: Pasifika Peoples and the Law', 'Undeniably, Pasifika peoples in Aotearoa experience a number of inequalities. However, the complex relationships between these inequalities and the law are rarely explored in legal education and scholarship. Therefore, this course aims to give students an understanding of these relationships by encouraging the use of critical perspectives to examine a range of socio-legal issues facing Pasifika communities in Aotearoa today.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law General', 'LAWGENRL 459', 'Special Topic', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law General', 'LAWGENRL 702', 'Foundations of Human Rights - Level 9', 'A study of the moral and jurisprudential basis for human rights and for their protection through law including a study of the major theories of justice and rights. Involves individual research resulting in a substantial individual research essay.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law General', 'LAWGENRL 711', 'Special Topic: Corruption: Comparative and International Approaches - Level 9', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law General', 'LAWGENRL 712', 'Therapeutic Jurisprudence - Level 9', 'The role of the law as a therapeutic agent. The impact of the law on emotional life and psychological wellbeing. Investigation of different areas of the practice of law that may be susceptible to a therapeutic jurisprudence analysis, including drug treatment and mental health courts. Involves individual research resulting in a substantial individual research essay.\n</p>', 'null', 'null', 'null');
