INSERT INTO `law` VALUES ('Law Honours', 'LAWHONS 702B', 'Human Rights', 'The legal modes for protection of human rights, including the New Zealand Bill of Rights Act 1990 and the Human Rights Act 1993, freedom of expression and religion, criminal procedural rights, equality, and the prohibited grounds of discrimination.', 'Restriction: LAW 342, 452', 'null', 'null');
INSERT INTO `law` VALUES ('Law Honours', 'LAWHONS 706A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Honours', 'LAWHONS 706B', 'Criminal Law and Policy', 'An in-depth analysis of current issues in substantive and procedural criminal law including: the role and function of forensic experts, developments in criminal law, criminal justice theory and criminal law reform.', 'Restriction: LAW 346', 'null', 'null');
