INSERT INTO `law` VALUES ('Law Honours', 'LAWHONS 720B', 'Maritime Law', 'A study of the law governing ships including: the ownership of and property in ships, charter parties, ship mortgages, law of carriage, shipboard crimes and torts, the law of collision, salvage, wrecks, the admiralty jurisdiction of the courts, national shipping laws, the international regulatory framework, the conflict of laws rules applicable to admiralty disputes and marine insurance.', 'Restriction: LAW 360', 'null', 'null');
