INSERT INTO `law` VALUES ('Law Honours', 'LAWHONS 722B', 'Medico-legal Problems', 'Selected studies in the relationship between law and medicine including: the purchase and provision of health services, the relationship between health providers and consumers, professional accountability, codes of rights, legal and ethical issues at the start and end of life, and biomedical research.', 'Restriction: LAW 362', 'null', 'null');
