INSERT INTO `law` VALUES ('Law Honours', 'LAWHONS 728B', 'Studies in Public Law', 'Advanced studies in respect of the principles and workings of the New Zealand constitution, the powers, privileges and immunities of the three branches of government, the exercise and control of government power and the relationship between the individual and the state (including the position of Mori under the Treaty of Waitangi).', 'Restriction: LAW 368, 403, 404', 'null', 'null');
INSERT INTO `law` VALUES ('Law Honours', 'LAWHONS 729A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Honours', 'LAWHONS 729B', 'Studies in Torts', 'A study of policy issues in the law of tort, developments in the law of negligence, the economic torts, breach of statutory duty, invasion of privacy, informed consent, defences and remedies.', 'Restriction: LAW 369', 'null', 'null');
INSERT INTO `law` VALUES ('Law Honours', 'LAWHONS 733A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Honours', 'LAWHONS 733B', 'Studies in Contract Law', 'Advanced studies of selected topics in contract law (and related areas), which may include consideration of the history of contract law, various jurisprudential and/or comparative approaches to contract law, various doctrines of contract law, the law of remedies and statutory reform of contract law.', 'Restriction: LAW 383', 'null', 'null');
INSERT INTO `law` VALUES ('Law Honours', 'LAWHONS 734A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Honours', 'LAWHONS 734B', 'Issues in Insurance Law', 'A consideration of the law governing insurance contracts, including the duty of utmost good faith; the interpretation of the policy; the scope of cover; warranties and conditions; the claims process and fraudulent claims; and quantification of the insurer’s obligations; subrogation and recoupment; and third party rights.', 'Restriction: LAW 384, 431, LAWCOMM 424, 453', 'null', 'null');
INSERT INTO `law` VALUES ('Law Honours', 'LAWHONS 735A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Honours', 'LAWHONS 735B', 'Corruption and Democracy', 'Corruption has devastating implications for self-government. This seminar explores how corruption manifests within democracies specifically: the impact of corruption on democratic values and priorities; the inequalities it creates within political and economic systems; the environmental destruction and the potential for human rights violations it creates - all from the perspective of distinct sources of law including constitutions and treaties.', 'Restriction: LAW 385, LAWPUBL 467', 'null', 'null');
INSERT INTO `law` VALUES ('Law Honours', 'LAWHONS 736A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Honours', 'LAWHONS 736B', 'Topics in International Law', 'An in-depth analysis of selected topics in historical and contemporary international law, the aim of which is to provide students with a deeper appreciation of the theoretical debates in the discipline, as well as a broader understanding of the topics being studied.', 'Corequisite: LAW 435 or LAWPUBL 402', 'null', 'null');
INSERT INTO `law` VALUES ('Law Honours', 'LAWHONS 737A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Honours', 'LAWHONS 737B', 'Law of Agency', 'A study of the principles of agency law and selected applications thereof.', 'To complete this course students must enrol in LAWHONS 737 A and B', 'null', 'null');
INSERT INTO `law` VALUES ('Law Honours', 'LAWHONS 738A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Honours', 'LAWHONS 738B', 'Studies in Company Law and Contract Law', 'An advanced study of selected topics in the areas of company law and contract law, including the relationship between directors, theories of the company, comparative corporate law, shareholders and the company, consideration of the history of contract law, various jurisprudential and/or comparative approaches to contract law, various doctrines of contract law and potential statutory reform of contract law.', 'Corequisite: LAWCOMM 402', 'null', 'null');
INSERT INTO `law` VALUES ('Law Honours', 'LAWHONS 739A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Honours', 'LAWHONS 739B', 'Theoretical Foundations of Private Law', 'Explores some of the main schools of thought in contemporary private law theory, including formalism, realism, economic analysis and critical legal studies. Examines the key concepts and values associated with each theory before considering different theoretical frameworks for understanding two of the main categories of private law: property and contract.', 'To complete this course students must enrol in LAWHONS 739 A and B', 'null', 'null');
INSERT INTO `law` VALUES ('Law Honours', 'LAWHONS 740A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Honours', 'LAWHONS 740B', 'The History of the Law of Obligations', 'The doctrinal history of the law of contract, tort and unjust enrichment from the twelfth century to the twentieth century. Original primary materials in the form of case law and legal treatises are considered.', 'Restriction: LAW 356, LAWGENRL 445', 'null', 'null');
INSERT INTO `law` VALUES ('Law Honours', 'LAWHONS 741A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Honours', 'LAWHONS 741B', 'Indigenous Peoples and the Law', 'An overall consideration of Indigenous peoples in international, constitutional and human rights law in New Zealand and internationally.', 'Restriction: LAWPUBL 446', 'null', 'null');
INSERT INTO `law` VALUES ('Law Honours', 'LAWHONS 742A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Honours', 'LAWHONS 742B', 'Public Authority Liability', 'Covers the various public and private law bases for monetary liability of public authorities (with a focus on Torts); the doctrinal and policy debates surrounding such liability; the uncertain interface between private and public law.', 'Restriction: LAW 466, LAWGENRL 450, LAWPUBL 450', 'null', 'null');
INSERT INTO `law` VALUES ('Law Honours', 'LAWHONS 743A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Honours', 'LAWHONS 743B', 'Refugee and Immigration Law', 'Advanced studies on a range of refugee and immigration law matters including: the history and development of the international refugee system, changing understandings of refugees, detention systems and camps, people smuggling, themes in New Zealand immigration law history, the securitisation of immigration law and prospects of a borderless world. The course will incorporate history, theory, policy and critical analysis.', 'To complete this course students must enrol in LAWHONS 743 A and B', 'null', 'null');
INSERT INTO `law` VALUES ('Law Honours', 'LAWHONS 744', '', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Honours', 'LAWHONS 744A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Honours', 'LAWHONS 744B', 'Special Topic', '', 'To complete this course students must enrol in LAWHONS 744 A and B, or LAWHONS 744', 'null', 'null');
INSERT INTO `law` VALUES ('Law Honours', 'LAWHONS 745', 'Corporate Governance', 'A study of the governance of corporations adopting comparative, and law-and-economics perspectives including the role and obligations of the board, the legal and economic relationships between corporate constituents, and trends and developments in corporate governance.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Honours', 'LAWHONS 746A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Honours', 'LAWHONS 746B', 'Concepts in Law and Security', 'An in-depth analysis of theoretical concepts related to law and security. The aims of the course are to introduce students to key concepts in the relevant academic discourse, to explore the ways in which these concepts frame discussions regarding law and security in the post-9/11 era, and to examine the extent to which those concepts influence policy, practice and discourse.', 'To complete this course students must enrol in LAWHONS 746 A and B', 'null', 'null');
INSERT INTO `law` VALUES ('Law Honours', 'LAWHONS 747A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Honours', 'LAWHONS 747B', 'Law of Restitution', 'A study of the law of just restitution, including its general principles, the concept of unjust enrichment, and the relationship of the subject with other areas of law, including the law of property, contract law, and the law of tort. Consideration is also given to selected areas of practical application, such as the reversal of transfers for mistake, impaired judgement, duress and failure of conditions, and profiting from wrongs.', 'To complete this course students must enrol in LAWHONS 747 A and B', 'null', 'null');
INSERT INTO `law` VALUES ('Law Honours', 'LAWHONS 748A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Honours', 'LAWHONS 748B', 'Special Topic', '', 'To complete this course students must enrol in LAWHONS 748 A and B', 'null', 'null');
INSERT INTO `law` VALUES ('Law Honours', 'LAWHONS 749A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Honours', 'LAWHONS 749B', 'Special Topic', '', 'To complete this course students must enrol in LAWHONS 749 A and B', 'null', 'null');
INSERT INTO `law` VALUES ('Law Honours', 'LAWHONS 789', 'Dissertation - Level 9', 'A dissertation of approximately 15,000 words resulting from original research of the student, having the scope, and depth of research, of a competent law review article. The topic of the dissertation needs the approval of the Dean of Faculty of Law.', 'Restriction: LAW 789', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 401', 'Administrative Law', 'A study of the general principles of judicial review of administrative action, including the ultra vires principle, the substantive and procedural restraints on the exercise of public power, and the remedies available for breach of these principles.', 'Prerequisite: LAW 201, 211, 231, 241', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 402', 'International Law', 'An introduction to the basic principles and nature of public international law and its role in contemporary society including an overview of the current legal framework, the sources of international law, the law of treaties, issues surrounding international personality, international dispute resolution and the use of force.', 'Prerequisite: 30 points at Stage II in Global Politics and Human Rights or LAW 211, 231, 241', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 403', 'Special Topic: Advanced International Law', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 404', 'Special Topic', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 420', 'Advanced Criminal Law', 'An examination of selected topics in criminal law and the criminal justice process of Aotearoa New Zealand, with comparison to developments in other jurisdictions. Topics may include: victims and the criminal process; restorative justice; therapeutic jurisprudence; solution-focused courts; family violence; indigenous peoples and the criminal justice system; media and crime; and imprisonment. The focus is on law-in-action and law-in-context.', 'Prerequisite: LAW 201', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 421', 'Advanced International Law', 'Advanced studies in selected areas of the law of nations; a critical analysis of existing and developing international law, and consideration of the relationship between law, economics, politics and international diplomacy.', 'Prerequisite: LAW 435 or LAWPUBL 402', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 422', 'Contemporary Tiriti/Treaty Issues', 'Contemporary legal issues arising under Te Tiriti o Waitangi.', 'Prerequisite: LAW 211', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 423', 'Criminology', 'The study of major theories of criminology; the definition, nature and causes of criminal behaviour; the administration of criminal justice; and the interrelation of specific crimes and the criminal justice system.', 'Prerequisite: LAW 201, or 121G and either CRIM 201 or 202', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 424', 'Immigration and Refugee Law', 'A consideration of the basic features of the Immigration Act 1987 with emphasis on the role of administrative law in the immigration field, an introduction to the law of refugee status, and the jurisprudence of the New Zealand. Refugee Status Appeals Authority.', 'Prerequisite: LAW 211', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 425', 'Employment Law', 'A study of the common law individual contract of employment and the mutual duties implied therein; the statutory system and the collective agreement under the Employment Relations Act and its predecessors; the impact on industrial law of specific statutory reforms such as the Human Rights Act, the Health and Safety in Employment Act, and the Parental Leave legislation; and analysis of strikes and lockouts, both as common law torts and as events subject to statutory control.', 'Prerequisite: LAW 231, 241', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 426', 'Judicial Review', 'A study of the general principles of judicial review of administrative action, and remedies available for breach of those principles.', 'Prerequisite: LAW 211', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 427', 'Mori Land Law', 'Customary Mori land tenure and legal issues arising under Te Ture Whenua Mori Act 1993.', 'Corequisite: LAW 301', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 428', 'Rights and Freedoms', 'The legal modes for protection of civil rights including study of the New Zealand Bill of Rights Act 1990, the Human Rights Act 1993, freedom of speech and religion, criminal procedural rights, equality and the prohibited grounds of discrimination.', 'Prerequisite: LAW 211', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 429', 'Law and Policy', 'An introduction to different theoretical perspectives on the role of the State and the policy-making process; the policy-making process and the techniques for analysing policy; the process, substance and effects of key policy changes since 1984.', 'Prerequisite: LAW 211', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 430', 'Criminal Procedure', 'The rules governing the conduct of criminal trials and the investigation of crime. Changes to criminal procedure brought about by judicial interpretation of the New Zealand Bill of Rights Act 1990. Selected topics which may include: search and seizure, name suppression, right to counsel, exclusion of evidence, bail, juries, trial delay.', 'Prerequisite: LAW 201', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 431', 'Advanced Public Law', 'Advanced studies in selected areas of Public Law.', 'Prerequisite: LAW 211', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 432', 'International Economic Regulation', 'The growing array of trade and investment, or economic integration, agreements at the multilateral, regional and bilateral levels. Core concepts, theories, institutions and rules from the perspective of public international law, as well as the realpolitik of trade negotiations.', 'Prerequisite: 30 points at Stage II in International Relations and Business or LAW 211', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 434', 'International Criminal Law', 'The evolution of international criminal law, from the Nuremberg and Tokyo Tribunals to the International Criminal Court. Topics include: the nature and sources of international criminal law; jurisdiction; individual and collective responsibility; substantive crimes and defences; alternatives to criminal trials, such as truth commissions and amnesties.', 'Prerequisite: LAW 201', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 435', 'Law of the Sea and Antarctica', 'A foundational study of the Law of the Sea and the law relating to Antarctica, with specialised work on contemporary legal and policy issues.', 'Prerequisite: 30 points at Stage II in International Relations and Business, or LAW 211', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 436', 'International Human Rights', 'An outline of the growing jurisprudence relating to international human rights law, with a particular focus on the case law of the UN Human Rights Committee and the European Court of Human Rights; consideration of the core human rights protected, and the practicalities of how human rights cases are brought before the main adjudicatory bodies.', 'Prerequisite: 30 points at Stage II in Global Politics and Human Rights or LAW 211', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 438', 'European Public Law', 'An introduction to the unique organisational structure of the European Union and to its fundamental principles. Topics include: the fundamental structure of the EU; how EU law is made and by whom; the role of the European Courts, and the relationship between EU and national law; and selected areas of EU substantive law, such as the free movement of persons and goods.', 'Prerequisite: 30 points at Stage II in BGlobalSt courses or LAW 211', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 441', 'Nga Tikanga Mori', 'A study of Mori customary law. Topics that may be covered include: the content of Customary Law as it relates to Mori social and political organisation and land tenure; Customary Law’s interaction with the general (e.g., through statute or the common law); Customary Law and legal pluralism and culture theory; and the role of Customary Law in contemporary Crown-iwi relations.', 'Prerequisite: LAW 211', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 442', 'Researching Indigenous Rights Theory, Law and Practice', 'Aims to provide students with the opportunity to develop their research and writing skills, while also acquiring substantive knowledge about indigenous rights law in the context of international law; comparative law; and the domestic law of states. Students will also learn how to research indigenous rights law, for example by learning how to access materials, academic papers, and information.', 'Prerequisite: LAW 211', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 443', 'Refugee Law', 'An overview of the international and New Zealand refugee law systems including who is a refugee, the pathways and processes for becoming a refugee, the rights of refugees. Critical approaches to the field of refugee law; discussion of contemporary challenges in the field.', 'Prerequisite: 30 points at Stage II in Global Politics and Human Rights or LAW 211', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 444', 'Immigration Law', 'An overview of the immigration issues in New Zealand. The history of immigration law; examination of some of the key issues in immigration law today including the immigration law system, citizenship, visas, deportation, appeals and judicial review of immigration decisions and national security issues. Contemporary challenges in the field.', 'Prerequisite: LAW 211', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 445', 'European Union Law', 'The law related to the European Union and its institutional, economic and social structure as well as the general economic and political implications of the present status of the European Union.', 'Prerequisite: 30 points at Stage II in BGlobalSt courses or LAW 211', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 446', 'Indigenous Peoples in International Law', 'An introduction to international law as it relates to Indigenous peoples including: third world approaches to international law; the United Nations Declaration on the Rights of Indigenous peoples; the relationship between international human rights and Indigenous peoples\' rights; the universal human rights regime and Indigenous peoples; regional human rights systems and Indigenous peoples\' rights; international economic institutions and Indigenous peoples; business and Indigenous peoples\' rights and special topics relevant to Indigenous peoples in the Pacific.', 'Prerequisite: 30 points at Stage II in Global Environment and Sustainable Development or Global Politics and Human Rights, or LAW 211', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 447', 'Statutory Interpretation: Theory and Practice', 'The normal mode of legal reasoning and legal argumentation proceeds by way of interpretation: of statutes, of precedents, of contracts etc. This course takes up the systematic study of statutory interpretation. Drawing on examples from different common law jurisdictions, it surveys the traditional techniques employed in the interpretation of statutes and analyses the contemporary debate between different theoretical schools such as textualism, intentionalism, purposivism and pragmatism.', 'Restriction: LAW 488', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 451', 'Counterterrorism Law and Policy', 'An examination of various legal issues arising out of the \'global war on terror\', including: different paradigms for dealing with emergencies; difficulties of legally defining terrorism; detention of terrorist suspects; ethnic/racial profiling; electronic surveillance; coercive interrogation and torture; targeted killing; criminalisation of offences related to terrorism.', 'Prerequisite: 30 points at Stage II in Global Environment and Sustainable Development or Global Politics and Human Rights, or LAW 201, 211', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 452', 'Law of Armed Conflict', 'The legal rules governing the conduct of hostilities; historical and contemporary operation; associated issues, including treaties and UN peace operations.', 'Corequisite: LAW 435 or LAWPUBL 402', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 454', 'International Disputes Settlement', 'Topics are likely to include: the international law obligation to settle disputes peacefully; legal and political mechanisms for settling international disputes; the establishment and functions of the International Court of Justice; global dispute settlement bodies: dispute settlement system of the World Trade Organization, the Permanent Court of Arbitration, and the International Tribunal for the Law of the Sea; regional tribunals; international criminal courts and tribunals; mixed investor-state dispute settlement.', 'Prerequisite: LAW 211', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 455', 'The Law of Disarmament', 'A legal analysis of the framework governing disarmament and arms control in the international sphere, including (but not limited to) treaties such as the Nuclear Non Proliferation Treaty, the Comprehensive Test Ban Treaty, the Chemical Weapons Convention, and the Landmines Convention. Relevant case law of the International Court of Justice. The role of civil society in disarmament campaigns.', 'Prerequisite: 30 points at Stage II in Global Politics and Human Rights, or LAW 211', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 456', 'Introduction to Criminology', 'A study of classical and contemporary theories concerning the nature, causes and effects of crime and delinquency; the varieties of behaviour that society chooses to control or regulate; formal and informal methods and institutions used to achieve crime control; and the operation of various law enforcement, security, correctional and judicial organisations. Throughout the course attention is given to the relationship between theory and practice and students are encouraged to think critically about the implications of criminological research for criminal justice policy.', 'Prerequisite: LAW 201', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 457', 'Advanced Employment Law', 'Advanced study of both collective and individual aspects of employment law, including comparative treatment of good faith in collective bargaining and ILO conventions. Comparative treatment of tenure in employment, the \'contingent\' workforce and transfer of undertakings.', 'Prerequisite: LAWPUBL 425', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 458', 'Comparative Indigenous Law Topics', 'Study of law and legal issues affecting indigenous peoples in various jurisdictions.', 'Prerequisite: 30 points at Stage II in Global Environment and Sustainable Development or Global Politics and Human Rights, or LAW 211', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 459', 'Aspects of Advanced International Law', 'Advanced studies in selected aspects of international law.', 'Prerequisite: LAW 435 or LAWPUBL 402', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 460', 'Comparative Constitutional Law', 'Through a comparative approach to constitutional structures, history and constitution-making, this course aims to prepare students both theoretically and practically to evaluate New Zealand’s constitution and to consider its future.', 'Prerequisite: LAW 211', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 461', 'Human Rights Theory and its Application', 'Explores substantive international and domestic human rights law and associated legal skills taught through a combination of seminars and experiential learning.', 'Prerequisite: 30 points at Stage II in Global Politics and Human Rights or LAW 399 and one of LAWPUBL 422, 428, 436, 458', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 462', 'Law of the Sea', 'An examination of the history of the development of the law of the sea; the sources of the contemporary law of the sea, leading to the adoption of the 1982 United Nations Convention on the Law of the Sea; and the legal regime of various maritime zones (territorial sea, exclusive economic zone, high seas etc). Particular issues such as the settlement of disputes, maritime delimitation, maritime security, fisheries and bioprospecting are also addressed.', 'Prerequisite: 30 points at Stage II in International Relations and Business, or LAW 211', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 463', 'Advanced Topics in Criminal Law', 'An examination of selected topics in criminal law and the criminal justice process of Aotearoa New Zealand, with comparison to developments in other jurisdictions. Topics may include: victims and the criminal process; restorative justice; therapeutic jurisprudence; solution-focused courts; family violence; indigenous peoples and the criminal justice system; media and crime; and imprisonment. The focus is on law-in-action and law-in-context.', 'Prerequisite: LAW 201', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 464', 'Special Topic', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 465', 'Special Topic: International Law in Aotearoa/New Zealand', 'Students will consider international law from a New Zealand perspective. The course evaluates how international law shapes New Zealand and operates with(in) its legal system, and focuses on New Zealand’s interaction with, contribution to, and attitude towards different areas of international law.', 'Corequisite: LAWPUBL 402', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 466', 'Contemporary Issues in Disarmament Law', 'A study of the legal and humanitarian issues relating to arms control and disarmament, including both conventional weapons (landmines, small arms, incendiary weapons for example) and “weapons of mass destruction” (chemical, biological and nuclear weapons). Themes include the role of civil society in law-making, difficulties of verification and dispute resolution and the role of law in disarmament.', 'Prerequisite: 30 points at Stage II in Global Politics and Human Rights or LAW 211', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 467', 'Anti-corruption Law and Democracy', 'The global significance of corruption and its implications for self-government are explored within the context of rising economic and political inequality and illiberal populism. This course asks whether anti-corruption law can respond to democracy’s vulnerabilities and decline. It examines current economic and political trends, anti-corruption law domestically and internationally, and the potential for enhancing the law to better-protect political integrity.', 'Prerequisite: LAW 201, 211', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 469', 'Special Topic', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 470', 'Special Topic', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 700', 'The International Legal System - Level 9', 'A discussion of the framework, development and theory of international law. An examination of the key concepts and fundamental principles of international law. Involves individual research resulting in a substantial individual research essay.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 705', 'Criminal Law and Policy - Level 9', 'The history and principles of criminal law, analysis of current issues in substantive and procedural criminal law, criminal justice theory, including the theory of punishment, and criminal law reform. Involves individual research resulting in a substantial individual research essay.', 'Restriction: LAW 715', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 707', 'Employment Law - Level 9', 'Selected and comparative studies in employment law. Involves individual research resulting in a substantial individual research essay.', 'Restriction: LAW 725', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 720', 'Local Government Law - Level 9', 'The law relating to the structure, powers and service delivery functions of local government including: the history of local government, reorganisation schemes, powers and administrative principles, civil liability, elections, council procedures, staff employment, works contracts, financial accountability, land valuation, rating systems, environmental functions and bylaws. Consideration of related public bodies, e.g., education and health authorities. Involves individual research resulting in a substantial individual research essay.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 725', 'Privacy Law - Level 9', 'The law governing data surveillance and techniques of social control through the use of information technology, privacy aspects of the prevention of credit and insurance fraud, criminal investigation and the balance between individual rights and commercial and/or public interests. Involves individual research resulting in a substantial individual research essay.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 726', 'Public International Law - Level 9', 'The principles of international law and their application to municipal law. Involves individual research resulting in a substantial individual research essay.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 730', 'Indigenous Peoples’ Rights in National Law - Level 9', 'A study of the application of indigenous peoples’ rights in national jurisdictions including New Zealand, Canada, the United States and select Latin American states. Involves individual research resulting in a substantial individual research essay.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 732', 'Comparative Indigenous Rights Law - Level 9', 'Study of the nature and legal protection of the rights of indigenous persons and groups in international and comparative perspective, including rights to self-government, cultural, religious and linguistic rights. Involves individual research resulting in a substantial individual research essay.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 736', 'Human Rights Litigation - Level 9', 'Study of international conventions and customary international law on human rights, including: free speech, exercise of religion, privacy and nondiscrimination, enforcement mechanisms, human rights theories in international law, third generation human rights including rights to development and a functioning environment, and the recognition of these in international law. Involves individual research resulting in a substantial individual research essay.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 740', 'Special Topic: Local Government Law - Level 9', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 741', 'Special Topic: International Peace and Security - Level 9', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 742', 'Fundamental Principles of Criminal Law - Level 9', 'Advanced study of the scope and application of fundamental principles in criminal liability. Involves significant individual research resulting in a substantial individual research essay.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 743', 'International Criminal Law - Level 9', 'The evolution of international criminal law, from the Nuremberg and Tokyo Tribunals to the International Criminal Court. Topics include the nature and sources of international criminal law, jurisdiction, individual and state responsibility, substantive crimes and defences, and alternatives to criminal trials such as truth commissions and amnesties. Involves individual research resulting in a substantial individual research essay.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 744', 'Special Topic: Human Rights and Technology - Level 9', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 745', 'Special Topic: Constitution and Custom in the South Pacific - Level 9', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 746', 'Special Topic: Waitangi Tribunal: Past, Present and Future - Level 9', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 747', 'Special Topic: Patients\' Rights - Level 9', 'This wide-ranging course examines key current issues in patients’ rights. Topics may include: access, quality and information, rights to access health care, how the quality of health care is assessed, limits of information disclosure, outcomes data, complaint history, resolution of patient concerns and access to justice, patient protection from incompetence and abusive practitioners and the role of professional discipline.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 748', 'Special Topic: Life and Liberty in Comparative Constitutional Law - Level 9', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 749', 'Special Topic: Indigenous Persons: Law and Policy - Level 9', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 750', 'Regulation of Healthcare - Level 9', 'The regulation of healthcare in New Zealand. Topics include: the legal structure of the publicly funded health system, regulation of health practitioners, the Code of Consumers’ Rights and the HDC complaint system, professional discipline, and the role of the Human Rights Review Tribunal. Involves individual research resulting in a substantial individual research essay.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 751', 'Special Topic: Litigating Human Rights - Law and Practice in Comparative Perspective - Level 9', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 752', 'Special Topic: Contemporary Issues in International Law - Level 9', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 753', 'Special Topic: Comparative Constitutional Law - Level 9', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 754', 'Special Topic: Comparative Human Rights Law - Level 9', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 755', 'Special Topic: Comparative Criminology - Level 9', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 756', 'Crown and State Liability - Level 9', 'The availability of remedies against the state or Crown. Conceptual and practical anomalies arising under the Crown Proceedings Act 1950. The development of judicial review, both in New Zealand and in other parts of the Commonwealth, to resolve such anomalies. The potential impact of the Bill of Rights Act 1990. Analytical difficulties, and possibilities for reform. Involves individual research resulting in a substantial individual research essay.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 757', 'Special Topic: International Organisations - Level 9', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 758', 'Special Topic: Uncensored History of International Law - Level 9', '</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 759', 'Privacy at Common Law - Level 9', 'The concept of privacy; definitions; privacy-related interests. Sources of privacy law. Common law privacy protection in New Zealand and other jurisdictions, including the recognition of privacy torts and possible future developments. Specific applications. Involves individual research resulting in a substantial individual research essay.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 760', 'Selected Issues in Public International Law - Level 9', 'Explores a selection of contemporary issues in international law. Involves individual research resulting in a substantial individual research essay.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 761', 'Indigenous Peoples’ Rights in International Law - Level 9', 'An in-depth study of the international law relevant to the protection of the rights of indigenous peoples. The history of the development of indigenous peoples’ rights in international law; analysis of those rights; the mechanisms in place for indigenous peoples to advocate for their rights in international fora. Involves individual research resulting in a substantial individual research essay.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 770', 'Theory and History of Criminal Law and Policy - Level 9', 'Explores the history and principles of criminal law, criminal justice theory, including the theory of punishment with analysis of some current issues in substantive criminal law. Involves individual research resulting in a substantial individual research essay.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 771', 'Advanced Criminal Procedure: Selected Topics - Level 9', 'An in-depth examination of selected laws governing police investigations and criminal trial practice in New Zealand. Involves individual research resulting in a substantial individual research essay.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 772', 'Comparative Criminal Procedure - Level 9', 'An examination of the law related to police investigations and criminal trial practice in Canada, United States and New Zealand. Involves individual research resulting in a substantial individual research essay.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 773', 'Advanced Evidence Law in Criminal Cases - Level 9', 'An in-depth examination of the law governing the presentation of evidence in criminal trial proceedings under the New Zealand Evidence Act 2006. Involves individual research resulting in a substantial individual research essay.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 774', 'Human Rights - Level 9', 'The legal framework for the protection of human rights including a consideration of the moral and jurisprudential underpinnings of modern human rights. Involves significant individual research resulting in a substantial individual research essay.\n</p>', 'null', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 775', 'Human Rights Remedies - Level 9', 'A study of human rights dispute mechanisms and remedies available for breach of rights. Involves individual research resulting in a substantial individual research essay.', 'Prerequisite: LAWPUBL 774', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 776', 'Human Rights: Selected Topics - Level 9', 'A study of selected contemporary human rights issues. Involves individual research resulting in a substantial individual research essay.', 'Prerequisite: LAWPUBL 774', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 777', 'Human Rights in Mental Health Law - Level 9', 'A study of human rights issues arising in the specific context of mental health law. Involves individual research resulting in a substantial individual research essay.', 'Prerequisite: LAWPUBL 774', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 778', 'Issues in Search and Surveillance - Level 9', 'A study of current search and surveillance issues in the context of human rights law. Involves significant individual research resulting in a substantial individual research essay.', 'Prerequisite: LAWPUBL 774', 'null', 'null');
INSERT INTO `law` VALUES ('Law Public', 'LAWPUBL 779', 'Special Topic: Counterterrorism and Constitutionalism - Level 9', '</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Academic Integrity', 'ACADINT A01', 'Academic Integrity Course', 'The Academic Integrity Course is an online course designed to increase student knowledge of academic integrity, university rules relating to academic conduct, and the identification and consequences of academic misconduct. Students work through a series of modules, outlining scenarios that they may encounter while studying at university. Each scenario provides information on relevant rules, resources and expected behaviour.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Audiology', 'AUDIOL 701', 'Auditory Neuroscience', 'The anatomy and physiology of the auditory system, including the central nervous system. Topics include: the anatomy and neuroanatomy of the ear, the role of the middle ear, cochlear mechanics and micromechanics, transduction in the cochlea and vestibular system, responses of the auditory nerve and cochlear homeostasis.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Audiology', 'AUDIOL 702', 'Basic Diagnostic Audiology', 'The basic principles and techniques of diagnostic audiology in adults and children. Topics studied include: basic audiometric techniques (history-taking, pure tone audiometry, speech audiometry, immittance audiometry, traditional site-of-lesion tests, paediatric assessment, non-organic hearing loss). Emphasis is placed on critical assessment of current literature.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Audiology', 'AUDIOL 704', 'Central Auditory Function', 'Auditory neurophysiology and electrophysiology of central auditory pathways, psychoacoustics, and speech and language. Topics include: the use of electrophysiology, imaging technologies and psychoacoustics to probe the function of the auditory system. Central processes involved in speech and language.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Audiology', 'AUDIOL 713', 'Clinical Otolaryngology and Related Sciences', 'An introduction to otolaryngology and speech pathology. Topics include: principles of pathology and mechanisms of disease, imaging techniques, diseases of the ear, head and neck, the genetics of deafness, neurological disorders that affect hearing and balance, occupational deafness and hearing conservation, speech pathology.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Audiology', 'AUDIOL 714', 'Hearing Aids and Other Devices for the Hearing Impaired', 'An introduction to the design and technology of analogue and digital hearing aids, cochlear implants and assistive devices for children and adults with hearing-impairment. Analysis of the signal processing techniques and strategies used in digital hearing aids and cochlear implants.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Audiology', 'AUDIOL 715', 'Physics and Acoustics for Audiology', 'The basic physics of sound; and instrumentation and the principles of digital signal processing involved in audiological research. Topics include: the physics of sound waves, room acoustics, the measurement of reverberation time; the nature of acoustic impedance; the nature of filters and amplifiers, acoustics of speech, calibration.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Audiology', 'AUDIOL 716A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Audiology', 'AUDIOL 716B', 'Clinical Practicum I', 'Introduces the clinical practice of Audiology. Topics include communication skills; ethics; cultural issues; and the clinical practice of audiology, including counselling, understanding the effects of aging, tinnitus and hyperacusis management. Students will obtain the skills and knowledge to take a clinical history and to perform a basic audiometric assessment of adults and children. Particular emphasis is placed on critical evaluation and independent learning. Involves clinical work including a nine week practicum during the summer semester between Part I and Part II.', 'To complete this course students must enrol in AUDIOL 716 A and B', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Audiology', 'AUDIOL 718A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Audiology', 'AUDIOL 718B', 'Clinical Practicum II', 'The advanced clinical practice of audiology with particular emphasis on paediatric audiology and case management of children and adults. Topics include assessment of hearing in the paediatric population, including clinical electrophysiology, development of speech, auditory processing disorders and management of hearing loss in children. Particular emphasis is placed on critical evaluation and independent learning. The course involves substantial clinical work.', 'Prerequisite: AUDIOL 716', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Audiology', 'AUDIOL 796A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Audiology', 'AUDIOL 796B', 'Thesis - Level 9', '', 'To complete this course students must enrol in AUDIOL 796 A and B', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Clinical Education', 'CLINED 703', 'Learning in Small Groups', 'Explores how clinicians operate as members and leaders of groups, and the conditions underlying effective group function both in education and the workplace.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Clinical Education', 'CLINED 705', 'Simulation and Clinical Skills Teaching', 'Theory and practice around the use of simulators in clinical education. Addresses underlying theory, research, course design, acquisition of clinical skills, scenario-based learning, scenario design, simulator programming, and feedback after simulated performance.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Clinical Education', 'CLINED 706', 'Interprofessional Learning, Teamwork and Patient Safety', 'Explores and evaluates the evidence-base on interprofessional learning in the health professions. Evaluates the role of interprofessional learning in building effective healthcare teams.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Clinical Education', 'CLINED 707', 'Advanced Studies in Clinical Education', 'Supervised research on a topic approved by the Head of School of Medicine.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Clinical Education', 'CLINED 710', 'Special Studies', 'Independent study on a topic approved by the Head of School of Medicine.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Clinical Education', 'CLINED 711', 'E-learning and Clinical Education', 'Develops the knowledge and skills to critically evaluate e-learning in the clinical setting. Addresses underlying theoretical constructs, practical skills, sourcing and selection of learning objects, course design and assessment.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Clinical Education', 'CLINED 712', 'Curriculum and Course Design', 'Theory, concepts, and processes that underlie curriculum development and the design of short courses for a clinical setting. Addresses outcome-based course design and the development of objectives, content, methods, materials, assessment and evaluation for a course or curriculum.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Clinical Education', 'CLINED 713', 'Clinical Supervision', 'Students will explore theories of workplace learning and models of supervision of students and trainees in the clinical workplace, understand the different roles of clinical supervisors, and develop knowledge and skills to improve the effectiveness of clinical supervision in their own context.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Clinical Education', 'CLINED 715', 'Theory and Practice of Clinical Education', 'Examines the conceptual frameworks for learning in a clinical setting. The course will explore learning theory as it relates to the clinical experience, programme design, learner preparation, practical skills in enhancing learning in the clinical setting, and translation of theoretical knowledge into clinical practice.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Clinical Education', 'CLINED 716', 'Assessing Clinical Performance', 'Examines the purpose, criteria, methods, scoring methods and examiner training for a range of assessments of health professionals, with a focus on ensuring competence to practice. This will include concepts of reliability and validity, standard setting as well as advanced techniques to compare and effectively implement different types of clinical assessments.', 'Restriction: CLINED 704', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Clinical Education', 'CLINED 717', 'Special Topic', '</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Clinical Education', 'CLINED 718', 'Professionalism in Clinical Education', 'Students will examine and critically reflect on the notion of professionalism in clinical education to ascertain how professionalism is fostered in health care settings. The course will address methods of teaching and learning professionalism.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Clinical Education', 'CLINED 719', 'Clinical Education in Action', 'Takes a broad look across essential topics in clinical education of relevance to all clinical teachers involved in teaching with patients, assessing students and planning lessons. Application to practice and peer observation are key components of this course.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Clinical Education', 'CLINED 790', '', '</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Clinical Education', 'CLINED 790A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Clinical Education', 'CLINED 790B', 'Dissertation - Level 9', '', 'Corequisite: POPLHLTH 701 or equivalent experience', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Clinical Education', 'CLINED 796A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Clinical Education', 'CLINED 796B', 'Thesis - Level 9', '', 'Prerequisite: POPLHLTH 701 or equivalent experience', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Clinical Education', 'CLINED 797A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Clinical Education', 'CLINED 797B', 'Research Portfolio - Level 9', 'Supervised research that represents the personal scholarly work of a student based on a coherent area of inquiry. Culminates in a conclusive piece of work related to a specific area of specialisation or scope of practice.', 'Prerequisite: POPLHLTH 701 or equivalent experience', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Clinical Imaging', 'CLINIMAG 201', 'Radiographic Clinical Practice I', 'Introduces the fundamental knowledge and clinical skills necessary to perform a range of routine radiographic examinations with a patient-centred focus.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Clinical Imaging', 'CLINIMAG 301', 'Radiographic Clinical Practice II', 'Extends the fundamental knowledge and clinical skills necessary to perform a range of routine and non-routine radiographic examinations, including specialist views and adaptive techniques, with a patient-centred focus.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Clinical Imaging', 'CLINIMAG 302', 'Radiographic Clinical Practice III', 'Develops the fundamental knowledge and clinical skills necessary to perform a range of advanced radiographic imaging examinations including mammography, angiography, interventional procedures, and computed tomography (CT), with a patient-centred focus.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Clinical Imaging', 'CLINIMAG 401', '', '</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Clinical Imaging', 'CLINIMAG 401A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Clinical Imaging', 'CLINIMAG 401B', 'Radiographic Clinical Practice IV', 'Consolidates the knowledge and clinical skills necessary to perform all radiographic imaging examinations, with a patient-centred focus.', 'To complete this course students must enrol in CLINIMAG 401 A and B, or CLINIMAG 401', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Clinical Imaging', 'CLINIMAG 705', 'Nuclear Medicine Clinical Applications', 'Addresses normal and altered radiopharmaceutical biodistribution imaging appearances and protocol selection relating to clinical practice of various body systems including respiratory, endocrine, hepatobiliary, genitourinary and gastro-intestinal systems.', 'Prerequisite: MEDIMAGE 720', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Clinical Imaging', 'CLINIMAG 706', 'Nuclear Medicine Specialised Clinical Applications', 'Addresses normal and altered radiopharmaceutical biodistribution appearances, and protocol selection and development, associated with cardiovascular, lymphatic and oncological applications in Nuclear Medicine. Students will also examine non-imaging radionuclide investigations and therapeutic applications associated with current and evolving Nuclear Medicine techniques.', 'Prerequisite: MEDIMAGE 720', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Clinical Imaging', 'CLINIMAG 707', 'CT Clinical Practice', 'Addresses normal and abnormal CT imaging appearances, protocol selection and development, and includes reflection on clinical practice. The course will ensure students develop the knowledge, competencies, skills and attitudes needed to demonstrate mastery in both academic and professional capability in CT practice.', 'Prerequisite: MEDIMAGE 710', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Clinical Imaging', 'CLINIMAG 708', 'Mammographic Clinical Practice', 'Addresses normal and abnormal mammographic imaging appearances, technique evaluation and adaptation, and includes reflection on clinical practice relating to mammography. The course will ensure students develop the knowledge, competencies, skills and attitudes needed to demonstrate mastery in academic and professional mammographic practice.', 'Prerequisite: MEDIMAGE 707', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Clinical Imaging', 'CLINIMAG 709', 'Principles of Clinical Ultrasound', 'Provides a fundamental understanding of ultrasound technology and applications. Students will integrate physical principles of ultrasound including transducer technology, quality assurance, bio-effects and safety, and apply these to clinical practice. In addition, they will analyse standard imaging techniques, normal and abnormal imaging appearances of the abdomen, pelvis and lower leg veins and perform examinations of these areas.', 'Corequisite: MEDIMAGE 716', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Clinical Imaging', 'CLINIMAG 710', 'MRI Clinical Applications', 'Addresses normal and abnormal imaging appearances, protocol selection and development, and applications associated with standard neurological, musculoskeletal and body MRI examinations.', 'Prerequisite: MEDIMAGE 714', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Clinical Imaging', 'CLINIMAG 711', 'MRI Specialised Clinical Applications', 'Addresses complex scientific principles of MRI relevant to a range of specialised applications. Students will examine advanced pulse sequences, specialised procedures including breast MR, enterography, MR angiography functional MRI and cardiac MRI. Techniques such as perfusion, spectroscopy, diffusion tensor imaging (DTI), and tractography, will be investigated in addition to new and evolving techniques.', 'Prerequisite: MEDIMAGE 714', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Clinical Imaging', 'CLINIMAG 712', 'MRI Clinical Practice', 'Develops the knowledge, competencies, skills and attitudes needed to demonstrate mastery in both academic and professional capability in MRI practice.', 'Prerequisite: Departmental approval', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Clinical Imaging', 'CLINIMAG 713', 'Ultrasound Clinical Applications in Obstetrics and Gynaecology', 'Addresses normal and abnormal ultrasound imaging appearances, adaptation of scanning techniques relating to gynaecology and obstetrics ultrasound imaging.', 'Prerequisite: CLINIMAG 709 or MEDIMAGE 716', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Clinical Imaging', 'CLINIMAG 714', 'Ultrasound Clinical Applications', 'Addresses normal and abnormal ultrasound imaging appearances, adaptation of scanning techniques relating to the abdomen, musculoskeletal system, vascular system, small parts and paediatric imaging. Students will develop theoretical knowledge and reflect on competencies, skills and attitudes required for mastery in academic and professional ultrasound practice.', 'Prerequisite: CLINIMAG 709 or MEDIMAGE 716', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Clinical Imaging', 'CLINIMAG 715', 'Ultrasound Clinical Practice', 'Develops the knowledge, competencies, skills and attitudes needed to demonstrate mastery in both academic and professional capability in ultrasound practice.', 'Prerequisite: Departmental approval', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Clinical Imaging', 'CLINIMAG 716', 'Nuclear Medicine Clinical Practice', 'Develops the knowledge, competencies, skills and attitudes needed to demonstrate mastery in both academic and professional capability in Nuclear Medicine practice.', 'Prerequisite: Departmental approval', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Clinical Imaging', 'CLINIMAG 717', 'CT Clinical Applications', 'Addresses normal and abnormal Computed Tomography (CT) imaging appearances, protocol selection and modification, and application to clinical practice.', 'Restriction: CLINIMAG 707', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Clinical Imaging', 'CLINIMAG 718', 'Special Topic', '</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Clinical Imaging', 'CLINIMAG 719', 'Ultrasound Abdominal Clinical Applications', 'Addresses normal and abnormal ultrasound imaging appearances, scanning techniques and applications associated with abdominal ultrasound examinations. An emphasis will be placed on integrating theory and clinical practice elements to facilitate sound clinical decision making and clinical competence.', 'Prerequisite: MEDIMAGE 716', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Clinical Imaging', 'CLINIMAG 720', 'Ultrasound Specialised Clinical Applications', 'Addresses normal and abnormal ultrasound imaging appearances, scanning techniques and applications associated with musculoskeletal, vascular, small parts and paediatric ultrasound examinations. An emphasis will be placed on integrating theory and clinical practice elements to facilitate sound clinical decision making and clinical competence.', 'Prerequisite: MEDIMAGE 716', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Dietetics', 'DIETETIC 703', 'Clinical Nutrition: Disease Pathophysiology and Management - Level 9', 'Provides basic and practical material for professional application of clinical nutrition knowledge to dietetic practice and case management. Includes the aetiology and pathophysiology of disease states that are relevant to, and underpin, nutritional management and/or treatment. Includes assessment of nutritional status and nutritional requirements, fluid and electrolyte management, nutrition therapy by tube feeding, and dietetic management of various disease states.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Dietetics', 'DIETETIC 705', 'Professional Skills 2 - Level 9', 'Further develops professional communication and organisation/management skills that will enable students to work effectively as dietitians. Progresses the knowledge and skills required to develop competency in the nutrition and dietetic care process as it applies to clinical practice, including assessment, planning, implementation and evaluation of nutrition and dietetic interventions, and introduces the principles of food service management ranging from food safety and hygiene to the development of menus for therapeutic diets. Includes practical placement in the domains of clinical and applied nutrition, and food service management in part fulfilment of the professional course accreditation requirements of the New Zealand Dietitians Board.', 'Corequisite: DIETETIC 704', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Dietetics', 'DIETETIC 706A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Dietetics', 'DIETETIC 706B', 'Professional Skills 3', 'Further develops professional communication and organisation/management skills that will enable students to work effectively as dietitians and fulfil the registration competency requirements. Reviews and explores the dietetic process as it applies to clinical practice, including assessment, planning, implementation and evaluation of nutrition and dietetic interventions. Includes practical placement in the domains of applied and clinical nutrition, and public health for part fulfilment of the professional course accreditation requirements of the New Zealand Dietitians Board.', 'Prerequisite: DIETETIC 703, 704, 705', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Dietetics', 'DIETETIC 707', 'Professional Skills 1', 'Evaluates professional communication, organisation and management skills that will enable students to work effectively as dietitians. Students will describe and appraise nutrition information, dietetic expertise, judgement and reasoning to the nutrition assessment, intervention and evaluation of nutrition and dietetic process plans. Introduces the principles of food service systems and public health to optimise nutrition, health and well-being.', 'Restriction: DIETETIC 704', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Dietetics', 'DIETETIC 708', 'Professional Skills 2', 'Integrates professional communication, organisation and management skills that will enable students to work effectively as dietitians. Students will apply nutrition knowledge, dietetic expertise, judgement and reasoning to the nutrition assessment, intervention and evaluation of nutrition and dietetic process plans. Applies the principles of food service systems and public health to optimise nutrition, health and well-being.', 'Prerequisite: DIETETIC 707', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Dietetics', 'DIETETIC 709A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Dietetics', 'DIETETIC 709B', 'Professional Skills 3', 'Advances effective communication skills to optimise nutrition, health, well-being for individuals and communities. Integrates and appraises the dietetic process as it applies to clinical and dietetic practice. Critically evaluates the scientific principles of clinical nutrition to enable the translation of the evidence to best practice. Apply communication and organisation principles, which will ensure effective, management and leadership within varied environments.', 'Prerequisite: DIETETIC 707, 708', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Dietetics', 'DIETETIC 793A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Dietetics', 'DIETETIC 793B', 'Thesis - Level 9', '', 'Prerequisite: DIETETIC 703, 704, 705', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Health Informatics', 'HLTHINFO 722', 'Special Study in Health Informatics 1', '</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Health Informatics', 'HLTHINFO 723', 'Health Knowledge Management', 'The objective of this course is to develop an ability to analyse the role and dynamics of knowledge in the working environment in the health sector, and to develop aspects of knowledge infrastructure.', 'Restriction: POPLHLTH 723', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Health Informatics', 'HLTHINFO 724', 'Special Study in Health Informatics 2', '</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Health Informatics', 'HLTHINFO 725', 'The New Zealand Health Data Landscape', 'An overview of key issues to support the appropriate and effective use of large volumes of routinely collected data to drive improvements in the delivery of health care. Ethical and equitable use of health data, critical evaluation of health data, identification of analytic methods and appropriate interpretation to support health care decision-making are discussed. Specific datasets are not analysed.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Health Informatics', 'HLTHINFO 728', 'Principles of Health Informatics', 'The study of information technology and information management concepts relevant to the delivery of high quality and cost-effective healthcare. Theoretical frameworks such as data management, decision support, strategic planning and implementation, change management, knowledge management and privacy and other ethical aspects of health informatics are included.', 'Restriction: POPLHLTH 728', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Health Informatics', 'HLTHINFO 730', 'Healthcare Decision Support Systems', 'Familiarises students with the main developments of decision support systems in healthcare. The theoretical concepts and the technology including data mining, clinical decision support systems, diagnostic systems and decision support in managed care are outlined. Ethical issues are also addressed.', 'Restriction: POPLHLTH 730', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Health Management', 'HLTHMGT 721', 'Health Management', 'The application of general management principles to health organisations and resources, with particular reference to the nature of health organisations and health professional teams. Includes theory and concepts supporting the effective management of health human resources and financial resources.', 'Restriction: POPLHLTH 721', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Health Management', 'HLTHMGT 724', 'Special Topic', '</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Health Management', 'HLTHMGT 725', 'Special Study in Health Leadership', '</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Health Management', 'HLTHMGT 729', 'Strategic Health Management', 'The importance and contribution of strategic management to the health sector is established through the application of strategic management thinking and theory to complex systems. Skills in strategy formulation are developed through application of the logic and processes of strategy.', 'Restriction: POPLHLTH 729', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Health Management', 'HLTHMGT 754', 'Health Leadership', 'Establishes the conceptual foundation of health leadership related to the self, others and organisations. Contemporary leadership frameworks are compared and linked to leadership theory and concepts in the context of improving health and outcomes.', 'Restriction: NURSING 732, POPLHLTH 754', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Health Management', 'HLTHMGT 755', '', '</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Health Management', 'HLTHMGT 755A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Health Management', 'HLTHMGT 755B', 'Project in Health Leadership - Level 9', 'An applied research-based project in an aspect of health leadership. The project provides a capstone experience to the degree. Students critically analyse real-world cases and problems and develop evidence-informed and innovative solutions through expert consultation and literature research.', 'To complete this course students must enrol in HLTHMGT 755 A and B, or HLTHMGT 755', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Health Psychology', 'HLTHPSYC 122', 'Behaviour, Health and Development', 'Introduction to the relationship between behaviour and the major biological, cognitive and social-emotional processes, applying them to health and development across the life span. Focuses on aspects of behaviour and development particularly relevant for the healthcare professional.', 'Restriction: POPLHLTH 122', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Health Psychology', 'HLTHPSYC 714', 'Health Psychology', 'A review of the psychological factors involved in health and illness. Topics include: the understanding of patient behaviour in medical settings, preventative health behaviour, cognitive models of illness, stress and illness, communication and adherence to treatment, the psychology of physical symptoms and coping with chronic disease.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Health Psychology', 'HLTHPSYC 715', 'Research Methods in Health Psychology', 'A review of the principal methods used in the design, conduct and analysis of studies in the health psychology area. This will focus on quantitative research, but qualitative methodologies will also be addressed.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Health Psychology', 'HLTHPSYC 716', 'Psychoneuroimmunology', 'Outlines the nature of the human immune system, its measurement and limitations of current practices and models. The main focus of the course is the extent to which psychological processes such as stress, emotions, and social interactions have been found to influence immune behaviour and the implications of these findings for health and wellbeing. Various theoretical frameworks through which psycho-immune relationships might be understood are presented and discussed.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Health Psychology', 'HLTHPSYC 717', 'Emotions, Emotion Regulation, and Health', 'Extends content knowledge in health psychology by focussing on the expanding literature linking emotions and emotion regulation with health outcomes. The course provides an overview of the nature and functions of emotions, discrete versus dimensional approaches, developmental and cultural considerations, and the links between emotions and cognitive processes. Specific topics include direct and indirect pathways linking emotions and health, links between emotions and health-deleterious behaviours, symptom detection, screening behaviour, treatment decision-making, and adherence.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Health Psychology', 'HLTHPSYC 718', 'Psychophysiology and Health', 'Describes general psychophysiological methodology including the measurement, analysis and interpretation of physiological data. Topics include physiological responses to stress including heart rate, blood pressure, heart rate variability, cortisol and the startle response. This course takes a multi-systems approach to exploring health and affords the opportunity to view behavioural, physiological and neuroendocrine responses to stress.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Health Psychology', 'HLTHPSYC 719', 'Health Psychology Assessment', 'Extends content knowledge in health psychology through the development of skills in the assessment and evaluation of constructs commonly used in health psychological research and practice. Includes consideration of general issues in psychometric theory and the specific assessment issues commonly confronting widely-used health psychological research designs, as well as detailed coverage of specific content areas including illness cognitions, health-related psychophysiology, emotions, and health outcomes.', 'Restriction: PSYCH 701, 747', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Health Psychology', 'HLTHPSYC 720', 'Health Psychology Interventions', 'Reviews the underpinning theory base for approaches commonly used in health psychology interventions such as CBT, and applies these approaches to examples from the field of health psychology. Individual and group/community treatment targets will be considered, including common difficulties that impact on disease occurrence or management, and the psychological consequences of disease.', 'Restriction: PSYCH 701, 748', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Health Psychology', 'HLTHPSYC 721', 'Self-Management in Chronic Illness', 'Describes the concept of self-management and how it relates to managing the symptoms, treatment and psychological changes that occur after being diagnosed with a chronic health condition. Some of the most prevalent health conditions and relevant self-care behaviours will be covered including type 2 diabetes, cardiovascular disease, arthritis and cancer. A review of the psychological impact of having a chronic illness will also be covered, including coping with chronic illness. The course will also focus on designing self-management interventions, including a review of the frameworks for intervention development, implementation and evaluation.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Health Psychology', 'HLTHPSYC 742A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Health Psychology', 'HLTHPSYC 742B', 'Professional Practice in Health Psychology', 'Focuses on the professional intervention skills necessary to practice health psychology. Topics include: interviewing and assessment skills, formulation of problems, design and evaluation of interventions and models for interdisciplinary and multidisciplinary functioning. Relevant contexts include: hospitals, hospices, consultancies, general practice etc.', 'Prerequisite: HLTHPSYC 746', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Health Psychology', 'HLTHPSYC 743', 'Psychopathology and Clinical Interviewing', 'Common psychological disorders encountered in clinical practice and health settings. Practical teaching of clinical interview and diagnostic skills is completed in class.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Health Psychology', 'HLTHPSYC 744', 'Research Topic in Health Psychology', 'Offers the opportunity for academic staff to provide a specific course of study for one or several students. It is available only by arrangement between the staff member(s) and students.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Health Psychology', 'HLTHPSYC 745A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Health Psychology', 'HLTHPSYC 745B', 'Practicum in Health Psychology - Level 9', 'A practical component of supervised applied work of not less than 1,500 hours in approved health settings, and other work as required. A detailed written report of the work undertaken will be required of the student.', 'Prerequisite: HLTHPSYC 746', 'null', 'null');
