INSERT INTO `medicalandhealthscience` VALUES ('Health Sciences', 'HLTHSCI 795B', 'Research Project in Health Practice - Level 9', 'An applied research-based project relating to an aspect of health practice in a specialised community development setting. Students will critically analyse real-world cases and problems and develop evidence-informed, innovative solutions to community health issues through literature search, consultation with community leaders and relevant health professionals and through application of relevant community development and change frameworks.', 'Prerequisite: 15 points from POPLHLTH 701, 704, 705', 'null', 'null');
