INSERT INTO `medicalandhealthscience` VALUES ('Medical Science', 'MEDSCI 793B', 'Research Portfolio - Level 9', 'Supervised research that represents the personal scholarly work of a student based on a coherent area of inquiry. Culminates in a conclusive piece of work related to a specific area of specialisation or scope of practice.', 'To complete this course students must enrol in MEDSCI 793 A and B', 'null', 'null');
