INSERT INTO `medicalandhealthscience` VALUES ('Medicine', 'MEDICINE 700', 'Designing Safer Systems', 'The application of improvement science and safety science methods to achieve better outcomes for patients by reducing harm, waste and variation in health care; includes a focus on measurement for improvement and the application of human factors theory and concepts to design a safer and more reliable health care system.', 'Prerequisite: NURSING 775 or POPLHLTH 724', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Medicine', 'MEDICINE 702', 'Understanding Complex Clinical Systems', 'Draws across domains of system science, safety science, complexity theory, and implementation science to help analyse how leaders understand and effect change in healthcare. A particular focus is on understanding how things go wrong and how organisational culture, power, and politics impact on models of effective leadership within clinical systems.', 'Prerequisite: NURSING 775 or POPLHLTH 724', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Medicine', 'MEDICINE 703', 'Special Studies in Medicine', 'Advanced study in a specific area usually related to the field of study of the thesis. Topics include, but are not restricted to, clinical neuroscience, bone science, rheumatology, geriatrics, cardiology, respiratory and renal medicine.\n</p>', 'null', 'null', 'null');
