INSERT INTO `medicalandhealthscience` VALUES ('Nursing Practice', 'NURSPRAC 701', 'Cardiac Specialty Nursing', 'Draws on evidence from biomedical and social science research to equip nurses for the care and treatment of cardiac patients. Students will be expected to integrate evidence from a range of sources and apply this to the practice of cardiac nursing.', 'Restriction: NURSING 730', 'null', 'null');
