INSERT INTO `medicalandhealthscience` VALUES ('Nursing Practice', 'NURSPRAC 702B', 'Critical Care Specialty Nursing', 'Draws on evidence from biomedical and social science research to equip nurses for the care and treatment of critical care patients. Students will be expected to integrate evidence from a range of sources and apply this to the practice of critical care nursing.', 'Restriction: NURSING 730', 'null', 'null');
