INSERT INTO `medicalandhealthscience` VALUES ('Nursing Practice', 'NURSPRAC 703B', 'Paediatric Cardiac Specialty Nursing', 'Draws on evidence from biomedical and social science research to equip nurses for the care and treatment of paediatric cardiac patients. Students will be expected to integrate evidence from a range of sources and apply this to the practice of paediatric cardiac nursing.', 'Restriction: NURSING 730', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Nursing Practice', 'NURSPRAC 704', 'Cancer Specialty Nursing', 'Draws on evidence from biomedical and social science research to equip nurses for the care and treatment of patients with cancer. Students will be expected to integrate evidence from a range of sources and apply this to the practice of cancer nursing.', 'Restriction: NURSING 730', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Nursing Practice', 'NURSPRAC 705', 'Stroke Specialty Nursing', 'Draws on evidence from biomedical and social science research to equip nurses for the care and treatment of patients following a stroke. Students will be expected to integrate evidence from a range of sources and apply this to the practice of stroke nursing.', 'Restriction: NURSING 730', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Nursing Practice', 'NURSPRAC 706', 'Orthopaedic Specialty Nursing', 'Draws on evidence from biomedical and social science research to equip nurses for the care and treatment of orthopaedic patients. Students will be expected to integrate evidence from a range of sources and apply this to the practice of orthopaedic nursing.', 'Restriction: NURSING 730', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Nursing Practice', 'NURSPRAC 707', 'Registered Nurse First Surgical Assist', 'Draws on evidence from biomedical and social science research to equip nurses for the care and treatment of surgical patients. Students will be expected to integrate evidence from a range of sources and apply this to the practice of RNFSA nursing.', 'Restriction: NURSING 730', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Nursing Practice', 'NURSPRAC 708', 'Emergency Specialty Nursing', 'Draws on evidence from biomedical and social science research to equip nurses for the care and treatment of patients in the emergency setting. Students will be expected to integrate evidence from a range of sources and apply this to the practice of emergency nursing.', 'Restriction: NURSING 730', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Nursing Practice', 'NURSPRAC 710', 'Palliative Care Specialty Nursing', 'Draws on evidence from biomedical and social science research to equip nurses for the care and treatment of palliative care patients. Students will be expected to integrate evidence from a range of sources and apply this to the practice of palliative care nursing.', 'Restriction: NURSING 730', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Nursing Practice', 'NURSPRAC 711', 'Pain Nursing Specialty Nursing', 'Draws on evidence from biomedical and social science research to equip nurses for the care and treatment of patients experiencing pain. Students will be expected to integrate evidence from a range of sources and apply this to the practice of nursing patients with pain.', 'Restriction: NURSING 730', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Nursing Practice', 'NURSPRAC 712', 'Diabetes Specialty Nursing', 'Draws on evidence from biomedical and social science research to equip nurses for the care and treatment of diabetic patients. Students will be expected to integrate evidence from a range of sources and apply this to the practice of nursing patients with diabetes.', 'Restriction: NURSING 730', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Nursing Practice', 'NURSPRAC 713', 'Paediatric Intensive Care Nursing', 'Draws on evidence from biomedical and social science research to equip nurses for the care and treatment of paediatric intensive care patients. Students will be expected to integrate evidence from a range of sources and apply this to the practice.', 'Restriction: NURSING 730', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Nursing Practice', 'NURSPRAC 715', 'Endoscopy Specialty Nursing', 'Draws on evidence from biomedical and social science research to equip nurses for the care and treatment of patients undergoing an endoscopy procedure. Students will be expected to integrate evidence from a range of sources and apply this to the practice of endoscopy nursing.', 'Restriction: NURSING 730', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Nursing Practice', 'NURSPRAC 716', 'Ophthalmology Specialty Nursing', 'Draws on evidence from biomedical and social science research to equip nurses for the care and treatment of ophthalmology patients. Students will be expected to integrate evidence from a range of sources and apply this to the practice of ophthalmology nursing.', 'Restriction: NURSING 719', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Nursing Practice', 'NURSPRAC 717', 'Practicum for RN Designated Prescribers', 'Prepares registered nurses to apply for prescribing rights as Designated Prescribers. Provides nurses with the opportunity to develop knowledge and skills in the application of pharmacotherapeutic concepts to prescribing as a designated prescriber. This includes direct supervision of prescribing activities in the clinical area and the ability to work closely and effectively in a multidisciplinary team environment.', 'Prerequisite: NURSING 742, 773, 785', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Nursing Practice', 'NURSPRAC 718', 'Contemporary Mental Health and Addictions Nursing Practice', 'Explores contemporary mental health and addictions nursing practice from both socio-political and practice-skills perspectives. Focuses on developing awareness of the unique mental health and addictions context of Aotearoa/New Zealand and the cultural and values based practices and policies which have emerged. Builds on foundational therapeutic and interpersonal skills and develops knowledge and skills in contemporary, evidence-based mental health and addictions nursing interventions.', 'Restriction: NURSING 786', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Nursing Practice', 'NURSPRAC 719', 'Clinical Practice in Mental Health and Addictions', 'A clinically based course focusing on history taking, assessment, formulation and nursing care planning. There is an emphasis on mental health, physical health and addictions assessment and the development of nursing formulation skills.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Nursing Practice', 'NURSPRAC 720', 'Special Topic: Advanced Mental Health Assessment - Level 9', 'A clinically based course covering history taking, assessment and case formulation in advanced clinical practice for mental health nurses. There is an emphasis on comprehensive mental health assessment, and negotiation of a client-focused plan of care.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Nursing Practice', 'NURSPRAC 721', 'Integrative Nursing Practice', 'A problem-based course where students develop the knowledge and assessment skills associated with nursing clients across a variety of clinical settings.  The course provides learning opportunities for students to gain knowledge, skills and develop attitudes that will ensure safe nursing practice. Principles of medication management to prepare students for practice as a registered nurse are integrated into the course.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Nursing Practice', 'NURSPRAC 722', 'Transition to Professional Nursing Practice', 'Enables students to transition from student to registered nurse through an extended period of clinical practice. Integration of nursing knowledge and legal and ethical parameters of competency will occur alongside the development of autonomy and accountability of practice.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Obstetrics and Gynaecology', 'OBSTGYN 705', 'Special Topic in Obstetrics and Gynaecology', '</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Obstetrics and Gynaecology', 'OBSTGYN 712', 'Contraception and Pre and Early Pregnancy', 'An evidence-based approach to contraception and pre and early pregnancy care. Mechanisms, side effects and contraindications of methods of contraception are covered. Pre pregnancy care will include preconceptual counselling and the psycho-social aspects of pregnancy care such as effects of drugs, alcohol, smoking and travel. Best practise and referral guidelines for early pregnancy ante natal care will be covered including diagnosis and management of early pregnancy problems such as recurrent miscarriage, ectopic pregnancy, gestational trophoblastic disease and hyperemesis.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Obstetrics and Gynaecology', 'OBSTGYN 713', 'Pregnancy and Postnatal Care in the Community', 'Common problems of pregnancy for primary care. Includes pregnancy care in the community, obstetric emergencies, common disorders in pregnancy, birth matters, the immediate postpartum period, the newborn.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Obstetrics and Gynaecology', 'OBSTGYN 715', 'Medical Gynaecology 1', 'Women\'s health and sexually transmitted diseases, menstrual disorders, pelvic pain and dyspareunia, vulva problems and vaginal discharge, menopause management.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Obstetrics and Gynaecology', 'OBSTGYN 716', 'Medical Gynaecology 2', 'Pathophysiology and clinical management of infertility, gynaecological malignancies, family violence, adolescent gynaecology, termination of pregnancy, urogynaecology.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Obstetrics and Gynaecology', 'OBSTGYN 717', '', '</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Obstetrics and Gynaecology', 'OBSTGYN 717A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Obstetrics and Gynaecology', 'OBSTGYN 717B', 'Practical Obstetrics and Gynaecology', 'Practice of obstetrics and medical gynaecology, practical procedures in obstetrics and gynaecology including competency in examinations, cervical smear taking, and insertion of intrauterine contraceptive devices. Competency in normal labour and delivery and minor surgical procedures encountered in obstetric practice. Requires the completion of a logbook approved by the Clinical Supervisor and Head of Department.', 'Corequisite: OBSTGYN 721, 722', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Obstetrics and Gynaecology', 'OBSTGYN 721', 'Obstetrics Residential', 'Attitudes to women\'s health, cultural issues, ethics, history taking and minor procedures. This course must be completed prior to students sitting the clinical and written examinations.', 'Restriction: OBSTGYN 718', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Obstetrics and Gynaecology', 'OBSTGYN 722', 'Gynaecology Residential', 'Approaches to women\'s health issues, history and examination principles and procedures, issues of screening, hormone replacement therapy and case-based studies. This course must be completed prior to students sitting the clinical and written examinations.', 'Restriction: OBSTGYN 719', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Obstetrics and Gynaecology', 'OBSTGYN 723', 'Special Studies', '</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Obstetrics and Gynaecology', 'OBSTGYN 724', 'Obstetrics Residential', 'Attitudes to women\'s health, including cultural and ethical issues. History-taking techniques and techniques for minor procedures are developed.', 'Restriction: OBSTGYN 721', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Obstetrics and Gynaecology', 'OBSTGYN 725', 'Gynaecology Residential', 'Approaches to women\'s health issues, principles and procedures associated with history-taking and examination. Issues of screening, hormone replacement therapy and other case-based studies are addressed.', 'Restriction: OBSTGYN 722', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Ophthalmology', 'OPHTHAL 703', 'Special Topic: Research Methods and Skills for Eye Research', 'A comprehensive overview, focusing primarily on the ophthalmic arena. Includes: research, methodologies, literature reviews, implementation and appraisal of qualitative and quantitative research, developing research questions and writing up of research for presentation and publication. Provides skills specific to eye research that may not be relevant to other health care professionals.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Ophthalmology', 'OPHTHAL 704', 'Special Topic: Ophthalmic Technology', 'The theory, basic principles, techniques and interpretation of results for ophthalmic technology used in the diagnosis and treatment of eye disease. Technology covered includes: slit lamp biomicroscopy, tonometry, A-scan ultrasound, keratometry; IOL master, HRT, OCT, computerised topography, anterior segment photography, FFA, autorefraction and therapeutic lasers. The latest advances in ophthalmic technology will also be included.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Ophthalmology', 'OPHTHAL 705', 'Special Topic: Management of Acute Eye Disease', 'Overview of the diagnosis and management of \'acute eye conditions\' in the community and hospital settings including: signs and symptoms, differential diagnosis, treatment modalities and medium term management.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Ophthalmology', 'OPHTHAL 706', 'Special Study in Ophthalmology', 'To provide an opportunity to study a selected field of ophthalmology at an advanced level by undertaking a detailed review of a selected topic or undertaking a research project in a field related to ophthalmology.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Optometry and Vision Science', 'OPTOM 101G', 'How We See', 'Overview of the interdisciplinary study of human vision. The course introduces the biological/physiological organisation of the visual system, discusses the subjective nature of perception, and the implications of studies of biological visual systems for machine vision. Interdisciplinary understandings of vision will be enriched by the examination of historical paintings and artists’ visual experiences.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Optometry and Vision Science', 'OPTOM 216A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Optometry and Vision Science', 'OPTOM 216B', 'Introduction to Optometry', 'A clinically-focused course introducing students to optometric practice and addressing, at an introductory level, the ethical, cultural, theoretical and clinical aspects of the optometric examination. Topics covered include: preliminary tests from the eye examination, communication skills and clinical problem solving. The course will emphasise assessment utilising advanced equipment and the production of clinically relevant outcomes and diagnosis-supportive hypotheses.', 'To complete this course students must enrol in OPTOM 216 A and B', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Optometry and Vision Science', 'OPTOM 263A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Optometry and Vision Science', 'OPTOM 263B', 'Essential Optics', 'An introduction to optics relevant to optometry and necessary to understand the optical performance of the eye, the design of ophthalmic lens applications, and the principles of operation of clinical instrumentation. Topics include; the basic principles of physical optics, the principles of image formation by lenses and lens systems mirrors and prisms, optics of the eye, ocular ametropia and aberrations.', 'Restriction: OPTOM 215, 262, 265', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Optometry and Vision Science', 'OPTOM 272A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Optometry and Vision Science', 'OPTOM 272B', 'Visual Science 1: Structure and Function of the Visual System', 'Anatomy and physiology of the eye and visual pathway. Topics include composition and structure of the tear film, neural processing in the visual cortex, aspects of visual function including spatial and temporal vision, motion perception and colour vision. Investigation of visual perception using psychophysical and electrophysiological techniques.', 'Restriction: OPTOM 151, 170, 171', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Optometry and Vision Science', 'OPTOM 292A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Optometry and Vision Science', 'OPTOM 292B', 'Issues in Optometry', 'Topics of special interest to students entering Optometry from overseas and from the graduate entry quota.', 'Prerequisite: Permission of Head of School', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Optometry and Vision Science', 'OPTOM 316A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Optometry and Vision Science', 'OPTOM 316B', 'Optometry', 'An integrative approach to the scope of optometric practice, addressing both the theoretical basis and clinical practice of the optometric examination, correction of refractive error and dispensing of optical appliances. Topics covered include: visual acuity, visual fields, colour vision, biomicroscopy, ophthalmoscopy, refractive examination, binocular examination, optical correction, lens materials and coatings, history taking, communication skills and clinical problem solving.', 'Restriction: OPTOM 211, 212, 265, 313, 314, 365, 366', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Optometry and Vision Science', 'OPTOM 345A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Optometry and Vision Science', 'OPTOM 345B', 'Principles of Ocular Pharmacology', 'General principles of pharmacology. Pharmacodynamics. Drug absorption, distribution and metabolism. Mechanism of drug action at receptors. Drugs and their application on ophthalmic practice. The autonomic nervous system: anatomy and physiology. Mechanisms of action of ocular pharmaceutical agents. Principles of pharmacological treatment of ocular disease. Drug interactions. Legislation on use of ocular pharmaceutical agents by optometrists in New Zealand and internationally. Introduction to therapeutic agents in optometric practice. Scope of treatment. Shared care.', 'Prerequisite: OPTOM 171 or 272', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Optometry and Vision Science', 'OPTOM 353A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Optometry and Vision Science', 'OPTOM 353B', 'Ocular Pathology', 'Pathophysiology of the eye. Histopathology of eye disease. Pathology of orbit, lacrimal system, conjunctiva, cornea, uvea, lens and retina. Developmental anomalies of the eye.', 'Restriction: OPTOM 251', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Optometry and Vision Science', 'OPTOM 375A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Optometry and Vision Science', 'OPTOM 375B', 'Visual Science 2', 'To provide an understanding of visual information processing in human brain. In particular the cortical processing of shape, motion and colour, and development of the visual cortex will be addressed. A problem-oriented approach will develop critical thinking and problem solving skills. Students will acquire the ability to seek, evaluate and retrieve scientific information on which to base their clinical practice.', 'Restriction: OPTOM 270', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Optometry and Vision Science', 'OPTOM 392A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Optometry and Vision Science', 'OPTOM 392B', 'Issues in Optometry 2', '', 'Prerequisite: Permission of Head of School', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Optometry and Vision Science', 'OPTOM 416A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Optometry and Vision Science', 'OPTOM 416B', 'Clinical Optometry', 'Facilitates the transition from student to professional optometrist. Topics addressed include: structuring the routine optometric examination in a clinical setting, diagnosis and management of disorders of the visual system, case analysis, myopia control, visual ergonomics, vision screening, and visual standards. This course culminates in students examining and managing clients in the public University Clinics under supervision.', 'Restriction: OPTOM 312, 415', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Optometry and Vision Science', 'OPTOM 430A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Optometry and Vision Science', 'OPTOM 430B', 'Contact Lens Practice', 'Principles of contact lens fitting and clinical procedures used in contact lens practice. Topics include: current designs of contact lenses, soft and rigid materials used in contact lens manufacture, contact lens optics and verification techniques, contact lens fitting, patient contact lens care, and complications associated with contact lens wear.', 'Restriction: OPTOM 330', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Optometry and Vision Science', 'OPTOM 442A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Optometry and Vision Science', 'OPTOM 442B', 'Optometry for Special Populations', 'An advanced clinical course including consideration of visual disorders specific to children, adults with binocular vision abnormalities, or those with visual impairment including the older population. Topics include: developmental aspects and assessment of infants/children, investigation and management of binocular eye-movement disorders; and diagnosis and management of vision problems in visually impaired patients including electronic, optical and non-optical low vision appliances.', 'Restriction: OPTOM 341, 440, 441', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Optometry and Vision Science', 'OPTOM 450A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Optometry and Vision Science', 'OPTOM 450B', 'Diseases of the Eye and Visual System: Diagnosis and Management', 'Signs, symptoms and diagnosis of diseases of the eye, ocular adnexa and visual system, including neurological dysfunction and signs of systemic disease. Management of diseases of eye, ocular adnexa and visual system, including the use of therapeutic agents. Indications, contraindications and side effects of therapeutic agents for the treatment of ocular disease.', 'Restriction: OPTOM 351, 352, 355', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Optometry and Vision Science', 'OPTOM 492A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Optometry and Vision Science', 'OPTOM 492B', 'Issues in Optometry 3', '', 'Prerequisite: Permission of Head of School', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Optometry and Vision Science', 'OPTOM 510A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Optometry and Vision Science', 'OPTOM 510B', 'Advanced Clinical Optometry 1', 'Clinical work with responsibility, under supervision, for patients.', 'Restriction: OPTOM 410', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Optometry and Vision Science', 'OPTOM 520A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Optometry and Vision Science', 'OPTOM 520B', 'Advanced Clinical Optometry 2', 'Clinical work with greater emphasis on particular areas in optometry including: contact lenses, low vision, binocular vision, paediatric optometry and practice management.', 'Restriction: OPTOM 420', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Optometry and Vision Science', 'OPTOM 560A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Optometry and Vision Science', 'OPTOM 560B', 'Optometry in Practice', 'Supervised clinical work in locations external to the Grafton Campus Optometry Clinic. These locations may include University satellite clinics, private optometry practice, hospital eye departments, overseas institutions, or experience in other approved locations. Lectures address; legislation relevant to healthcare including registration and competency, occupational safety and health, ethics, practice management, small business management.', 'Restriction: OPTOM 462', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Optometry and Vision Science', 'OPTOM 561A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Optometry and Vision Science', 'OPTOM 561B', 'Optometry in Practice', 'Advanced clinical work experience in locations external to the Grafton Campus Optometry Clinic. These locations may include University satellite clinics, private optometry practices, hospital eye departments, private ophthalmology practices, overseas institutions, or other approved locations. Topics include; therapeutic management of eye disease, legislation relevant to healthcare including registration and competency, occupational safety and health, ethics, practice management, small business management.', 'Restriction: OPTOM 462, 560', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Optometry and Vision Science', 'OPTOM 570A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Optometry and Vision Science', 'OPTOM 570B', 'Research in Advanced Optometric Science', 'Study modules on a range of topics in optometry and vision science, with the focus being on developing an evidence-based approach on selected topics. Study will include supervised investigations into an approved topic relating to optometry and vision science, including clinical and applied research.', 'Prerequisite: OPTOM 416, 430, 442, 450', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Optometry and Vision Science', 'OPTOM 592A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Optometry and Vision Science', 'OPTOM 592B', 'Issues in Optometry 4', 'A number of special topics in Clinical Skills. Further information may be obtained from the School of Optometry and Vision Science.', 'Prerequisite: Permission of Head of School', 'null', 'null');
